INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156626, 1, 0, 'Murlocs are dangerous in large numbers. Take back our supplies, but avoid fighting too many of them at once.', 12, 0, 100, 0, 0, 0, 0, 178507, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156626, 2, 0, 'You three, scour the island for any sign of the expedition. $N, you and I will scout ahead.', 12, 0, 100, 0, 0, 0, 0, 178509, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156651, 0, 0, 'Of course I did. He\'s my son.', 12, 0, 100, 0, 0, 0, 0, 176881, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156651, 1, 0, 'Eat first, Alaria. You need your strength.', 12, 0, 100, 0, 0, 0, 0, 192440, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156651, 2, 0, 'Recruit! Quilboar are far deadlier than murlocs. You need to be ready for the dangers ahead.', 12, 0, 100, 0, 0, 0, 0, 191072, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156651, 3, 0, 'Alright, let\'s see what you know!', 12, 0, 100, 0, 0, 0, 0, 189218, 0, 'Captain Garrick');
