INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156662, 6, 0, 'Let\'s go. Those monsters have my son.', 12, 0, 100, 0, 0, 0, 0, 178649, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156662, 7, 0, 'I know. I raised him to overcome any challenge.', 12, 0, 100, 0, 0, 0, 0, 191076, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156676, 0, 1, 'Sacrifice... for... ritual...', 12, 0, 100, 0, 0, 150271, 0, 178951, 0, 'Ogre Overseer');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156698, 0, 0, 'You return, and with others this time! That\'s astounding. No soul has ever returned once, let alone twice with allies! A Maw Walker indeed!', 12, 0, 100, 603, 0, 152107, 0, 0, 0, 'Attendant Protector to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156779, 0, 0, 'Что за гордыня...', 12, 0, 100, 0, 0, 152080, 0, 0, 0, 'Падшая искательница знаний to Player');
