INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156929, 0, 0, '¡Me salvaste la vida! Permíteme devolverte el favor. ¡Sube!', 12, 0, 100, 0, 0, 152936, 0, 0, 0, 'Ralia Cazasueños to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156943, 0, 0, '¡Si sientes un cosquilleo extraño, significa que la ilusión funciona!', 12, 0, 100, 0, 0, 152955, 0, 0, 0, 'Meredy Huntswell to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156954, 0, 0, 'Gracias por salvarme… ¡pero aún necesitamos detener a Gor\'groth!', 12, 0, 100, 0, 0, 153064, 0, 0, 0, 'Capitana Kelra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157051, 0, 0, 'Preparémonos para la práctica. Te dejaré dar el primer golpe.', 12, 0, 100, 0, 0, 152846, 0, 0, 0, 'Soldado Cole to Player');
