INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157054, 0, 0, 'Ты не остановишь раскольников.', 12, 0, 100, 0, 0, 152043, 0, 0, 0, 'Заблудший претендент to Player');
