INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157426, 0, 0, 'Вот она! Моя флейта!', 12, 0, 100, 0, 0, 154340, 0, 0, 0, 'Индри Древопев to Индри Древопев');
