INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157561, 5, 0, 'Вот... прими мое благословение.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Останки связанной души to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157561, 6, 0, 'Забери меня... отсюда.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Останки связанной души to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157561, 7, 0, 'Ты... меня слышишь?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Останки связанной души to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157579, 0, 0, 'For the Master!', 12, 0, 100, 0, 0, 150646, 0, 0, 0, 'Forbidden Researcher to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157620, 0, 0, 'So easily confused!', 14, 0, 100, 0, 0, 143644, 0, 180274, 0, 'Prophet Skitra to Prophet Skitra');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157620, 1, 0, 'Your eyes betray you.', 14, 0, 100, 0, 0, 143652, 0, 180275, 0, 'Prophet Skitra to Prophet Skitra');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157669, 0, 0, 'My... final... battle...', 12, 0, 100, 0, 0, 150141, 0, 0, 0, 'Steelskin Vanguard to Secutor Mevix');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157669, 1, 0, 'Face me!', 12, 0, 100, 0, 0, 150133, 0, 0, 0, 'Steelskin Vanguard to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157669, 2, 0, 'I... am broken...', 12, 0, 100, 0, 0, 150143, 0, 0, 0, 'Steelskin Vanguard to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157669, 3, 0, 'For eternal glory!', 12, 0, 100, 0, 0, 150135, 0, 0, 0, 'Steelskin Vanguard to Secutor Mevix');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157669, 4, 0, 'I fought... to... the end...', 12, 0, 100, 0, 0, 150147, 0, 0, 0, 'Steelskin Vanguard to Serafk');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157669, 5, 0, 'The war... goes on...', 12, 0, 100, 0, 0, 150151, 0, 0, 0, 'Steelskin Vanguard to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157669, 6, 0, 'Glorious battle!', 12, 0, 100, 0, 0, 150139, 0, 0, 0, 'Steelskin Vanguard to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157669, 7, 0, 'I will end you!', 12, 0, 100, 0, 0, 150131, 0, 0, 0, 'Steelskin Vanguard to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157671, 0, 0, 'You have met your match!', 12, 0, 100, 0, 0, 152327, 0, 0, 0, 'Prime Bladeguard to Steelskin Vanguard');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157676, 0, 0, 'Hmph. Can\'t you see I\'m busy?', 12, 0, 100, 1, 0, 166605, 0, 0, 0, 'Bonesmith Heirmir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157676, 1, 0, 'Is that... it can\'t be...', 12, 0, 100, 432, 0, 166606, 0, 0, 0, 'Bonesmith Heirmir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157676, 2, 0, 'So this was the cause of all that commotion. A blade and a message from the Primus himself...', 12, 0, 100, 1, 0, 169257, 0, 0, 0, 'Bonesmith Heirmir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157676, 3, 0, 'Hmm. Whatever his reason, a blade\'s no good without a hilt. Time to get to work.', 12, 0, 100, 1, 0, 166608, 0, 0, 0, 'Bonesmith Heirmir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157676, 4, 0, 'Well? Are you ready to start or not?', 12, 0, 100, 1, 0, 166610, 0, 0, 0, 'Bonesmith Heirmir');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157676, 5, 0, 'The Primus may have taught me a lot about the forge, but I\'ve learned a thing or two myself since then!', 12, 0, 100, 1, 0, 166611, 0, 0, 0, 'Bonesmith Heirmir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157676, 6, 0, 'Ah, he enchanted the blade. Of course he did. You might want to get ready for a fight.', 12, 0, 100, 0, 0, 166612, 0, 0, 0, 'Bonesmith Heirmir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157676, 7, 0, 'Keep those things off me while I work! This is a test of your skills as well as mine!', 12, 0, 100, 1, 0, 166613, 0, 0, 0, 'Bonesmith Heirmir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157676, 8, 0, 'Just how many souls did he forge into this thing?!', 12, 0, 100, 1, 0, 166614, 0, 0, 0, 'Bonesmith Heirmir to Ephemeral Warrior');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157676, 9, 0, 'The enchantment\'s final guardian! Vanquish it and claim your runeblade!', 12, 0, 100, 0, 0, 166615, 0, 0, 0, 'Bonesmith Heirmir to Fragmented Echo');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157676, 10, 0, 'Gah! Well, I forged it... but clearly you are the only one meant to wield it. Go on, then.', 12, 0, 100, 1, 0, 166616, 0, 0, 0, 'Bonesmith Heirmir to Guardian of the Blade');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157676, 11, 0, 'Looks like taking up the blade empowered one of its runes. Knowing the Primus, that\'s just the start of something more.', 12, 0, 100, 1, 0, 166617, 0, 0, 0, 'Bonesmith Heirmir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157696, 0, 0, 'Como yo. Ven, Lentios.', 12, 0, 100, 669, 0, 0, 0, 0, 0, 'Nemea to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157696, 1, 0, 'Due to the anima drought, the cloudstriders no longer shed their antlers... you will have to collect them off of their bodies. I implore you to not be wasteful, and to use as many parts of the cloudstriders as you can.', 12, 0, 100, 669, 0, 0, 0, 0, 0, 'Nemea to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157696, 2, 0, '$n, you return! Andira seems happy. I think she\'s taken a liking to you!', 12, 0, 100, 669, 0, 0, 0, 0, 0, 'Nemea to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157696, 3, 0, 'Larions are social creatures. Andira will not eat without her pride. It is vital that we bring food back to the retreat.', 12, 0, 100, 669, 0, 0, 0, 0, 0, 'Nemea to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157696, 4, 0, 'I hope bonding with Andira softened you to my way of life. Larions are more than just companions. Never have I felt a bond with anybody else, kyrian or otherwise, that could be more rewarding than my relationships with my pride.', 12, 0, 100, 669, 0, 0, 0, 0, 0, 'Nemea to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157696, 5, 0, 'I knew that you felt the pull of kinship, $n. Andira and the other larions shall be pleased for you to return.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Nemea to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157696, 6, 0, 'The pride will be overjoyed to have you, $n. Return soon, there is plenty more bonding to be done.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Nemea to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157732, 0, 0, 'Так... холодно...', 12, 0, 100, 0, 0, 151397, 0, 0, 0, 'Обожженный пепельный вурдалак to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157733, 0, 0, 'Покой... хорошо...', 12, 0, 100, 20, 0, 151401, 0, 0, 0, 'Обезумевший пепельный вурдалак to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157733, 1, 0, 'От меня не скрыться!', 12, 0, 100, 20, 0, 151407, 0, 0, 0, 'Обезумевший пепельный вурдалак to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157754, 0, 0, 'This was... a mistake...', 12, 0, 100, 0, 0, 149480, 0, 190551, 0, 'Darksworn Mechanic to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157754, 1, 0, 'An intruder!', 12, 0, 100, 0, 0, 149464, 0, 190538, 0, 'Darksworn Mechanic to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157846, 0, 0, 'Здоров, $n. Хорошо, что ты здесь. Эта лужа совсем испортилась.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Рендл to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157846, 1, 0, 'Ладно, пора состряпать здоровяка.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Рендл to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157846, 2, 0, 'Эй! Вы трое, тоже залезайте! Хрясь-хрясь!', 12, 0, 100, 25, 0, 152526, 0, 0, 0, 'Рендл to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157883, 0, 0, 'Склонись или умри!', 12, 0, 100, 0, 0, 150588, 0, 0, 0, 'Одержимая сильвара to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157883, 1, 0, 'Protect... the heart...', 12, 0, 100, 0, 0, 160748, 0, 0, 0, 'Possessed Sylvar to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157883, 2, 0, 'I... failed...', 12, 0, 100, 0, 0, 150602, 0, 0, 0, 'Possessed Sylvar to Serafk');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157883, 3, 0, 'Join us or perish!', 12, 0, 100, 0, 0, 150588, 0, 0, 0, 'Possessed Sylvar to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157885, 0, 0, 'Это теперь наше!', 12, 0, 100, 0, 0, 151586, 0, 0, 0, 'Одержимый воркай to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157891, 0, 0, 'Работа, работа, работа... А больше и ничего. Ладно... Что надо сделать?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Готовый к работе землерой to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157891, 1, 0, 'Эта куча хлама не слишком большая. Много времени не займет. Пожиратели на подходе!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Готовый к работе землерой to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157891, 2, 0, 'Вот так. Кажется, дело сделано. Возвращаюсь к лужам.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Готовый к работе землерой to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157895, 0, 0, 'Those spineless traitors are fools to challenge the might of the Chosen!', 12, 0, 100, 1, 0, 166595, 0, 0, 0, 'Baron Vyraz to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157898, 0, 0, 'Прыгай на Бутуса. Начнем топтать!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 'Бутус to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157899, 0, 0, 'Уф... Ладно. Пошли, ребята.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Увлеченный землерой to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157956, 0, 0, 'Пожиратели нападают! Бутус готов ломать бошки!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Бутус to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157956, 1, 0, 'Бутус, вроде, закончил. Пойдем-ка отсюда.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Бутус to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (157956, 2, 0, 'Пришли. Теперь Бутус вернется в лужу.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Бутус to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (158001, 0, 0, 'Бутус тут. Готов помахать кулаками!', 14, 0, 100, 0, 0, 152518, 0, 0, 0, 'Бутус to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (158004, 0, 0, '¡Kala, pórtate bien! No $ulo:la; muerdas. Tal vez vino a ayudar.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Zosime to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (158004, 1, 0, 'Ten paciencia, Kala. $n pronto regresará con tus juguetes.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Zosime to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (158005, 0, 0, '¡Hola! ¿Tienes un minuto para ayudar a que mi amiga Zosime baje por la cuesta?', 12, 0, 100, 6, 0, 0, 0, 0, 0, 'Tamesis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (158005, 1, 0, 'Gracias, $Uforastero:forastera;. Que la Arconte te guíe.', 12, 0, 100, 273, 0, 0, 0, 0, 0, 'Tamesis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (158007, 0, 0, 'First you bring war, now a blade of the Primus. Your legend grows, mortal!', 12, 0, 100, 0, 0, 165006, 0, 0, 0, 'Margrave Krexus to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (158038, 0, 0, 'Excuse me! Hello! Over here, in the shadows. I could use your assistance.', 14, 0, 100, 1, 0, 161511, 0, 0, 0, 'Laurent to Player');
