INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161279, 1, 0, 'It is done. You have my thanks.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Sparring Aspirant');
