INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161333, 0, 0, 'Твоей гордыне здесь не место!', 12, 0, 100, 0, 0, 150666, 0, 0, 0, 'Предательница из башни Темной Стены to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161333, 1, 0, 'Я воспитаю в тебе смирение.', 12, 0, 100, 53, 0, 150645, 0, 0, 0, 'Предатель из башни Темной Стены to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161333, 2, 0, 'Ты поддашься, как и все до тебя!', 12, 0, 100, 1, 0, 150668, 0, 0, 0, 'Предательница из башни Темной Стены to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161343, 0, 0, 'Раньше тут было полно пиратов, благородных воров и нищих королей. Провались эта засуха в Утробу.', 12, 0, 100, 0, 0, 163054, 0, 0, 0, 'Дуборыл to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161346, 0, 0, 'Good opportunity to measure combat prowess. Onward!', 12, 0, 100, 0, 0, 156571, 0, 188814, 0, 'Mikanikos to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161346, 1, 0, 'Go, my forgehands!', 12, 0, 100, 0, 0, 156574, 0, 185121, 0, 'Mikanikos to Bron');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161346, 2, 0, 'Forgehands ready for launch!', 12, 0, 100, 0, 0, 156572, 0, 189318, 0, 'Mikanikos to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161346, 3, 0, 'Forsworn thoroughly routed! Let us return.', 12, 0, 100, 0, 0, 156570, 0, 189200, 0, 'Mikanikos to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161350, 0, 0, 'We\'ve done it! The ogres and their dragon have been defeated and the storm has cleared.', 12, 0, 100, 0, 0, 0, 0, 188017, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161388, 0, 0, 'Твой путь закончится здесь!', 12, 0, 100, 25, 0, 149168, 0, 0, 0, 'Некрочистильщица to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161398, 0, 0, 'Я убивал врагов и посильнее.', 12, 0, 100, 539, 0, 152322, 0, 0, 0, 'Присягнувший клинку Вираза to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161398, 1, 0, 'Стой и сражайся!', 12, 0, 100, 539, 0, 152315, 0, 0, 0, 'Присягнувшая клинку Вираза to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161441, 0, 0, 'Well done! We shall begin your next lesson in a few moments.', 12, 0, 100, 1, 0, 128586, 0, 0, 0, 'Orator Kloe to Aethon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161448, 0, 0, 'The might of Maldraxxus protects me!', 12, 0, 100, 25, 0, 151476, 0, 185305, 0, 'Blackbale Lookout to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161448, 1, 0, 'Fool... I have... powerful allies...', 12, 0, 100, 0, 0, 151464, 0, 185308, 0, 'Blackbale Lookout to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161456, 0, 0, 'На колени перед повелителями!', 12, 0, 100, 0, 0, 150336, 0, 0, 0, 'Рассекатель душ в маске to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161456, 1, 0, 'Моя Королева... прости...', 12, 0, 100, 669, 0, 150344, 0, 0, 0, 'Рассекатель душ в маске to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161490, 0, 0, 'This ain\'t me specialty so if you\'d kindly hold still I\'d appreciate it.', 12, 0, 100, 0, 0, 150813, 0, 190243, 0, 'Blackbale Flunky to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161499, 0, 0, 'Oh no! Baedos is eating too quickly! He might get indigestion, watch out!', 14, 0, 100, 1, 0, 0, 0, 0, 0, 'Gerdus to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161537, 0, 0, 'Your legend ends here and now.', 12, 0, 100, 0, 0, 152325, 0, 0, 0, 'Mierina to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161540, 0, 0, 'Come with me, I will place a bell to help you get down to the Redelav District.', 12, 0, 100, 904, 0, 162273, 0, 186544, 0, 'Kleia to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161540, 1, 0, 'Ring this if you need help getting to the ground. I will provide aerial support. Good luck!', 12, 0, 100, 1, 0, 162274, 0, 186559, 0, 'Kleia to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161540, 2, 0, 'Take the sinstone, let us leave here before we are caught.', 12, 0, 100, 1, 0, 157952, 0, 191388, 0, 'Kleia to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161540, 3, 0, 'I am coming along. Aerial support, at your service.', 12, 0, 100, 66, 0, 162275, 0, 191681, 0, 'Kleia to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161543, 0, 0, 'I will go ahead and make sure it is safe.', 12, 0, 100, 1, 0, 157954, 0, 186577, 0, 'Pelagos to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161544, 0, 0, 'I will search for the frame. Pelagos, please find a better disguise.', 12, 0, 100, 1, 0, 164659, 0, 186560, 0, 'The Curator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161544, 1, 0, '$n, over here! We have found the mirror frame.', 12, 0, 100, 1, 0, 164660, 0, 191321, 0, 'The Curator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161544, 2, 0, 'With these shards and some of my own anima, I will piece this mirror back together.', 12, 0, 100, 1, 0, 157953, 0, 186575, 0, 'The Curator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161544, 3, 0, 'Be careful, Pelagos. We\'ll be right behind you. Everybody remember to apply the powder to protect yourself.', 12, 0, 100, 1, 0, 157955, 0, 186579, 0, 'The Curator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161544, 4, 0, 'After you, $n.', 12, 0, 100, 1, 0, 157964, 0, 186607, 0, 'The Curator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161544, 5, 0, 'I will fetch Kleia and Pelagos. May your trip to the Ember Ward be without trouble.', 12, 0, 100, 1, 0, 157982, 0, 191534, 0, 'The Curator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161571, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 1191, 0, 'Anubisath Sentinel');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161591, 0, 0, 'А, прямо душу греет... будь у меня душа.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Землерой при жаровне душ to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161610, 0, 0, 'It is too late to flee now!', 12, 0, 100, 0, 0, 150492, 0, 180846, 0, 'Lord Neculai to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161613, 0, 0, 'The plague has gone... and you will follow!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Korrim the Cunning to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161613, 1, 0, 'The damage... is already done...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Korrim the Cunning to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161614, 0, 0, 'You will meet your true death!', 12, 0, 100, 0, 0, 150500, 0, 180848, 0, 'Sir Vilhelm to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161614, 1, 0, 'This cannot be the end!', 12, 0, 100, 0, 0, 150504, 0, 180859, 0, 'Sir Vilhelm to Voidwalker');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161617, 0, 0, 'You are not one of us!', 12, 0, 100, 0, 0, 150500, 0, 180852, 0, 'Duke Omor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161617, 1, 0, 'This cannot be the end!', 12, 0, 100, 0, 0, 150504, 0, 180859, 0, 'Duke Omor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161621, 0, 0, 'Yeah, well, maybe, I wanted this! Ha!', 12, 0, 100, 0, 0, 151134, 0, 181014, 0, 'Chalice Courier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161621, 1, 0, 'Don\'t tell my boss about this!', 12, 0, 100, 0, 0, 151136, 0, 181010, 0, 'Chalice Courier to Voidwalker');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161621, 2, 0, 'I regret... nothing... wait, no... everything!', 12, 0, 100, 0, 0, 151133, 0, 181015, 0, 'Chalice Courier to Voidwalker');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161624, 0, 0, 'I could smell your stench as soon as you arrived. Come now, show me what the kyrian have taught you.', 12, 0, 100, 1, 0, 153107, 0, 191287, 0, 'Duchess Minodora to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161624, 1, 0, 'It is too late to flee now!', 12, 0, 100, 0, 0, 150493, 0, 180846, 0, 'Duchess Minodora to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161624, 2, 0, 'My shard... my ticket to power...', 12, 0, 100, 0, 0, 153108, 0, 191288, 0, 'Duchess Minodora to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161625, 0, 0, 'This cannot be the end!', 12, 0, 100, 0, 0, 150504, 0, 180859, 0, 'Duke Grigore to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161634, 0, 0, 'You are not one of us!', 12, 0, 100, 0, 0, 150501, 0, 180852, 0, 'Duchess Ioana to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161638, 0, 0, 'You are not one of us!', 12, 0, 100, 0, 0, 150501, 0, 180852, 0, 'Lady Ihrin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161645, 0, 0, 'Как ты смеешь приходить сюда?!', 12, 0, 100, 0, 0, 150498, 0, 0, 0, 'Сэр Виорел to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161668, 0, 0, 'Visectus is here! And soon, the Blackhound Outpost will be gone!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 'Summoner Gerard to Visectus');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161668, 1, 0, 'Visectus shall rise once more, begin the summoning my brethren.', 14, 0, 100, 0, 0, 161206, 0, 0, 0, 'Summoner Gerard to 162441');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161720, 0, 0, 'My dear friends. A wondrous honor has been bestowed upon you. The chance to embrace your destiny, as I have.', 12, 0, 100, 0, 0, 143669, 0, 0, 1, 'Intro 1');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161720, 1, 0, 'N\'Zoth has shown me the inevitable truth. That to save Azeroth, we must accept his gift.', 12, 0, 100, 0, 0, 143670, 0, 0, 1, 'Intro 2');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161733, 0, 0, 'Кровь и слава! Когда выходишь на арену?', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Анцио Непогрешимый to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161884, 0, 0, 'Come, my chosen. Witness the fate of this world.', 12, 0, 100, 0, 0, 143666, 0, 182392, 1, 'N\'Zoth the Corruptor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161884, 1, 0, 'Behold the one who shall herald its coming.', 12, 0, 100, 0, 0, 143667, 0, 182393, 1, 'N\'Zoth the Corruptor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161884, 2, 0, 'You tread within a vision of what was... and what will be.', 12, 0, 100, 0, 0, 144341, 0, 183478, 1, 'N\'Zoth the Corruptor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161884, 3, 0, 'My empire is this world\'s inevitable destiny.', 12, 0, 100, 0, 0, 144342, 0, 183480, 1, 'N\'Zoth the Corruptor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (161884, 4, 0, 'The Black Empire stretches before you. Join me and herald the coming age.', 12, 0, 100, 0, 0, 145776, 0, 186479, 1, 'N\'Zoth the Corruptor to Player');
