INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 8, 0, 'The conflict seems to move in this direction. We should follow the trail before it grows... cold.', 12, 0, 100, 1, 0, 161934, 0, 201585, 0, 'Highlord Darion quest to Guard frozen ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 9, 0, 'More of Lady Proudmoore\'s handiwork.', 12, 0, 100, 1, 0, 161935, 0, 201586, 0, 'Highlord Darion quest to a sheep');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 10, 0, 'It would be unwise to break the enchantment. Let\'s move along.', 12, 0, 100, 6, 0, 161936, 0, 201587, 0, 'Highlord Darion quest to a sheep');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 11, 0, 'The Lord Admiral is truly formidable, to have bested one of these monstrosities.', 12, 0, 100, 1, 0, 161937, 0, 201588, 0, 'Highlord Darion quest to stygian');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 12, 0, 'Hold! What\'s that up ahead?', 12, 0, 100, 5, 0, 161938, 0, 201589, 0, 'Highlord Darion quest to stygian');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 13, 0, 'Up ahead! It\'s Lady Proudmoore!', 12, 0, 100, 5, 0, 161946, 0, 194831, 0, 'Highlord Darion to jaina');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 14, 0, 'Looks like we\'ve secured the Lord Admiral. I\'ll signal the Ebon Blade to advance.', 12, 0, 100, 1, 0, 161947, 0, 194832, 0, 'Highlord Darion to jaina');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 15, 0, 'Whatever is going on here, we don\'t want any part of it.', 12, 0, 100, 274, 0, 161942, 0, 194826, 0, 'Highlord Darion to Thelonia the Cruel');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 16, 0, 'Let\'s move on.', 12, 0, 100, 1, 0, 161943, 0, 194827, 0, 'Highlord Darion to Thelonia the Cruel');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 17, 0, 'I hope our allies didn\'t end up in the belly of that... thing.', 12, 0, 100, 1, 0, 161940, 0, 194822, 0, 'Highlord Darion to Serath the Gluttonous');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 18, 0, 'I suggest we try not to get eaten!', 12, 0, 100, 6, 0, 161941, 0, 194823, 0, 'Highlord Darion to Serath the Gluttonous');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 19, 0, 'If one of our allies is trapped within that, I doubt we\'re going to get them out.', 12, 0, 100, 25, 0, 161944, 0, 194828, 0, 'Highlord Darion to Mawsworn Soulrenders');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 20, 0, 'Keep searching.', 12, 0, 100, 1, 0, 161945, 0, 194830, 0, 'Highlord Darion to Mawsworn Soulrenders');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 21, 0, 'Something\'s flying at us from above!', 12, 0, 100, 22, 0, 161952, 0, 195275, 0, 'Highlord Darion Mograine');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 22, 0, 'Knights, secure the cave! We\'ll hold off these fiends.', 14, 0, 100, 1, 0, 161960, 0, 195282, 0, 'Highlord Darion Mograine');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 23, 0, 'What did Thrall mean by \"slip past them again\"? You\'ve escaped before?', 12, 0, 100, 6, 0, 161962, 0, 195285, 0, 'Highlord Darion Mograine');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 24, 0, 'Lord Admiral, before we found you, did you see any sign of the Night Warrior?', 12, 0, 100, 6, 0, 161966, 0, 195289, 0, 'Highlord Darion Mograine');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 25, 0, 'She leapt into the rift just ahead of us. She vowed to find Sylvanas Windrunner... at any cost.', 12, 0, 100, 1, 0, 161968, 0, 201851, 0, 'Highlord Darion Mograine');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 26, 0, 'Not if we end you first!', 12, 0, 100, 1, 0, 161971, 0, 195296, 0, 'Highlord Darion Mograine');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 27, 0, 'Countless souls are now pouring into this realm. But some have been here for untold ages. Perhaps one of them knows a way out.', 12, 0, 100, 25, 0, 162121, 0, 195648, 0, 'Highlord Darion Mograine_q59756');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 28, 0, 'Knights of the Ebon Blade have ways to compel the dead to speak. If we seize one of them, we can extract its secrets.', 12, 0, 100, 1, 0, 162124, 0, 195650, 0, 'Highlord Darion Mograine_q59756');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 29, 0, 'Use the helm while it\'s weakened!', 12, 0, 100, 5, 0, 162125, 0, 195820, 0, 'Highlord Darion Mograine_q59757');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 30, 0, 'Useless. These souls are too fragmented. There must be another way...', 12, 0, 100, 5, 0, 162126, 0, 195821, 0, 'Highlord Darion Mograine_q59757');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 31, 0, 'This target poses a considerable threat. I will signal the Ebon Blade for assistance once we engage it.', 12, 0, 100, 25, 0, 162127, 0, 195822, 0, 'Highlord Darion Mograine_q59758');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 32, 0, 'Ebon Blade, to me! Tear this monstrosity down!', 12, 0, 100, 0, 0, 162128, 0, 195823, 0, 'Highlord Darion Mograine_q59758');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 33, 0, 'Now! Bend it to your will!', 12, 0, 100, 0, 0, 162129, 0, 195827, 0, 'Highlord Darion Mograine_q59758');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 34, 0, 'You\'re coming with us!', 12, 0, 100, 0, 0, 162131, 0, 195835, 0, 'Highlord Darion Mograine_q59915');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 35, 0, 'Speak, fiend!', 12, 0, 100, 0, 0, 162132, 0, 196084, 0, 'Highlord Darion Mograine_q59915');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 36, 0, 'Your arrival is timely.', 12, 0, 100, 1, 0, 163087, 0, 196073, 0, 'Highlord Darion Mograine_q59776');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 37, 0, 'Our friend has something to share.', 12, 0, 100, 25, 0, 163088, 0, 196074, 0, 'Highlord Darion Mograine_q59776');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 38, 0, 'Speak! How do we leave this place?', 12, 0, 100, 1, 0, 163089, 0, 196075, 0, 'Highlord Darion Mograine_q59776');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 39, 0, 'So there is a way out! Tell us where to find it!', 12, 0, 100, 5, 0, 163090, 0, 196077, 0, 'Highlord Darion Mograine_q59776');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 40, 0, 'Could you make sense of what it said, Lord Admiral?', 12, 0, 100, 6, 0, 163093, 0, 196081, 0, 'Highlord Darion Mograine_q59776');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 41, 0, 'Then it seems our course is clear.', 12, 0, 100, 25, 0, 163095, 0, 196083, 0, 'Highlord Darion Mograine_q59776');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 42, 0, 'We cannot leave these innocent souls alone to their fate.', 12, 0, 100, 1, 0, 163096, 0, 197808, 0, 'Highlord Darion Mograine_q59776');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165918, 43, 0, 'Our remaining forces will remain on guard here. Signal us when you find this \"waystone.\"', 12, 0, 100, 1, 0, 163097, 0, 197809, 0, 'Highlord Darion Mograine_q59776');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165946, 0, 0, 'Узрите мою силу и трепещите!', 14, 0, 100, 0, 0, 152547, 0, 0, 0, 'Мордрета, Вечная императрица');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165946, 1, 0, 'Вас ждет пучина забвения.', 14, 0, 100, 0, 0, 152555, 0, 0, 0, 'Мордрета, Вечная императрица to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165946, 2, 0, 'Сама реальность обратила свой гнев на вас.', 14, 0, 100, 0, 0, 152550, 0, 0, 0, 'Мордрета, Вечная императрица');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165946, 3, 0, 'Всему... приходит... конец...', 14, 0, 100, 0, 0, 152546, 0, 0, 0, 'Мордрета, Вечная императрица to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165970, 0, 0, 'Face the power of necromancy!', 12, 0, 100, 0, 0, 33646, 0, 0, 0, 'Requisitioner Kem to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (165978, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'Scavenging Mawrat');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166076, 0, 0, 'Нужен хлам? Он есть у меня.', 14, 0, 100, 1, 0, 0, 0, 0, 0, 'Мэтс to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166082, 0, 0, 'Кто готов сразиться со мной?', 12, 0, 100, 669, 0, 0, 0, 0, 0, 'Почтенная дуэлянтка to Почтенная дуэлянтка');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166082, 1, 0, 'Я позволю своему сопернику нанести первый удар!', 12, 0, 100, 669, 0, 0, 0, 0, 0, 'Почтенная дуэлянтка to Почтенная дуэлянтка');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166082, 2, 0, 'Кто следующий?', 12, 0, 100, 669, 0, 0, 0, 0, 0, 'Почтенная дуэлянтка to Почтенная дуэлянтка');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166111, 0, 0, 'Perish, weakling!', 12, 0, 100, 669, 0, 155580, 0, 0, 0, 'Blighted Fadeblade to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166147, 0, 0, 'Аметия!', 12, 0, 100, 0, 0, 158888, 0, 0, 0, 'Каликс to Каликс');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166147, 1, 0, 'Спасибо тебе, пилигрим Утробы. Да направит тебя Архон.', 12, 0, 100, 0, 0, 158889, 0, 0, 0, 'Каликс to Каликс');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166148, 0, 0, 'Вот и он. Руки-ноги вроде на месте.', 12, 0, 100, 0, 0, 157429, 0, 0, 0, 'Шон to Шон');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166148, 1, 0, 'Спасибо за помощь, пилигрим. Впредь мы постараемся быть осторожнее.', 12, 0, 100, 0, 0, 157430, 0, 0, 0, 'Шон to Шон');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166151, 0, 0, 'КНОПКА!', 12, 0, 100, 0, 0, 165982, 0, 0, 0, 'Мориаза Красная to Мориаза Красная');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166151, 1, 0, 'Спасибо, пилигрим. Непросто было собрать Кнопку из тех частей, что тут можно найти. Нам лучше уйти отсюда.', 12, 0, 100, 0, 0, 165983, 0, 0, 0, 'Мориаза Красная to Мориаза Красная');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166157, 0, 0, 'Каликс! Хвала Архонту!', 12, 0, 100, 0, 0, 158888, 0, 0, 0, 'Аметия to Аметия');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166244, 0, 0, 'Шон! Наконец-то!', 12, 0, 100, 0, 0, 157428, 0, 0, 0, 'Варрик to Варрик');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166246, 0, 0, 'Марриаза!', 12, 0, 100, 0, 0, 155867, 0, 0, 0, 'Кнопка to Кнопка');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166282, 0, 0, 'NOT AGAIN! MUCK IS RABBL\'N!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 'Muckmaster Rotjaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166327, 0, 0, 'Враги будут страдать!', 12, 0, 100, 0, 0, 151057, 0, 0, 0, 'Собиратель семян to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166333, 0, 0, 'We must join the fight above. May the Flames of Courage guide your blade.', 12, 0, 100, 0, 0, 164755, 0, 195891, 0, 'Xandria to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166338, 0, 0, 'It is not your time. Rise and fight on!', 12, 0, 100, 0, 0, 158413, 0, 194511, 0, 'Vesiphone to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166340, 0, 0, 'A worthy challenger.', 12, 0, 100, 0, 0, 152115, 0, 190314, 0, 'Fallen Legionnaire to Pelagos');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166340, 1, 0, 'A fitting end.', 12, 0, 100, 0, 0, 152107, 0, 190318, 0, 'Fallen Legionnaire to Kleia');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166340, 2, 0, 'I regret nothing...', 12, 0, 100, 0, 0, 152105, 0, 93983, 0, 'Fallen Legionnaire to Praetor Wingblade');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166426, 0, 0, 'I will... serve again...', 12, 0, 100, 0, 0, 153666, 0, 179855, 0, 'Stitchyard Apprentice to Player');
