INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166969, 4, 0, 'Boogie down!', 14, 0, 100, 0, 0, 155206, 0, 0, 0, 'Baroness Frieda to Baroness Frieda');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166969, 5, 0, 'Enough! Your gyrations sicken me.', 14, 0, 100, 0, 0, 155207, 0, 0, 0, 'Baroness Frieda to Baroness Frieda');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166969, 6, 0, 'You will always... be beneath me...', 14, 0, 100, 0, 0, 155216, 0, 0, 0, 'Baroness Frieda to Baroness Frieda');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166970, 0, 0, 'How dare you invade my party in such dreadful attire!', 14, 0, 100, 0, 0, 155234, 0, 0, 0, 'Lord Stavros to Lord Stavros');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166970, 1, 0, 'Everyone, find your partners! Or die!', 14, 0, 100, 0, 0, 155231, 0, 0, 0, 'Lord Stavros');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166970, 2, 0, 'Come, friends! Dance until you bleed!', 14, 0, 100, 0, 0, 155232, 0, 0, 0, 'Lord Stavros');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166970, 3, 0, 'Everyone, take your positions! It is time for the dance macabre!', 14, 0, 100, 0, 0, 155222, 0, 0, 0, 'Lord Stavros to Lord Stavros');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166970, 4, 0, 'Boogie down!', 14, 0, 100, 0, 0, 155223, 0, 0, 0, 'Lord Stavros to Lord Stavros');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166970, 5, 0, 'Shimmy right!', 14, 0, 100, 0, 0, 155227, 0, 0, 0, 'Lord Stavros to Lord Stavros');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166970, 6, 0, 'Sashay left!', 14, 0, 100, 0, 0, 155226, 0, 0, 0, 'Lord Stavros to Lord Stavros');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166970, 7, 0, 'Prance Forward!', 14, 0, 100, 0, 0, 155225, 0, 0, 0, 'Lord Stavros to Lord Stavros');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166970, 8, 0, 'Your steps were simply delicious! Pity you have to die now.', 14, 0, 100, 0, 0, 155207, 0, 0, 0, 'Lord Stavros to Lord Stavros');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166970, 9, 0, 'But I am... the life... of the party...', 14, 0, 100, 0, 0, 155235, 0, 0, 0, 'Lord Stavros to Lord Stavros');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166971, 0, 0, 'What kind... of strategy... was that...', 14, 0, 100, 0, 0, 155253, 0, 0, 0, 'Castellan Niklaus to Castellan Niklaus');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 0, 0, 'Thank the Light you found us!', 12, 0, 100, 1, 0, 161948, 0, 194833, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 1, 0, 'We can\'t stay here. Can you walk, Thrall?', 12, 0, 100, 1, 0, 161949, 0, 195272, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 2, 0, 'They\'ve found us! Be ready!', 12, 0, 100, 1, 0, 161953, 0, 195276, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 6, 0, 'There\'s a cave just around the bend. With any luck, we can reach it before they notice.', 12, 0, 100, 1, 0, 161957, 0, 195279, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 7, 0, 'Well, there goes our luck!', 12, 0, 100, 1, 0, 161959, 0, 199510, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 8, 0, 'So much for remaining unnoticed. Let\'s hurry.', 12, 0, 100, 5, 0, 161961, 0, 195284, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 9, 0, 'Indeed. I\'ve broken free countless times, only to end up back in a cage.', 12, 0, 100, 6, 0, 161963, 0, 199554, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 10, 0, 'I\'ve hidden inside this cave before. We can\'t stay long, but it should buy us a little time.', 12, 0, 100, 1, 0, 161964, 0, 199555, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 11, 0, 'Tyrande? Here? No, I have not.', 12, 0, 100, 6, 0, 161967, 0, 199557, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 12, 0, 'That is troubling. An ancient ritual infused her with incredible power. I fear it may be growing beyond her ability to contain it.', 12, 0, 100, 1, 0, 161969, 0, 199559, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 13, 0, 'I\'ve had enough of your cages, fiend!', 12, 0, 100, 5, 0, 161970, 0, 195293, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 14, 0, 'In here!', 12, 0, 100, 5, 0, 161972, 0, 195297, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 15, 0, 'We should be safe enough here for the moment.', 12, 0, 100, 1, 0, 162106, 0, 195299, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 16, 0, 'When you first arrived, I wasn\'t sure if you were even real... or if you were just another illusion meant to torment us.', 12, 0, 100, 1, 0, 162107, 0, 195301, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 17, 0, 'I was beginning to lose hope that I\'d see anyone from our world again.', 12, 0, 100, 1, 0, 162108, 0, 195303, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 41, 0, 'Hold on, Baine! Do not let the Jailer take you!', 12, 0, 100, 0, 0, 163142, 0, 199143, 0, 'Lady Jaina Proudmoore_q60644');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 42, 0, 'I wish we had more time for you to recover, but we need to move.', 12, 0, 100, 6, 0, 163148, 0, 199149, 0, 'Lady Jaina Proudmoore_q60644');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 43, 0, 'Our way out of here--we hope.', 12, 0, 100, 5, 0, 163154, 0, 199154, 0, 'Lady Jaina Proudmoore_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 44, 0, 'My spell will get us past most of his guards, but there will likely be some who can see through it.', 12, 0, 100, 0, 0, 163181, 0, 197740, 0, 'Lady Jaina Proudmoore_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 45, 0, 'Stay close. We can\'t afford to draw their attention.', 12, 0, 100, 0, 0, 163182, 0, 199237, 0, 'Lady Jaina Proudmoore_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 46, 0, 'Careful. These eyes can see through my spell.', 12, 0, 100, 0, 0, 163189, 0, 199323, 0, 'Lady Jaina Proudmoore_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 47, 0, 'Hold here.', 12, 0, 100, 0, 0, 163188, 0, 199325, 0, 'Lady Jaina Proudmoore_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 48, 0, 'Look! On the platform ahead! That must be the waystone!', 12, 0, 100, 25, 0, 163155, 0, 197424, 0, 'Lady Jaina Proudmoore_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 49, 0, 'We need to reach it before the Jailer takes notice.', 12, 0, 100, 6, 0, 163156, 0, 197425, 0, 'Lady Jaina Proudmoore_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 50, 0, 'I don\'t think the stone is responding to us. I think it\'s responding to $n.', 12, 0, 100, 6, 0, 163159, 0, 197530, 0, 'Lady Jaina Proudmoore_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 51, 0, 'Stay close, $n. My spell has limited range.', 12, 0, 100, 1, 0, 163150, 0, 198031, 0, 'Lady Jaina Proudmoore_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 52, 0, 'I can make us invisible again. Give me a moment.', 12, 0, 100, 0, 0, 163185, 0, 197758, 0, 'Lady Jaina Proudmoore_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 53, 0, 'Hold the line! The waystone must finish charging!', 12, 0, 100, 0, 0, 163164, 0, 197744, 0, 'Lady Jaina Proudmoore_q59770');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 54, 0, 'It\'s nearly charged! Do not falter!', 12, 0, 100, 0, 0, 163166, 0, 197747, 0, 'Lady Jaina Proudmoore_q59770');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 55, 0, 'The stone responds to you, $n! Try to activate it while we hold off the Jailer\'s army!', 12, 0, 100, 5, 0, 163168, 0, 197752, 0, 'Lady Jaina Proudmoore_q59770');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166980, 56, 0, 'We must stand together if we hope to survive. Hold your ground!', 12, 0, 100, 5, 0, 163160, 0, 201387, 0, 'Lady Jaina Proudmoore_q59770');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166981, 0, 0, 'I... will manage.', 12, 0, 100, 1, 0, 161950, 0, 195273, 0, 'Thrall ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166981, 1, 0, 'Thank you for coming to my aid, Jaina.', 12, 0, 100, 1, 0, 161951, 0, 195274, 0, 'Thrall ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166981, 2, 0, 'Let\'s hope so. Our captors won\'t be pleased you\'ve managed to slip past them again.', 12, 0, 100, 1, 0, 161958, 0, 195340, 0, 'Thrall');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166981, 3, 0, 'Another ambush! Bring them down!', 14, 0, 100, 1, 0, 161965, 0, 195287, 0, 'Thrall');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166981, 23, 0, 'When I break the blade, be prepared for whatever comes.', 12, 0, 100, 0, 0, 163139, 0, 199140, 0, 'Thrall_q60644');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166981, 24, 0, 'It almost did, were it not for this champion.', 12, 0, 100, 6, 0, 163146, 0, 199147, 0, 'Thrall_q60644');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166981, 25, 0, 'We seek a waystone located beyond the Cauldron. Have you seen it?', 12, 0, 100, 0, 0, 163151, 0, 199151, 0, 'Thrall_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166981, 26, 0, 'Jaina, can you hide us from their sight?', 12, 0, 100, 0, 0, 163180, 0, 197754, 0, 'Thrall_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166981, 27, 0, 'Despite all Sylvanas has done, I never thought she would be part of something like this.', 12, 0, 100, 0, 0, 163184, 0, 199242, 0, 'Thrall_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166981, 28, 0, 'We have stopped endless armies before. We will never yield!', 12, 0, 100, 0, 0, 163165, 0, 197746, 0, 'Thrall_q59770');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166987, 0, 0, 'Drown in terror!', 14, 0, 100, 0, 0, 162295, 0, 0, 0, 'Deslan the Torturer');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166996, 0, 0, 'Shuja? I saw no sign of her. Perhaps she evaded capture.', 12, 0, 100, 0, 0, 0, 0, 196262, 0, 'Mithdran Dawntracker to Warlord Breka Grimaxe');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166996, 1, 0, 'Good, you\'re here. The quilboar are conducting some foul ritual...', 12, 0, 100, 0, 0, 0, 0, 196258, 0, 'Mithdran Dawntracker to Warlord Breka Grimaxe');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166997, 0, 0, 'We move, now. Those monsters have my daughter.', 12, 0, 100, 0, 0, 0, 0, 196261, 0, 'Warlord Breka Grimaxe to Mithdran Dawntracker');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (166997, 1, 0, 'One can hope. She rarely acts with such caution.', 12, 0, 100, 0, 0, 0, 0, 196263, 0, 'Warlord Breka Grimaxe to Mithdran Dawntracker');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167008, 0, 0, 'Oh you\'re in for it now, pal!', 12, 0, 100, 0, 0, 0, 0, 196264, 0, 'Corcho Silbapum to Geoseñor Grek\'og');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167008, 1, 0, 'And that\'s why I always bet on the Horde! Let\'s scram!', 12, 0, 100, 0, 0, 156983, 0, 196265, 0, 'Corcho Silbapum to Geoseñor Grek\'og');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167019, 0, 0, 'This here\'s a tiny version of the Choppy Booster Mark 5. Just let me enlarge it...', 12, 0, 100, 0, 0, 0, 0, 196285, 0, 'Cork Fizzlepop to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167019, 1, 0, 'Okay, might have overdone it a bit. Hold on...', 12, 0, 100, 0, 0, 0, 0, 196286, 0, 'Cork Fizzlepop to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167019, 2, 0, 'Perfect! It ain\'t pretty, but it gets the job done.', 12, 0, 100, 0, 0, 0, 0, 196287, 0, 'Cork Fizzlepop to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167019, 3, 0, 'Well, I promised a giant boar. Now we just gotta deliver.', 12, 0, 100, 0, 0, 0, 0, 196328, 0, 'Cork Fizzlepop to Player');
