INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167271, 1, 0, 'Going to... pieces...', 12, 0, 100, 0, 0, 150073, 0, 0, 0, 'Blastsewn Loader to Secutor Mevix');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167290, 0, 0, 'I will join Bo farther north, where the harpies roost. Meet me there.', 12, 0, 100, 0, 0, 157157, 0, 0, 0, 'Shuja Grimaxe to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167290, 1, 0, 'I\'ll cut a path straight through these harpies.', 12, 0, 100, 0, 0, 0, 0, 196489, 0, 'Shuja Grimaxe to Bo');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167291, 0, 0, 'They seem to have captured a Forsaken prisoner for their ritual.', 12, 0, 100, 0, 0, 0, 0, 196486, 0, 'Bo to Shuja Grimaxe');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167291, 1, 0, 'Count me in!', 12, 0, 100, 0, 0, 0, 0, 196490, 0, 'Bo to Shuja Grimaxe');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167298, 0, 0, 'A shame I must break such a fascinating spell. Keep the harpies away from me!', 12, 0, 100, 0, 0, 157187, 0, 0, 0, 'Herbert Estallalumbra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167298, 1, 0, 'I\'m free! Hahaha! Ahem. Let\'s make ourselves scarce, shall we?', 12, 0, 100, 0, 0, 157190, 0, 0, 0, 'Herbert Estallalumbra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167300, 0, 0, 'I\'ve got your back! Let us strike them down!', 12, 0, 100, 0, 0, 0, 0, 196517, 0, 'Herbert Gloomburst to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167300, 1, 0, 'Something big is coming this way!', 12, 0, 100, 0, 0, 0, 0, 196512, 0, 'Herbert Gloomburst to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167301, 0, 0, 'You do not fight alone! Lok-tar ogar!', 12, 0, 100, 0, 0, 0, 0, 196518, 0, 'Shuja Grimaxe to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167301, 1, 0, 'Then we slay it together. For the Horde!', 12, 0, 100, 0, 0, 0, 0, 196513, 0, 'Shuja Grimaxe to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167307, 0, 0, 'Well fought. We are one step closer to crushing those pitiful ogres.', 12, 0, 100, 0, 0, 157162, 0, 0, 0, 'Shuja Grimaxe to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167385, 0, 0, 'Враг слишком силен! Спаси нас, Тизо!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 'Острохвойный костолом to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167404, 0, 0, 'Too... soon...', 12, 0, 100, 0, 0, 152376, 0, 0, 0, 'Dauntless Champion to Dauntless Champion');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167424, 0, 0, 'An ill omen, to be certain. What can it mean?', 12, 0, 100, 0, 0, 162364, 0, 195115, 0, 'q60129');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167424, 1, 0, 'Is this mortal worthy of such an audience? They may prove to be an agent of the Jailer!', 12, 0, 100, 1, 0, 162366, 0, 195122, 0, 'q60148');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167424, 2, 0, 'Blessed be her Voice.', 12, 0, 100, 1, 0, 164016, 0, 203810, 0, 'q60148');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167424, 3, 0, 'You seek to open a doorway back to the realm of the living? Oh dear. Oribos is crowded enough with so many Brokers arriving every day.', 12, 0, 100, 1, 0, 162368, 0, 195139, 0, 'q60151');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167424, 4, 0, 'But Honored Voice, we have so little anima in our reserves! Surely we should be more--', 12, 0, 100, 1, 0, 162370, 0, 195161, 0, 'q60154');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167424, 5, 0, 'Come, mortal. I will show you the way.', 12, 0, 100, 0, 0, 162371, 0, 195163, 0, 'q60154');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167424, 6, 0, 'Here, mortal. This teleporter will convey you to the Ring of Transference.', 12, 0, 100, 0, 0, 162376, 0, 202741, 0, 'q60154');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167425, 0, 0, 'The reports from our scryers were unmistakable. The Maw shuddered for a moment, as if reacting to some change.', 12, 0, 100, 0, 0, 162356, 0, 195113, 0, 'q60129');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167425, 1, 0, 'Another unexpected development. We shall see to their reception.', 12, 0, 100, 0, 0, 162363, 0, 197243, 0, 'q60129');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167425, 2, 0, 'Impossible!', 12, 0, 100, 5, 0, 131775, 0, 173301, 0, 'q60129');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167425, 3, 0, 'Wondrous news, Honored Voice! Mere moments ago, there was a disturbance at the boundary of the Maw. Then this mortal appeared, claiming to have broken free of it.', 12, 0, 100, 1, 0, 162358, 0, 195120, 0, 'q60148');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167425, 4, 0, 'Blessed be her voice.', 12, 0, 100, 1, 0, 162481, 0, 203004, 0, 'q60148');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167425, 5, 0, 'Now, now, Kah-Sher, be hospitable. Mortal, I suggest establishing your portals where you first arrived... just outside the city.', 12, 0, 100, 1, 0, 162361, 0, 195140, 0, 'q60151');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167425, 6, 0, 'True, but it would seem this is the only clue our mortal friend has to follow. It falls to us to open the way for them.', 12, 0, 100, 1, 0, 162362, 0, 195159, 0, 'q60154');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167426, 0, 0, 'This one\'s gonna net me some fame.', 12, 0, 100, 0, 0, 152396, 0, 0, 0, 'Risen Valiant to Thanikos');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167432, 0, 0, 'My legend begins!', 12, 0, 100, 0, 0, 152395, 0, 0, 0, 'Risen Champion to Thanikos');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167443, 0, 0, 'Forward! For Maldraxxus! For Bastion! For the Shadowlands!', 12, 0, 100, 0, 0, 164775, 0, 196312, 0, 'Alexandros Mograine to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167483, 0, 0, 'Stay alert, $p! They\'ve broken through our lines!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Boneplated Sharpshooter to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167483, 1, 0, 'Защитите тела наших соратников! Не дайте разобрать их на запчасти!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Костяной снайпер to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167483, 2, 0, 'Из меня не сделают части для кадавров!', 12, 0, 100, 0, 0, 163150, 0, 0, 0, 'Костяной снайпер to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167484, 0, 0, 'My paragon... I... failed you...', 12, 0, 100, 0, 0, 164459, 0, 0, 0, 'Thanikos Spinerender to Xandria');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167486, 0, 0, 'A living mortal! Surely their presence here serves the Purpose. If the Arbiter reacted to their arrival, perhaps they are the key to waking her.', 12, 0, 100, 1, 0, 159783, 0, 195121, 0, 'q60148');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167486, 1, 0, 'It is the Arbiter\'s place to judge souls, Kah-Sher, not ours. I will bring this mortal before her. With her voice have I spoken.', 12, 0, 100, 0, 0, 159784, 0, 195123, 0, 'q60148');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167486, 2, 0, 'I pray the Arbiter will awaken in your presence, mortal.', 12, 0, 100, 0, 0, 162380, 0, 195126, 0, 'q60148');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167486, 3, 0, 'If the Maw\'s influence bleeds into your realm, the balance between Life and Death will be broken. All you hold dear will be consumed in darkness.', 12, 0, 100, 1, 0, 159777, 0, 195154, 0, 'q60152');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167486, 4, 0, 'Tell me, how did these allies from your world become trapped in the Maw?', 12, 0, 100, 1, 0, 159778, 0, 195155, 0, 'q60154');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167486, 5, 0, 'The winged kyrian of Bastion ferry souls from the mortal realm into the Shadowlands, but not with chains. And they are noble, untainted by the Maw.', 12, 0, 100, 1, 0, 159779, 0, 195158, 0, 'q60154');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167486, 6, 0, 'Overseer Kah-Sher, take this mortal to the Ring of Transference. Use our anima reserves to open a pathway to Bastion.', 12, 0, 100, 1, 0, 159780, 0, 195160, 0, 'q60154');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167486, 7, 0, 'See it done, Kah-Sher. With her voice have I spoken.', 12, 0, 100, 1, 0, 159781, 0, 195162, 0, 'q60154');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167489, 0, 0, 'Прошу, не медли. Нельзя, чтобы предатель узнал, что мы все знаем... и что именно мы знаем.', 12, 0, 100, 1, 0, 166052, 0, 0, 0, 'Лахос to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167498, 0, 0, '¡Prepararé centuriones para un ataque!', 12, 0, 100, 1, 0, 157731, 0, 0, 0, 'Mikanikos to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167524, 0, 0, 'Who seeks to walk the path of judgement?', 14, 0, 100, 0, 0, 154783, 0, 195728, 0, 'Valinor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167524, 1, 0, 'Trial of courage commencing.', 14, 0, 100, 0, 0, 154785, 0, 195729, 0, 'Valinor');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167524, 2, 0, '|TInterface\\Icons\\Spell_AnimaBastion_Beam.blp:20|t Valinor targets $n with a |cFFFF0000|Hspell:327262|h[Charged Anima Blast]|h|r!', 41, 0, 100, 0, 0, 154791, 0, 0, 0, 'Valinor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167524, 3, 0, 'Those that do not survive, cannot ascend.', 14, 0, 100, 0, 0, 154791, 0, 195732, 0, 'Valinor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167524, 4, 0, 'The path is perilous.', 14, 0, 100, 0, 0, 154787, 0, 195730, 0, 'Valinor');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167524, 5, 0, 'Trial suspended. Anima stores depleted.', 14, 0, 100, 0, 0, 154792, 0, 195733, 0, 'Valinor');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167524, 6, 0, 'Anima recharged, resuming trial.', 14, 0, 100, 0, 0, 154793, 0, 195738, 0, 'Valinor to Valinor');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167524, 7, 0, 'You must be tested.', 14, 0, 100, 0, 0, 154789, 0, 195731, 0, 'Valinor');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167524, 8, 0, 'To show true courage requires imminent defeat.', 14, 0, 100, 0, 0, 154794, 0, 195740, 0, 'Valinor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167524, 9, 0, 'Trial passed, participants victorious.', 14, 0, 100, 0, 0, 154795, 0, 195741, 0, 'Valinor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167526, 0, 0, 'Гра-а! Хватит магии, пора КРУШИТЬ!', 14, 0, 100, 0, 0, 154797, 0, 0, 0, 'Нургаш Жижерожденный');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167526, 1, 0, 'В небо смотрите! Ха-ха-ха!', 14, 0, 100, 0, 0, 154803, 0, 0, 0, 'Нургаш Жижерожденный');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167526, 2, 0, 'Теперь я ОЧЕНЬ разозлился!!!', 14, 0, 100, 0, 0, 154804, 0, 0, 0, 'Нургаш Жижерожденный');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167526, 3, 0, 'Теперь я разозлился!', 14, 0, 100, 0, 0, 154804, 0, 0, 0, 'Нургаш Жижерожденный');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167526, 4, 0, 'Гра-а!!!', 14, 0, 100, 0, 0, 154798, 0, 0, 0, 'Нургаш Жижерожденный');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167532, 0, 0, 'Я прославлюсь!', 12, 0, 100, 0, 0, 157667, 0, 0, 0, 'Хивин Ломатель to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167533, 0, 0, 'Твои кости украсят мои доспехи!', 12, 0, 100, 0, 0, 152214, 0, 0, 0, 'Адвент Невермор to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167535, 0, 0, 'Come. This is our only path to the House of the Chosen.', 12, 0, 100, 0, 0, 165694, 0, 0, 0, 'Baroness Draka to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167535, 1, 0, 'Margrave Krexus is the strongest of us. Show him no fear. Only respect.', 12, 0, 100, 0, 0, 165699, 0, 0, 0, 'Baroness Draka to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167586, 0, 0, 'Apply the poison to your weapon before you ambush Gutgruk. Take... him... out...', 12, 0, 100, 0, 0, 156783, 0, 0, 0, 'Drizza Traidora to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (167596, 0, 0, 'Recruit, we push forward! The rest of you, hold this position.', 12, 0, 100, 0, 0, 157174, 0, 0, 0, 'Señora de la guerra Breka Grimaxe to Player');
