INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168162, 1, 0, 'Then you have my eternal gratitude, $n.', 12, 0, 100, 0, 0, 163147, 0, 199148, 0, 'Baine_q60644');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168162, 2, 0, 'Understood. Lead the way.', 12, 0, 100, 0, 0, 163149, 0, 199150, 0, 'Baine_q60644');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168162, 3, 0, 'I spotted an obelisk of ancient stone upon the high ridge.', 12, 0, 100, 1, 0, 163152, 0, 199152, 0, 'Baine_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168162, 4, 0, 'It did not seem important. The Jailer\'s forces pay it little heed. What do you think it is?', 12, 0, 100, 6, 0, 163153, 0, 199153, 0, 'Baine_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168162, 5, 0, 'The Jailer\'s forces seem endless. There is no way we can fight through them all.', 12, 0, 100, 1, 0, 163179, 0, 197739, 0, 'Baine_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168162, 6, 0, 'The size of the Jailer\'s army rivals that of the Legion... and it\'s growing larger.', 12, 0, 100, 0, 0, 163178, 0, 199238, 0, 'Baine_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168162, 7, 0, 'To be forged into weapons for the Jailer.', 12, 0, 100, 0, 0, 163183, 0, 199259, 0, 'Baine_q59767');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168162, 8, 0, 'I think the stone is gaining power!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Baine_q59770');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168162, 9, 0, 'Jaina, something\'s wrong. It seems the waystone has stopped charging!', 12, 0, 100, 5, 0, 163176, 0, 203246, 0, 'Baine_q59770');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168174, 0, 0, 'Nowhere to hide!', 12, 0, 100, 0, 0, 151407, 0, 0, 0, 'Abandoned Ash Ghoul to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168237, 0, 0, 'Я тебя раньше не видела! Что это у тебя? Лахос устраивает встречу? Чудесно. Обожаю его выходки.', 12, 0, 100, 5, 0, 0, 0, 0, 0, 'Илка to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168238, 0, 0, 'А-а-а! Я занят, не видишь? Что еще нужно Лахосу? Неужели нельзя просто написать записку?', 12, 0, 100, 6, 0, 0, 0, 0, 0, 'Самю to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168244, 0, 0, 'All who follow the Archon m-m-must be destroyed.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Altered Sentinel to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168252, 0, 0, 'Hold. The Maw\'s power does linger upon them, but I sense... No, that cannot be possible!', 12, 0, 100, 0, 0, 155441, 0, 195106, 0, 'q60129');
