INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168290, 3, 0, 'Forsworn, take flight! Attack now, while they are wounded!', 14, 0, 100, 0, 0, 154790, 0, 195735, 0, 'Lysonia to Valinor');
