INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168322, 0, 0, 'Сейчас я позабавлюсь!', 12, 0, 100, 659, 0, 150065, 0, 0, 0, 'Сборщик анимы to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168322, 1, 0, 'Здесь есть анима!', 12, 0, 100, 659, 0, 152039, 0, 0, 0, 'Сборщик анимы');
