INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168390, 0, 0, 'Боль... что ты чуешь? Пилигрим Утробы явно где-то здесь.', 12, 0, 100, 0, 0, 162825, 0, 0, 0, 'Сокрушитель надежды Вилстав to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168390, 1, 0, 'Ага! Мне хорошо заплатят за твою голову.', 12, 0, 100, 0, 0, 162824, 0, 0, 0, 'Сокрушитель надежды Вилстав to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168390, 2, 0, 'Она... тебя прикончит.', 12, 0, 100, 0, 0, 162826, 0, 0, 0, 'Сокрушитель надежды Вилстав to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168392, 0, 0, 'Здесь был пилигрим Утробы? Говори, не то пущу тебе кровь!', 12, 0, 100, 0, 0, 162647, 0, 0, 0, 'Сокрушительница надежды Мурза to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168392, 1, 0, 'Кажется, глупая добыча сама пришла к хищнику!', 12, 0, 100, 0, 0, 162646, 0, 0, 0, 'Сокрушительница надежды Мурза to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168392, 2, 0, 'Я была одной из лучших... Прости меня, Владыка.', 12, 0, 100, 0, 0, 162648, 0, 0, 0, 'Сокрушительница надежды Мурза to Player');
