INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168441, 0, 0, 'Bet ya that guard can point us to the stables.', 12, 0, 100, 1, 0, 157202, 0, 0, 0, 'Corcho Silbapum to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168462, 0, 0, 'Yeesh, that\'s far. Better stick close to me, kid.', 12, 0, 100, 1, 0, 157215, 0, 0, 0, 'Corcho Silbapum to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (168462, 1, 0, 'Time is money, $n. Let\'s go!', 12, 0, 100, 1, 0, 157214, 0, 0, 0, 'Corcho Silbapum to Player');
