INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169062, 0, 0, '|TInterface\\ICONS\\INV_Archaeology_80_Witch_GuillotineAxe.BLP:20|t The Edge of Annihilation begins casting|cFFFF0000|Hspell:328789|h [Annihilate]|h|r!', 41, 0, 100, 0, 0, 0, 0, 0, 0, 'Edge of Annihilation to Artificer Xy\'mox');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169063, 0, 0, 'This slaughter ends now!', 12, 0, 100, 669, 0, 0, 0, 0, 0, 'Demoralized Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169063, 1, 0, 'They\'re dragging off our wounded!', 12, 0, 100, 669, 0, 0, 0, 0, 0, 'Demoralized Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169068, 0, 0, 'What reason could they have for this?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Demoralized Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169068, 1, 0, 'Forward! For Maldraxxus!', 12, 0, 100, 53, 0, 0, 0, 0, 0, 'Demoralized Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169070, 0, 0, 'Before her minions abducted key leaders, the Banshee Queen attacked Icecrown. She cut a path to the Frozen Throne and seized the Helm of Domination.', 12, 0, 100, 1, 0, 161846, 0, 198813, 0, 'Highlord Darion Mograine');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169070, 1, 0, 'Look to the sky and you will see the results of her actions. I... have never beheld anything like this.', 12, 0, 100, 1, 0, 161847, 0, 198814, 0, 'Highlord Darion Mograine');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169070, 2, 0, 'But shattering the helm also freed Bolvar Fordragon from the burden of being the Lich King.', 12, 0, 100, 1, 0, 161848, 0, 198815, 0, 'Highlord Darion Mograine');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169070, 3, 0, 'Bolvar believes he knows where the Banshee has taken those she abducted.', 12, 0, 100, 1, 0, 161849, 0, 202373, 0, 'Highlord Darion Mograine');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169070, 4, 0, 'He awaits you at the Frozen Throne. The leaders of the Alliance and Horde have gathered with him.', 12, 0, 100, 1, 0, 161850, 0, 198816, 0, 'Highlord Darion Mograine');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169070, 5, 0, 'The Ebon Blade does not fear death. Here, or in realms beyond.', 12, 0, 100, 1, 0, 161874, 0, 198478, 0, 'Highlord Darion Mograine');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169071, 0, 0, 'I too want Sylvanas to answer for her crimes. But Lor\'themar is right. Anduin wouldn\'t want us to risk the well-being of the Alliance for his sake.', 12, 0, 100, 1, 0, 161854, 0, 198462, 0, 'Genn Greymane');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169071, 1, 0, 'Find those who were taken and bring them home!', 12, 0, 100, 1, 0, 161880, 0, 198484, 0, 'Genn Greymane');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169071, 2, 0, 'Tyrande! Don\'t do this!', 12, 0, 100, 1, 0, 161882, 0, 198486, 0, 'Genn Greymane');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169071, 3, 0, 'We cannot lose you... to the darkness.', 12, 0, 100, 1, 0, 161884, 0, 201289, 0, 'Genn Greymane');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169072, 0, 0, 'Coward! I would pay any price to see the Banshee impaled upon my glaive!', 12, 0, 100, 1, 0, 161853, 0, 198461, 0, 'Tyrande Whisperwind');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169072, 1, 0, 'You dare speak to me of peace? After the atrocities your kind inflicted?!', 12, 0, 100, 1, 0, 161856, 0, 198464, 0, 'Tyrande Whisperwind');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169072, 2, 0, 'Rescue the others! The Banshee is mine to kill!', 12, 0, 100, 1, 0, 161881, 0, 198485, 0, 'Tyrande Whisperwind');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169072, 3, 0, 'ilisar\'thera\'nal!', 12, 0, 100, 1, 0, 161883, 0, 198487, 0, 'Tyrande Whisperwind');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169073, 0, 0, '...the peace remains tenuous. These are troubled times. Our people need stability--Alliance and Horde alike.', 12, 0, 100, 1, 0, 161851, 0, 198459, 0, 'Lor\'themar Theron');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169073, 1, 0, 'We cannot risk sending Azeroth\'s leaders on what might well be a suicide mission. Our friends who were abducted would offer the same counsel.', 12, 0, 100, 1, 0, 161852, 0, 198460, 0, 'Lor\'themar Theron');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169073, 2, 0, 'Your courage will be remembered, champion. Al diel shala.', 12, 0, 100, 1, 0, 161879, 0, 198483, 0, 'Lor\'themar Theron');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169075, 0, 0, 'The kaldorei need you if they are to have any hope of finding peace, High Priestess.', 12, 0, 100, 1, 0, 161855, 0, 198463, 0, 'Calia Menethil ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 0, 0, 'Enough! The war is over. We can ill afford to start another.', 12, 0, 100, 1, 0, 161857, 0, 198465, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 1, 0, 'A far more pressing threat is at hand, and we must prepare to face it.', 12, 0, 100, 1, 0, 161858, 0, 198466, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 2, 0, 'When the Banshee shattered the helm, it tore a gaping wound through the veil between our world and the Shadowlands.', 12, 0, 100, 1, 0, 161863, 0, 198467, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 3, 0, 'During my vigil upon the Frozen Throne, I caught fleeting glimpses into the realms of Death. Among them, I saw a place of inescapable darkness.', 12, 0, 100, 1, 0, 161864, 0, 198468, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 4, 0, 'That is where you must go, if you are to save those Sylvanas has taken. Together, we can open the way.', 12, 0, 100, 1, 0, 161865, 0, 198469, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 5, 0, 'Place the Shards of Domination around the circle of runes. Then the ritual can begin.', 12, 0, 100, 1, 0, 161866, 0, 198470, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 6, 0, 'When the helm rested upon my head, it took all my will to keep its insidious power in check. Many times, I nearly faltered.', 12, 0, 100, 1, 0, 161867, 0, 198471, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 7, 0, 'I could sense a dark presence at the edge of my consciousness. Not Arthas... not Ner\'zhul. Something... else.', 12, 0, 100, 1, 0, 161868, 0, 198472, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 8, 0, 'It is that very presence I sense lurking beyond the shattered sky above.', 12, 0, 100, 1, 0, 161869, 0, 198473, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 9, 0, 'I dare not travel with you. Even free of the helm, I fear I would become bound to the will of its true master.', 12, 0, 100, 1, 0, 161870, 0, 198474, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 10, 0, 'But this threat cannot be left unchecked. Therefore, you must venture where I cannot.', 12, 0, 100, 1, 0, 161871, 0, 198475, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 11, 0, 'The dangers you face will be considerable. Alone, you may well be outmatched.', 12, 0, 100, 1, 0, 161872, 0, 198476, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 12, 0, 'To aid in this task, the Knights of the Ebon Blade shall lend you their strength. Darion Mograine has volunteered to fight at your side.', 12, 0, 100, 1, 0, 161873, 0, 198477, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 13, 0, 'Living mortals were never intended to cross beyond the veil. Where you are going, you may be unable to return.', 12, 0, 100, 1, 0, 161875, 0, 198479, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 14, 0, 'But there is no other course. Death comes to claim Azeroth. It falls to you to defend her.', 12, 0, 100, 1, 0, 161876, 0, 198480, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 15, 0, 'The time has come. Take up the shattered helm and we will open the rift.', 12, 0, 100, 1, 0, 161877, 0, 198481, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169076, 16, 0, 'The path opens!', 12, 0, 100, 1, 0, 161878, 0, 198482, 0, 'Highlord Bolvar Fordragon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169090, 0, 0, 'Пробил твой час.', 12, 0, 100, 0, 0, 151271, 0, 0, 0, 'Охранница арсенала to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169092, 0, 0, 'What spirit is so powerful that their nightmares become real?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Grove Defender');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169092, 1, 0, 'We can only fight for so long. Something must be done for this spirit.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Grove Defender');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169092, 2, 0, 'We must do something to soothe this spirit, before her nightmares overtake us all.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Grove Defender');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (169092, 3, 0, 'This is unheard of! How are these manifesting?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Grove Defender');
