INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172055, 7, 0, 'I am blessed with a new opponent.', 12, 0, 100, 0, 0, 152048, 0, 190275, 0, 'Forsworn Aspirant to Heraclor');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172078, 0, 0, 'Все миры будут принадлежать друстам!', 12, 0, 100, 0, 0, 155586, 0, 0, 0, 'Дубосерд - пожиратель душ to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172079, 0, 0, 'Все будут служить нам!', 12, 0, 100, 0, 0, 150829, 0, 0, 0, 'Дубосердка - ночная крикунья to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172115, 0, 0, 'Je peux nous rendre invisibles Ã  nouveau. Donnez-moi une seconde !', 12, 0, 100, 0, 0, 163185, 0, 197758, 0, 'Dame Jaina Portvaillant');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172115, 1, 0, 'I can make us invisible again. Give me a moment.', 12, 0, 100, 0, 0, 163185, 0, 0, 0, 'Lady Jaina Proudmoore');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172119, 0, 0, 'VenezÂ ! Cette barriÃ¨re nous protÃ©geraÂ !', 12, 0, 100, 0, 0, 163170, 0, 197738, 0, 'Anduin Wrynn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172199, 0, 0, 'Don\'t... chop me... up...', 12, 0, 100, 0, 0, 149172, 0, 189056, 0, 'Maniacal Infuser to Xandria');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172199, 1, 0, 'Still... more... to learn...', 12, 0, 100, 0, 0, 149176, 0, 189066, 0, 'Maniacal Infuser to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172210, 0, 0, 'Looks like... I need... stitches...', 12, 0, 100, 0, 0, 155904, 0, 196673, 0, 'Madstitch Fleshcrafter to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172210, 1, 0, 'Beyond... repair...', 12, 0, 100, 25, 0, 155903, 0, 196672, 0, 'Madstitch Fleshcrafter to Infernal');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172228, 0, 0, 'Defend the Objective!', 41, 0, 100, 0, 0, 18500, 0, 0, 0, 'Banner to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172386, 0, 0, '|TInterface\\Icons\\INV_SmallAzeriteFragment.blp:20|tОтнесите аниму к проводнику, чтобы взять |cFFFF0000|Hspell:322921|h[Копье Архонта]|h|r!|TInterface\\Icons\\INV_Polearm_2H_BastionArchon_D_01.blp:20|t', 41, 0, 100, 0, 0, 0, 0, 0, 0, 'Проводник анимы');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172386, 1, 0, '|TInterface\\Icons\\INV_Polearm_2H_BastionArchon_D_01.blp:20|tВозьмите |cFFFF0000|Hspell:322921|h[копье Архонта]|h|r и бросьте в Девию, чтобы сбить ее на землю!', 41, 0, 100, 0, 0, 0, 0, 0, 0, 'Проводник анимы');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172532, 0, 0, 'An agent of the Maw is attacking Oribos! Defend the Arbiter!', 12, 0, 100, 0, 0, 155440, 0, 195105, 0, 'q60129');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172532, 1, 0, 'Overseers! This stranger appeared at the city\'s edge. I believe them to be... a living mortal.', 12, 0, 100, 0, 0, 155444, 0, 195116, 0, 'q60129');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172557, 0, 0, 'Fall before the Lichsworn!', 12, 0, 100, 0, 0, 33646, 0, 0, 0, 'Lichsworn Guardian to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172557, 1, 0, 'I will... serve again...', 12, 0, 100, 0, 0, 153666, 0, 179855, 0, 'Lichsworn Guardian to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172557, 2, 0, 'More will rise... in my place...', 12, 0, 100, 0, 0, 153669, 0, 179856, 0, 'Lichsworn Guardian to Khaliiq');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172634, 0, 0, 'Скорее, не зевай! На дерево ко мне влезай!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 'Яркий Блеск');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172634, 1, 0, 'Ха-ха-ха! Видишь меня?', 14, 0, 100, 0, 0, 0, 0, 0, 0, 'Яркий Блеск');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172652, 0, 0, 'These brokers are a greedy lot. Do not let such potent weapons fall into their hands!', 12, 0, 100, 1, 0, 161576, 0, 0, 0, 'Prince Renathal to Nathrian Archivist');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172652, 1, 0, 'Greed. Truly one of the most malignant of all sins.', 12, 0, 100, 1, 0, 161577, 0, 0, 0, 'Prince Renathal to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172652, 2, 0, 'Come, mortals. It is time to rid Revendreth of a haughty band of nobles that stand between us and Denathrius.', 12, 0, 100, 1, 0, 161595, 0, 0, 0, 'Prince Renathal');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172652, 3, 0, 'Another passage back to the antechamber lies this way.', 12, 0, 100, 1, 0, 161607, 0, 0, 0, 'Prince Renathal');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172652, 4, 0, 'You have done well so far, mortals, striking a blow to the efforts of Denathrius. But do not underestimate his power. His might is... considerable.', 12, 0, 100, 1, 0, 161608, 0, 0, 0, 'Prince Renathal');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172652, 5, 0, 'Watch them scatter! It sickens me how far some of my people have fallen.', 12, 0, 100, 0, 0, 161759, 0, 0, 0, 'Prince Renathal to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172713, 0, 0, 'We will break you, like all the rest!', 12, 0, 100, 0, 0, 150667, 0, 181025, 0, 'Muckpool Vandal to Ripened Laborer');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172713, 1, 0, 'A worthy subject! Let us begin!', 12, 0, 100, 0, 0, 150660, 0, 181021, 0, 'Muckpool Vandal to Ripened Laborer');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172714, 0, 0, 'Please be sure to get out safely as well! Farewell!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Trapped Caretaker to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172714, 1, 0, 'Archon be praised!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Trapped Caretaker to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172714, 2, 0, 'Thank you! Go in service, friend.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Trapped Caretaker to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172715, 0, 0, 'The Master will not be questioned!', 12, 0, 100, 0, 0, 150661, 0, 181022, 0, 'Muckpool Investigator to Ripened Laborer');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172860, 0, 0, 'Шевелись! Я не могу потратить на тебя целую вечность... Ладно, могу. Но не хочу!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Готовый к работе землерой to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172860, 1, 0, 'Work work work... It\'s all we do. Bah... take me to the problem.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Ripened Laborer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172860, 2, 0, 'Get going! I don\'t have all eternity... Okay I do. But I don\'t want to spend it with YOU.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Ripened Laborer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172860, 3, 0, 'How would YOU like it if you were dragged around by your neck? If I were a lil\' bit taller...', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Ripened Laborer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172862, 0, 0, 'Please, Maw Walker. Help me save my son.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Yero the Skittish to 25605');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172862, 1, 0, 'Follow me, I beg you! He is just down the slope from here. There is still time!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Yero the Skittish to 25605');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172862, 2, 0, 'Yes, this way! Hurry!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Yero the Skittish');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172872, 0, 0, 'The Paragons lie!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Doubtful Acolyte');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172872, 1, 0, 'I do not know what came over me... the Forsworn made it sound so simple...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Doubtful Acolyte');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172872, 2, 0, 'Not like this!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Doubtful Acolyte');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (172872, 3, 0, 'I\'m sorry! They told me they could help me remember!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Doubtful Acolyte');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173076, 0, 0, 'Let us show these fiends the true power of the House of the Chosen.', 12, 0, 100, 1, 0, 164772, 0, 203692, 0, 'Alexandros Mograine to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173119, 0, 0, 'Taking... all my strength... to hold back... their onslaught!', 14, 0, 100, 0, 0, 15962, 0, 0, 0, 'Prince Renathal to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173120, 0, 0, 'Incoming! Take cover!', 14, 0, 100, 961, 0, 160266, 0, 0, 0, 'General Draven to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173193, 0, 0, 'Meet me in Elysian Hold. We shall place the heart together.', 12, 0, 100, 1, 0, 164798, 0, 203710, 0, 'Xandria to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173298, 0, 0, 'Stoneborn! Strike down these usurpers!', 14, 0, 100, 0, 0, 159398, 0, 0, 0, 'General Kaal');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173416, 0, 0, 'Вор! Это мое!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Обгоревший собиратель to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173544, 0, 0, 'A Carved Trickster has appeared.', 42, 0, 100, 0, 0, 0, 0, 0, 0, 'Carved Trickster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173581, 0, 0, 'Ура! Рукоглодка радуется!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Рукоглодка to Рукоглодка');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173615, 0, 0, 'She is unchanged. Ever silent. Ever still.', 12, 0, 100, 0, 0, 162381, 0, 195127, 0, 'q60149');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173615, 1, 0, 'When you arrived in Oribos, I saw the Arbiter stir for the first time in many cycles. It gave me hope that she would awaken and resume her blessed Purpose.', 12, 0, 100, 0, 0, 162382, 0, 195128, 0, 'q60149');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173615, 2, 0, 'Mere words cannot describe the glory that was. Step forward, that I may show you.', 12, 0, 100, 0, 0, 162383, 0, 195129, 0, 'q60149');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173615, 3, 0, 'Now souls rich with anima travel only to the Maw. We have tried everything to stop it, to awaken our blessed Arbiter... to no avail.', 12, 0, 100, 0, 0, 162384, 0, 195130, 0, 'q60149');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173615, 4, 0, 'Without anima, drought has gripped the Shadowlands. The afterlives have grown isolated. Fear and uncertainty haunt us all.', 12, 0, 100, 0, 0, 162385, 0, 195131, 0, 'q60149');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173615, 5, 0, 'With countless souls feeding the Jailer, I fear he may grow strong enough to break free from the Maw.', 12, 0, 100, 0, 0, 162386, 0, 195132, 0, 'q60149');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173615, 6, 0, 'It is said he is filled with hatred for the Arbiter. If he strikes her down, it would bring the end of everything the First Ones made.', 12, 0, 100, 0, 0, 162387, 0, 195133, 0, 'q60149');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173615, 7, 0, 'Return to the lower levels. The overseers will prepare you for what is to come.', 12, 0, 100, 0, 0, 162388, 0, 195134, 0, 'q60149');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173748, 0, 0, 'Нашел! Это точно пригодится.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Талефи to Талефи');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173860, 0, 0, 'Polemarch. Maw Walker. I have something you need to see.', 12, 0, 100, 66, 0, 164101, 0, 203829, 0, 'Polemarch Kalisthene to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (173918, 0, 0, '%s lets out a deep, drawn out sigh.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'Tabithia');
