INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (177819, 6, 0, 'We made it! Imagine the wonders to be found here, Maw Walker! Oh, and that waystone of yours as well.', 12, 0, 100, 1, 0, 180813, 0, 208738, 0, 'Ve\'nari to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (177903, 0, 0, 'No purpose... only lies...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Dominated Protector to Player');
