INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (24624, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, |3-6($c).$B$BИменно такие храбрецы, как ты, смогут возродить Черное Копье. Я надеюсь, наши ряды пополнятся теми, кто хотя бы вполовину силен, как ты.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (24625, 1, 273, 0, 0, 0, 60, 0, 0, 'Молодец, $n! Этот нага нам здорово помешал, но никто не может сдерживать нас вечно.$B$BХорошо, что ты с нами, $n. У тебя горячее сердце.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (24626, 0, 0, 0, 0, 0, 0, 0, 0, 'Стремительный Коготь! Киджара говорила, что с ним хлопот не оберешься. Но я не против трудностей – из ящеров с характером получаются самые преданные и храбрые спутники.$B$BСпасибо, $gдорогой:дорогуша;. Ты мне здорово $gпомог:помогла;.', 26365);
