INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (24813, 1, 0, 0, 0, 0, 0, 0, 0, 'Теперь они не посмеют вернуться.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (24814, 1, 0, 0, 0, 0, 0, 0, 0, 'В момент смерти морской ведьмы высвободилось огромное количество энергии, и теперь с ее помощью я смогу увидеть отдаленные земли.$B$BЯ собираюсь связаться с Траллом и хочу, чтобы ты $gувидел это сам:увидела это сама;. Может, это откроет тебе глаза на многое.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (25035, 1, 0, 0, 0, 0, 0, 0, 0, 'Здорово, что ты с нами, $gбратишка:сестренка;.$B$BВ такие минуты начинаешь гордиться, что служишь Вол\'джину, и хочется шагнуть прямо навстречу опасности.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (26131, 1, 1, 0, 0, 0, 0, 0, 0, 'Você não imagina o quanto fico feliz de ver caras novas vindas de Altaforja. Talvez ter enviado o montanhista Cervevada não tenha sido perda de tempo.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (26150, 1, 24, 0, 0, 0, 0, 0, 0, 'William mandou você aqui? Que a Luz o abençoe. Eu... Eu estou bem... É só que... Bom...', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (26197, 1, 0, 0, 0, 0, 0, 0, 0, '$n, isn\'t it? It\'s a pleasure to finally meet you. While you were getting cleaned up, Nevin\'s team told me about your escape from Gnomeregan.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (26199, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, $n. I\'m sure your time in Gnomeregan afforded you ample opportunity to practice your arts. Things are somewhat safer here, but your talents will never go to waste.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (26202, 1, 0, 0, 0, 0, 0, 0, 0, 'So, another one of Nevin\'s \"rescued\" survivors, eh? More than likely, you saved his life from the troggs down there. Did he stumble over his words when he mentioned me? The man has a healthy respect for the demonic powers we channel.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (26206, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, $n. Nevin tells me that you managed to survive the dangers in Gnomeregan by using your wits. That\'s exactly what we need up here. Clever minds and quick blades.', 26365);
