INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (26424, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, $n. You\'ve probably heard others speaking of Operation: Gnomeregan. Let me tell you a bit about what happened and why we left the dwarven city of Ironforge.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (27440, 0, 0, 0, 0, 0, 0, 0, 0, 'Perdoe-me por dizer isto, mas estou perplexo que tenha cumprido a tarefa que lhe passei. Esses monstros que você exterminou foram responsáveis pela morte de incontáveis hostes de heróis que, de tão grandiosos, ainda hoje são lembrados em lendas.$B$BVocê ganhou meu respeito e merece uma recompensa pela bravura que demonstrou.', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (27565, 0, 0, 0, 0, 0, 0, 0, 0, 'Não sei como ainda estou vivo. As patrulhas passam por mim como se eu não estivesse aqui.$B$BEu... estou morto, $c?', 40725);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (27567, 0, 0, 0, 0, 0, 0, 0, 0, 'Hum... Ela não quis cooperar, né? Espero que consigamos detê-los antes que alguma coisa terrível aconteça.', 40725);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (27578, 396, 396, 71, 2, 0, 0, 0, 0, 'Você conseguiu! Você conseguiu mesmo! Finalmente, podemos comemorar todos os anos que Morgan passou planejando e esperando.$B$BEla perdeu terra e família quando Ragnaros partiu este solo ao meio mas, com os Ferro Negro abalados, construiremos um novo reino só nosso nas Estepes Ardentes. Nem mesmo o Senhor do Fogo conseguirá passar pelas nossas muralhas!$B$BNão se esqueça de nos visitar quando tivermos terminado. Você será $Gnosso convidado:nossa convidada; de honra! Obrigado do fundo do meu coração, $n.', 40725);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (27596, 0, 0, 0, 0, 0, 0, 0, 0, 'Você conseguiu o coração! Incrível! É muito mais belo do que eu imaginava$B$BPor favor, $n, aceite isto como pagamento.', 40725);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (27603, 0, 0, 0, 0, 0, 0, 0, 0, 'Maldição! Eu TENHO que descobrir o que tem do outro lado! Pode ser a descoberta da minha vida, entende?!$B$BHmm... quem sabe algumas explosões?', 40725);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (27672, 0, 0, 0, 0, 0, 0, 0, 0, 'Não se preocupe com a câmara: meus homens vão examinar o local. Você tem coisas mais importantes a fazer!', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (27673, 0, 0, 0, 0, 0, 0, 0, 0, 'Incrível, $n. O poder em cada núcleo... e os poderes opostos tão fortes e próximos!$B$BLevarei isso aos devidos anões imediatamente. Esses núcleos são extremamente importantes!', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (27676, 71, 0, 0, 0, 0, 0, 0, 0, 'Fantástico! Ótimo trabalho! Dentro em breve saberemos tudo sobre o Sítio Três!', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (27677, 0, 0, 0, 0, 0, 0, 0, 0, 'Bom trabalho, jovem! Agora pegue os discos e aprenda o máximo que puder!', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (28394, 1, 1, 0, 0, 0, 0, 0, 0, 'Conheço alguns engenheiros que vão querer dar uma olhada nesse diagrama. Bem, fico feliz por não termos mais que nos preocupar com aqueles golens. Bom trabalho, $n.', 40725);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (28562, 1, 0, 0, 0, 0, 0, 0, 0, 'Murder, rookie. That\'s what you\'re looking at on the ground in front of us.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (28852, 273, 1, 0, 0, 0, 0, 0, 0, 'Que ótimas notícias, $n! Fico feliz que nossos dracos lhe foram úteis. Tem minha gratidão por resgatá-los das garras do Martelo do Crepúsculo.', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29173, 0, 0, 0, 0, 0, 0, 0, 0, 'O império de Jin\'do desmorona ao redor dele como as ruínas desta cidade esquecida.', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29186, 1, 1, 1, 0, 0, 500, 500, 0, 'Então isso aí é o fetiche do poderoso Malacrass? É uma bela adição à minha coleção, $n. E agora, acho que tá na hora da recompensa.', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29217, 1, 1, 0, 0, 0, 0, 0, 0, 'Eu nem esperava mais ver eles de novo, $n. A informação que eles conseguiram vai ser a chave pra destruir a influência dos Zandalari em Zul\'aman. Cê tem minha gratidão e a dos meus batedores.', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29406, 1, 0, 0, 0, 0, 0, 0, 0, 'Not bad, $n. You may indeed have chosen the proper path.$B$BGood. Let us continue.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29408, 1, 0, 0, 0, 0, 0, 0, 0, 'The fact that you were able to snatch the flame so easily is no small feat.$B$BThe Edict of Temperance is a scroll of wisdom passed down from my elders\' elders. Wisdom from a more peaceful time.$B$BEvery lesson has its time and place, and with darkness on the horizon, the time for this particular wisdom has passed.$B$BThe burning of the scroll is an acceptance of tidings to come and a promise to action. Your hand carried the flame, and I suspect that it will continue to do so in the future.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29409, 1, 0, 0, 0, 0, 0, 0, 0, 'You truly impress, $n. It seems certain that the path of the $c is indeed the path for you.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29410, 1, 0, 0, 0, 0, 0, 0, 0, 'You came for Aysa? You... you really shouldn\'t interrupt her until she finishes her exercises. She doesn\'t speak to anyone until her routine is done.$B$BIn the meantime, could you maybe help me? I had some bad luck with forest sprites.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29414, 1, 0, 0, 0, 0, 0, 0, 0, 'Your path is set before you. You will be the one to rekindle the spirit of fire and bring it to the safety of the temple.$B$BThis will not be the only time you and Aysa work together. She is strong and wise. You can trust in her.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29417, 1, 0, 0, 0, 0, 0, 0, 0, 'You\'ve got some fight in you! I think I like you!', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29418, 1, 0, 0, 0, 0, 0, 0, 0, '$n! You\'ve returned, roots in hand! You\'re a down-to-business kind of $G guy:gal;, aren\'t you? I like that!', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29419, 1, 0, 0, 0, 0, 0, 0, 0, 'You are too kind, $c.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29420, 1, 0, 0, 0, 0, 0, 0, 0, 'Life isn\'t about finding yourself. Life is about creating yourself. There is a path before you, but you choose the trials you will face, and the trials you will overcome.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29421, 1, 0, 0, 0, 0, 0, 0, 0, 'Remember always, the superior warrior is modest in his speech, but exceeds in his actions.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29422, 1, 0, 0, 0, 0, 0, 0, 0, '<Through the glare, it looks like Huo is smiling. He seems to have warmed up to you.>', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29423, 1, 0, 0, 0, 0, 0, 0, 0, '$n, you\'ve done very well. Huo, the spirit of fire, is here. The temple flames are reignited. You accomplished the challenge I put before you, and with seeming ease.$B$BThere is a greater purpose in bringing Huo to the temple. It is time that I tell you more of things to come.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29424, 1, 0, 0, 0, 0, 0, 0, 0, 'Thank you! You\'re an honorable $c. They\'ve taught you well.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29521, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to see you again, $n.$B$BThis is the pool of the skunk, as I\'m sure you noticed.$B$BOver the many ages of Shen-zin Su, animals have died in some of these magical pools. Through their deaths, their spirits were infused into the waters, and anyone touching those waters will take their form.$B$BThere are several cursed pools here, some more dangerous than others.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29522, 1, 0, 0, 0, 0, 0, 0, 0, 'Hello! You look poised and confident... I like that!$B$BI\'ve heard some rather impressive whispers about you from the training grounds. If you\'re half as capable as they say, I think you and I are going to be good friends!', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29523, 1, 0, 0, 0, 0, 0, 0, 0, 'You know what it is to seize opportunity. I think you and I are kindred spirits, $n.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29524, 1, 0, 0, 0, 0, 0, 0, 0, 'Intriguing, my young pupil.$B$BMost of the other trainees have been here for quite some time, but you are able to match them even within this first hour.$B$BThis speaks well of you, but there are yet other lessons I would see you learn.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29661, 1, 0, 0, 0, 0, 0, 0, 0, 'Good. You\'re a quick study.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29662, 0, 0, 0, 0, 0, 0, 0, 0, 'Good.$B$BNo number of reeds have ever withstood my might.$B$BStep back, and let me dispel your doubts.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29663, 1, 0, 0, 0, 0, 0, 0, 0, 'Even now, your skills increase. Your footing becomes more sure. Your blows strike with more force.$B$BDiscipline and practice are the keys to reaching our full potential.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29664, 1, 0, 0, 0, 0, 0, 0, 0, 'These fires will give you the strength that you\'ve not yet obtained. They will illuminate your potential.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29666, 1, 0, 0, 0, 0, 0, 0, 0, 'The water pincers are strange creatures. They seem to be drawn to still water.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29676, 1, 0, 0, 0, 0, 0, 0, 0, 'Visitors are rare up here, but you are welcome. It is nice to meet you, young $c.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29677, 1, 0, 0, 0, 0, 0, 0, 0, 'Shu\'s desires are not so different from our own. He does not want to be alone. Unwanted, unloved. He longs to be needed, and to share his life with others.$B$BKnowing that, earning his trust is not terribly difficult. He just wants company.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29678, 1, 0, 0, 0, 0, 0, 0, 0, 'It looks like you have his attention, $n. I think he likes you.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29679, 1, 0, 0, 0, 0, 0, 0, 0, 'I have to admit, that looked pretty fun from my angle.$B$BAnd you seem to have made a new friend.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29680, 1, 0, 0, 0, 0, 0, 0, 0, '$n! I wondered when I would see you again!$B$BWell, the plus side is that finding Wugou, the spirit of earth, was easy enough. Waking him... not so much. I think he\'s going to wake quite sore from all the pushing and hitting I\'ve done trying to rouse him.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29690, 5, 0, 0, 0, 0, 0, 0, 0, '$n!?$b$bThey told me you were dead...$b$bI\'m glad to see it wasn\'t true. There\'s Alliance to kill!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29694, 6, 274, 1, 0, 0, 0, 0, 0, 'These are the only survivors, $n?$b$bSo be it. We will play the hand fate has dealt us.$b$b<Gorrok lifts his head up high.>$b$bNumbers be damned. This is where heroes are made.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29730, 1, 0, 0, 0, 0, 0, 0, 0, 'So, as you can see, none of this is Riko\'s fault.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29731, 396, 0, 0, 0, 0, 0, 0, 0, 'And that\'s how we found our Alliance captive.$b$bBut that\'s not all that happened. Not by a long shot...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29743, 0, 0, 0, 0, 0, 0, 0, 0, 'As you look over your sketch, you can\'t help but hope that these creatures don\'t really exist.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29765, 0, 0, 0, 0, 0, 0, 0, 0, '<Zin\'Jun sobs...you think.>$b$bDey done killed Snuff...$b$bWhat\'d \'ol Snuff ever do ta dem? ', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29768, 1, 0, 0, 0, 0, 0, 0, 0, 'Hah, $n! Now that\'s a mallet if I\'ve ever seen one! Let\'s make some noise.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29769, 1, 0, 0, 0, 0, 0, 0, 0, '$n, that\'s the way to do it! You show up, and problems start getting solved. That\'s just one of many reasons why I like you!', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29770, 1, 0, 0, 0, 0, 0, 0, 0, 'Some of these have mouth-sized chunks bitten out of them! Ah well, we can still make use.$B$BThank you for your help, friend.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29771, 0, 0, 0, 0, 0, 0, 0, 0, 'Good.$B$BMy head, it is harder than the densest wood.$B$BStep back, and let me dispel your doubts.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29772, 1, 0, 0, 0, 0, 0, 0, 0, 'Huh... well... that\'s definitely the ringing I expected, but I was hoping for a bit more from Wugou. This guy\'s a deep sleeper, that\'s for sure.$B$BIt\'s alright, $n. I\'ve got another idea, and there\'s not much preparation needed at all - just how I like it.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29774, 1, 0, 0, 0, 0, 0, 0, 0, 'YEAH!!! $n, we make quite a team. Persistence over planning, I always say!', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29775, 1, 0, 0, 0, 0, 0, 0, 0, 'You have done well, young student. Three of the four spirits have been returned to the temple. We are closer to the answers we seek.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29776, 1, 0, 0, 0, 0, 0, 0, 0, '$n, give me a hug! Our parting was not so long this time - I quite prefer it that way!$B$BYou\'ve been working with Aysa, have you not? Our disciplines have always kept us apart, so I know little of her. Watching her perform her exercises now, I must confess that she possesses a grace and discipline that is strangely alluring.$B$BThere are more pressing thoughts though, aren\'t there? $n, let\'s take care of this monkey problem while we search for Dafeng.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29777, 1, 0, 0, 0, 0, 0, 0, 0, 'I am very thankful, young one. I will soon begin the slow task of documenting all of our wisdoms again, so that the scrolls can be hung for the benefit of all to come.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29778, 1, 0, 0, 0, 0, 0, 0, 0, 'You\'re a good $gman:woman;, $n. You have proven that again and again, and I am glad to stand beside you.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29783, 0, 0, 0, 0, 0, 0, 0, 0, 'Good.$B$BMy head, it is harder than the strongest stone.$B$BStep back, and let me dispel your doubts.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29804, 6, 15, 0, 0, 0, 0, 0, 0, 'Zin\'jun is lost?$b$bThis is a dark day indeed, $gbrother:sister;. These vermin will pay!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29815, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, yes, $n. This will do nicely.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29821, 396, 0, 0, 0, 0, 0, 0, 0, 'This crate should be full of supplies...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29822, 396, 0, 0, 0, 0, 0, 0, 0, 'Welcome, traveler.$b$bPlease, make yourself comfortable.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29823, 1, 0, 0, 0, 0, 0, 0, 0, 'Yeah, I ran away. So blikkin\' what? If it weren\'t for me, Kiryn would be dead. Oh, wait... she\'s already dead... already...$b$bGo away! Riko\'s head hurts.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29824, 1, 0, 0, 0, 0, 0, 0, 0, 'The most important thing we learned was that... not only have the Alliance befriended the jinyu, but they\'re giving them weapons, and teaching them how to use them.$b$bThe Alliance now control a force that outnumbers us thirty-to-one. And we - the HORDE - have befriended monkeys.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29827, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent! What a creative solution!$b$bThat should hold them off for a while.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29835, 396, 0, 0, 0, 0, 0, 0, 0, 'Tu conseguiu mesmo?$B$BTu ultrapassou minhas expectativas, cara. Toma esse presente por trazer justiça a esse fim de mundo gelado.', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29838, 0, 0, 0, 0, 0, 0, 0, 0, 'Ahh, é isso aí, $gparceiro:parceira;! Era isso mesmo que eu tava querendo.$b$bToma aí. Fica com isso aí pelo que tu passou.', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29840, 396, 0, 0, 0, 0, 0, 0, 0, 'Valeu, cara.$B$BElas vão ser muito úteis pra gente. Tenho certeza.', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29879, 6, 273, 0, 0, 0, 500, 0, 0, 'Wikket save hozen? And bring slickies?$b$bHmm, maybe wikket not so bad.$b$bTooki Tooki never liked Dook Ookem anyway...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29924, 4, 1, 0, 0, 0, 0, 0, 0, 'The dreaded Kher Shan is dead. You have avenged  Ick Ick, Nab Nab, Eet Eet, Bitty Bitty, and the others that Nibi Nibi couldn\'t remember.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29933, 4, 1, 94, 0, 0, 500, 0, 0, 'Woot! Woot! Wikket da Ook Dook!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29935, 396, 0, 0, 0, 0, 0, 0, 0, 'Hmm.$b$bMean wikkets fighting with nice wikkets?$b$bHozen fight mean wikkets too!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29936, 396, 0, 0, 0, 0, 0, 0, 0, 'Impressive work, $n.$b$bIt had not occurred to me that we might somehow befriend these creatures.$b$bWith their acceptance, we can now turn our attention to more important matters.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29937, 11, 1, 94, 0, 0, 0, 0, 0, 'Yeah!$b$bNow that\'s the stuff. Just you wait and see...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29939, 479, 1, 0, 0, 0, 0, 0, 0, 'We\'ve just revolutionized how these cretins.. err, hozen, feed themselves.$b$bThey\'ll never be the same!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29941, 396, 0, 0, 0, 0, 0, 0, 0, 'Good work $n.$b$bThe away team you assembled has departed.$b$bI hope Gorrok had the sense to change out of that ragged red shirt. I admire his patriotism, but he\'s had it on since we left Orgrimmar.$b$bThey won\'t be surprising anyone if he\'s wearing it...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29942, 4, 1, 66, 0, 0, 0, 0, 0, 'Now everybody got grub! Great hozen wisdom in Eekle Eekle.$b$bMaybe Eekle Eekle become Chieftain someday...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29943, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes!$b$bBy Thrall\'s bloodied fist, I\'ve missed hunting Alliance!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29966, 396, 0, 0, 0, 0, 0, 0, 0, 'The Alliance must be purged from this land, $n!$b$bNo half-measures will do!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29967, 396, 0, 0, 0, 0, 0, 0, 0, 'I\'d say repelling the attack is an understatement!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (29968, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, the smell of progress, and methane. ', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30015, 1, 404, 1, 0, 0, 500, 500, 0, 'Greetings, $n!$b$bWelcome to Dawn\'s Blossom, a home of my people.$b$bSadly I must depart. Alas, a Lorewalker\'s work is never done.$b$bMake yourself at home. Our people could surely make use of one with your talents.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30027, 1, 0, 0, 0, 0, 0, 0, 0, 'A fine weapon. It should suit your needs well.$B$BNow let\'s put it to use.', 20886);
