INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30038, 1, 0, 0, 0, 0, 0, 0, 0, 'A fine weapon. It should suit your needs well.$B$BNow let\'s put it to use.', 20886);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30233, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes! Only one Cracklefang remains to terrorize the goats and yaks of the Vale of Eternal Blossoms. As it should be! Thank you for that!$B$BI sure hope no more big nasty wolves turn up. I really don\'t think I\'m going to be able to come up with another name that can live up to that one.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30234, 0, 0, 0, 0, 0, 0, 0, 0, 'My staff! My gorgeous staff! We shall never part again. I shall lean on you, and you will always be there. And I promise not to poke crocodiles with you anymore.$B$BThank you, friend. Your staff retrieval skills have proved invaluable today!', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30235, 0, 0, 0, 0, 0, 0, 0, 0, 'For the sake of the villagers, I hope he remains at bay for some time.$B$BThank you, my friend.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30236, 0, 0, 0, 0, 0, 0, 0, 0, 'The spirits may be childlike, but they possess generous mounts of strength for their size. Your victory is no small thing, and is very appreciated.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30239, 0, 0, 0, 0, 0, 0, 0, 0, 'Lao-Fe the Slavebinder was one of the most spiteful and vicious mogu of his time. May defeating his spirit act as a reminder to all the mogu in the vale of the fate that awaits them if they seek to defile the sacred pools again.$B$BYour strength is unquestionable. You have my respect, $C.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30248, 0, 0, 0, 0, 0, 0, 0, 0, 'Look at you! All proactive and assertive. Looks good on you.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30296, 0, 0, 0, 0, 0, 0, 0, 0, 'Leader gone. People safe. Rook save village. And friend save village too. Thank you, friend!', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30297, 0, 0, 0, 0, 0, 0, 0, 0, 'Leader gone. People safe. Rook save village. And friend save village too. Thank you, friend!', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30298, 0, 0, 0, 0, 0, 0, 0, 0, 'Their foothold in these ruins shall not last the night.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30299, 0, 0, 0, 0, 0, 0, 0, 0, 'Your efforts here make all the difference. Thank you so much for your help, friend.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30300, 0, 0, 0, 0, 0, 0, 0, 0, 'Many of those you saved are back here in camp recovering. You surely have their gratitude, and mine.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30301, 0, 0, 0, 0, 0, 0, 0, 0, 'Smoke in the distance tells me that you were successful. Good work, $C.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30302, 0, 0, 0, 0, 0, 0, 0, 0, 'You haven\'t let me down. I won\'t forget that.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30305, 0, 0, 0, 0, 0, 0, 0, 0, 'You really good friend. Rook like you. Thank you, good friend!', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30481, 0, 0, 0, 0, 0, 0, 0, 0, 'So many! And so shiny! You did real good!$B$BThank you, little friend. Rook like you a lot.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30620, 6, 0, 0, 0, 0, 0, 0, 0, 'Oh, thank goodness! You\'ve saved all of our skins, believe me.\n\nYou have no idea how much trouble those guys can stir up.\n\nErr, or, I guess you do, now. Kind of. Almost.\n\nImagine that, but a lot worse, and for a couple thousand years.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30655, 2, 0, 0, 0, 0, 0, 0, 0, 'Well, it looks like we\'ve got ourselves a little army, here, thanks to you.\n\nThey may not look like much yet, but a little Horde discipline and we\'ll have them in fighting shape in no time.\n\nBy the way, $n... good work.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30656, 4, 0, 0, 0, 0, 0, 0, 0, 'You can\'t tell me that wasn\'t fun, $n! I knew I should have gone over there with you!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30657, 0, 0, 0, 0, 0, 0, 0, 0, '<The Ordo lieutenants are dead. The shield is down.>$B$B<Time to take out the Ordo leader!>', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (30661, 4, 0, 0, 0, 0, 0, 0, 0, 'Once again you\'ve accomplished what no one else could.$B$BWas there any doubt?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31012, 273, 0, 0, 0, 0, 0, 0, 0, 'Pandaren... you\'re here.$B$BGood.', 18291);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31013, 273, 0, 0, 0, 0, 0, 0, 0, 'I know that few would be willing to leave their homeland to join the Horde. You, and the rest of your people, are welcome among our ranks.$B$BHellscream\'s eyes are upon you, pandaren. Welcome to the Horde', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31112, 0, 0, 0, 0, 0, 0, 0, 0, 'Look at that!$b$bThese supplies should keep us kickin\' for a few more days.$b$bAnd my boots! Ya managed not to blow \'em up. Way ta go, $n!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31121, 0, 0, 0, 0, 0, 0, 0, 0, 'I will help you, traveler, but first I would like to learn a little about this Horde of yours...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31132, 396, 0, 0, 0, 0, 0, 0, 0, 'I suppose the best way to explain what a Lorewalker is all about is to show you...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31134, 396, 0, 0, 0, 0, 0, 0, 0, 'That is enough translating for one day, I think.$b$bThis experience has been an enlightening one, I hope.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31135, 1, 0, 0, 0, 0, 0, 0, 0, 'Why, you look great! I\'ve heard bad things about what happens down there. Well, are you ready to start your training in the ways of the $c?', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31136, 0, 0, 0, 0, 0, 0, 0, 0, 'Well fought. I hope to see more of you soon.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31137, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, $n. You\'ve probably heard others speaking of Operation: Gnomeregan. Let me tell you a bit about what happened and why we left the dwarven city of Ironforge.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31152, 1, 0, 0, 0, 0, 0, 0, 0, 'I am most grateful for the knowledge my forebears have passed on to me.$b$bI stand upon their shoulders.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31167, 6, 0, 0, 0, 0, 0, 0, 0, 'Not so different, you and I.$b$bI believe we can become good friends.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31239, 71, 0, 0, 0, 0, 0, 0, 0, 'Ha ha! Wikket Grooked ookin\' Jinyu!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31240, 0, 0, 0, 0, 0, 0, 0, 0, 'Good, you\'re here.$B$BFor a while there, I was wondering if Ren would even deliver the message.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31241, 71, 0, 0, 0, 0, 0, 0, 0, 'Wikket grookin\' makin\' hozen rich with shinies!$b$b$n makin\' a good second impression. And for hozen, number two is most important!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31244, 0, 0, 0, 0, 0, 0, 0, 0, 'Glad you\'re here. Let\'s get to work.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31245, 0, 0, 0, 0, 0, 0, 0, 0, 'Ren send you my way? He puts on playful air, but he follows through when it matters. Can\'t hold it against him much.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31246, 0, 0, 0, 0, 0, 0, 0, 0, 'You come to help Rook?', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31247, 0, 0, 0, 0, 0, 0, 0, 0, 'Well timed. I was just about to go put some recruits through their paces, but I\'d be much more eager to see what you have to offer.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31249, 0, 0, 0, 0, 0, 0, 0, 0, 'It is strange to be dispatched away from the combat, but Leven is right in that the villagers need our protection. We cannot afford any risks.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31255, 2, 1, 0, 0, 0, 500, 0, 0, 'Oh, you are a friend of Cho\'s? Welcome! Welcome!$b$bI would serve you a banquet, but as you can see, we are in dire straights at the moment.$b$bSay, would you be willing to lend us a hand?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31295, 0, 0, 0, 0, 0, 0, 0, 0, 'Good to see you.$B$BI don\'t know where the mogu mustered all these forces, but we need to hit them hard before they can push their advantage.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31296, 0, 0, 0, 0, 0, 0, 0, 0, 'Sun is smart to send me the strong ones. I\'d accept nothing else.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31395, 5, 0, 0, 0, 0, 0, 0, 0, 'You did it, $n!$b$bThe White Tiger has agreed to allow us to accompany the Pandaren into the Vale of Eternal Blossoms.$b$bWhere the Alliance Prince failed, a hero of the Horde prevailed.$b$bA Horde outpost is already within the Vale. Now, the hard work of repairing the damage can begin.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31495, 25, 0, 0, 0, 0, 0, 0, 0, 'Bom começo, $p!$B$BEm pouco tempo, a Cruzada Escarlate não vai passar de uma nota tosca no rodapé dos anais da história.', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31497, 273, 274, 0, 0, 0, 3000, 0, 0, 'Ah, sim!$B$B<A Cruzada Encapuzada esfrega as mãos de tanta felicidade ao ver o códice.>$B$BAgora eu tenho uma lista com o nome de todos os membros da Cruzada Escarlate!$B$BVocê sabe, né... só para o caso de alguém desaparecer e tal. Não gosto de deixar pontas soltas.', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31511, 396, 0, 0, 0, 0, 0, 0, 0, 'Welcome to the Shrine of Two Moons, traveler.$B$BI know that the Celestials sent you here to help us, but first, take a moment to rest and explore the city.$B$BWelcome to the Vale.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31515, 0, 0, 0, 0, 0, 0, 0, 0, '<A segunda lâmina ungida flutua bem à frente da estátua molhada. Sobe um vapor da espada.>$B$B<Você tem a estranha sensação de que a Mão da Providência esteve esperando por você.>', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31765, 0, 0, 0, 0, 0, 0, 0, 0, 'Superior Horde firepower at its finest!$B$B...and you did okay too I guess.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31766, 1, 0, 0, 0, 0, 0, 0, 0, 'There you are!$b$bI was starting to worry we\'d have to charge in and have all the fun without you.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31767, 0, 0, 0, 0, 0, 0, 0, 0, 'Good work, $c! We have them on their heels!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31768, 0, 0, 0, 0, 0, 0, 0, 0, 'Haha! Good work, $n.$b$bLeave \'em nothing!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31769, 1, 1, 0, 0, 0, 500, 0, 0, 'An air base - they had a hidden airbase here this entire time! Had I known I would\'ve kept more men on the guns, I would\'ve - Uaaghh -$b$b<The color drains from Nazgrim\'s face, and his body begins to exude a mysterious dark energy.>', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31770, 5, 274, 1, 0, 0, 0, 0, 0, 'Your fighting has brought about much destruction for our tiny village!$b$bWe have no interest in your war, but we will not turn your injured away.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31771, 0, 0, 0, 0, 0, 0, 0, 0, 'I am glad you took responsibility for your actions. Left unchecked, negative emotions can quickly overwhelm an individual here, just as they started to overwhelm the one you call \"Nazgrim.\"', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31773, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, so your conflict at the ruins is beginning to pollute the local wildlife? This is very troubling - I have never seen a reaction so intense.$b$bThank you for protecting the village, but it is a tragedy that those poor animals had to die.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31774, 5, 0, 0, 0, 0, 0, 0, 0, '<Zin\'Jun is grievously wounded, but senses that you\'re friendly.>$b$bMonkey-tings! Everywhere dere be dese monkey-tings!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31775, 1, 0, 0, 0, 0, 0, 0, 0, 'The best thing about killing Alliance in Pandaria? It\'ll be months before they get any reinforcements.$b$bThis land will belong to the Horde, $c. We\'re nearly finished.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31776, 66, 0, 0, 0, 0, 0, 0, 0, 'Blood and thunder, $n. Blood and thunder.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31777, 11, 0, 0, 0, 0, 0, 0, 0, 'I hope those ding-dongs enjoyed the party you threw for them!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31778, 1, 1, 0, 0, 0, 0, 0, 0, 'Although I can\'t understand why you chose to bring your war to our land, I can clearly see which side is more virtuous.$b$bThank you for your aid, $r.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31779, 0, 0, 0, 0, 0, 0, 0, 0, 'The Alliance have been routed. You, Nazgrim, Kiryn, Gorrok, Shokia, and Rivett need a base of operations.$b$bThese flags will have to be changed.$b$bFor the Horde!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31853, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome aboard Hellscream\'s Fist, $n!$b$bThis\'ll be her maiden voyage. Ain\'t she a beauty?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31978, 1, 0, 0, 0, 0, 0, 0, 0, 'Well, it\'s not as much as I would have hoped, but it will have to do. I guess there is no sense in cryin\' over spilled brew.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (31999, 1, 0, 0, 0, 0, 0, 0, 0, 'We may have won the battle against the Alliance, but we won\'t get any further with this many jabbering monkeys on our perimeter.$b$bThe hozen are our next target.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (32662, 0, 0, 0, 0, 0, 0, 0, 0, 'Já que está aqui, aproveite para comprar sua montaria! Veron Ambarmanso está perto da porteira do Rancho.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (35172, 0, 0, 0, 0, 0, 0, 0, 0, 'We look forward to serving you again.', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38242, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah sure, I be findin\' stones like this everywhere in Draenor, but never one dis big!', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38307, 0, 0, 0, 0, 0, 0, 0, 0, 'Satisfied?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38427, 0, 0, 0, 0, 0, 0, 0, 0, 'I hope you find something you like!', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38672, 0, 0, 0, 0, 0, 0, 0, 0, '<Pull the lever.>', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38689, 273, 0, 0, 0, 0, 0, 0, 0, 'The color has returned to your face, $n.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38690, 396, 0, 0, 0, 0, 0, 0, 0, 'We always knew the Illidari would be called upon to defend this world. It is why we sacrificed everything to become who... WHAT... we are.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38723, 274, 0, 0, 0, 0, 0, 0, 0, 'Excellent. You have absorbed the powers of these brutes. Look how strong you have grown!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38725, 273, 0, 0, 0, 0, 0, 0, 0, '$n. I\'ve found her.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38727, 1, 0, 0, 0, 0, 0, 0, 0, 'True leadership is hard to come by. Leading from the front is an even rarer trait.\n\nWe are just about ready to assail Brood Queen Tyranna\'s command center.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38728, 1, 0, 0, 0, 0, 0, 0, 0, 'Now that we have the keystone in our possession, there is just one last thing left to do...', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38729, 0, 0, 0, 0, 0, 0, 0, 0, '<As you invoke the power of the Sargerite Keystone, the fel portal is suffused with an otherworldly energy.\n\nIt is time to return triumphant to the Black Temple and aid Lord Illidan in repelling the invading forces from Azeroth and Shattrath City.>', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38759, 0, 0, 0, 0, 0, 0, 0, 0, 'Their souls were being sucked dry by a jailer demon?\n\nThe Burning Legion in this place is far more powerful and brutal than we have ever seen before.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38765, 274, 1, 0, 0, 0, 0, 0, 0, 'Grim. But demon hunters are no strangers to sacrifice. We have given everything to become what we are.\n\nThe Legion will fall. We await your orders, $n.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38766, 273, 0, 0, 0, 0, 0, 0, 0, 'Already, the flow of demons from below has slowed to a trickle.\n\nI can sense the doom guard\'s energy. You have become more powerful.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38813, 273, 0, 0, 0, 0, 0, 0, 0, 'Our forces have engaged the Legion.\n\nSoon we will stand together victorious with Lord Illidan at the Black Temple.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38819, 273, 6, 0, 0, 0, 0, 0, 0, 'I am in complete agreement. From where I\'m standing, you utterly decimated their forces.\n\nWhat do you think? Are we ready to fly up to that command center and take the Sargerite Keystone?', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (38990, 0, 0, 0, 0, 0, 0, 0, 0, 'O Lich Rei fez uma sábia escolha ao dar-lhe essas lâminas, $n. Você se provou mais que $gdigno:digna; delas.$b$bNo entanto, devo admitir que estou feliz por não ser eu. Cansei de ter armas mágicas ditando meu destino.', 40120);
