INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39049, 273, 0, 0, 0, 0, 0, 0, 0, 'An inquisitor\'s essence... perfect. Allari was right.\n\nNow to complete my ritual and spy on the Legion\'s leadership.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39050, 1, 0, 0, 0, 0, 0, 0, 0, 'That\'s new. She looks like some kind of spider demon. As if the Legion needed spiders...', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39051, 0, 0, 0, 0, 0, 0, 0, 0, 'Suramar não fica longe daqui.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39247, 0, 0, 0, 0, 0, 0, 0, 0, 'Cadê os outros? Eles não...$B$BPercebo. Eles deram a vida por uma causa justa. As Lâminas Gêmeas mudarão o curso desta guerra, pode anotar.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39261, 1, 0, 0, 0, 0, 0, 0, 0, 'Temos muito a fazer e pouco tempo.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39262, 5, 0, 0, 0, 0, 0, 0, 0, 'I KNEW that I\'d sensed something foul!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39495, 25, 0, 0, 0, 0, 0, 0, 0, 'Excellent. You\'ve absorbed that fel lord\'s power and become more powerful in-turn.\n\nNow, we can fully concentrate on destroying the Legion and their Brood Queen.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39516, 1, 274, 0, 0, 0, 0, 0, 0, 'I blame myself, $n. Mannethrel was already struggling. It is a never-ending battle we all must endure... to control the fel energies that we have taken in.\n\nMaybe it would be best if you held off teaching the others for now. We can not afford to lose anyone else.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39663, 6, 0, 0, 0, 0, 0, 0, 0, 'Can you sense her power? It will take all of you combined.\n\nEven so... I better go secure our way out of here.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39682, 274, 0, 0, 0, 0, 0, 0, 0, 'Kayn and Altruis said you would be coming. Just in time, $n.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39683, 25, 0, 0, 0, 0, 0, 0, 0, 'Sí... Mírate... ¡rebosante de conocimientos y poderes nuevos!', 36839);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39684, 274, 0, 0, 0, 0, 0, 0, 0, 'I had no doubt you would know what to do.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39685, 274, 0, 0, 0, 0, 0, 0, 0, 'I knew you could do it, $n. We could not let that creature escape.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39686, 274, 0, 0, 0, 0, 0, 0, 0, 'This is serious. Their core ideals are in direct conflict.$B$BI fear they will take this duel to the death.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39688, 274, 0, 0, 0, 0, 0, 0, 0, 'Logramos salir de la bóveda, pero nuestro trabajo está lejos de acabar.$B$BEl ataque de la Legión a este mundo apenas ha comenzado.$B$BSi queremos que Azeroth sobreviva, puede que tengamos que trabajar con este... Archimago Khadgar.', 36839);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39689, 274, 0, 0, 0, 0, 0, 0, 0, 'If you are ready, I will take you to our destination.$B$BAre you prepared to leave?', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39691, 274, 0, 0, 0, 0, 0, 0, 0, 'Tempos sombrios se abatem sobre Ventobravo, $n.$b$bCom a morte do rei Varian Wrynn na Costa Partida, os líderes da Aliança são acometidos pela insegurança e pela tensão.$b$bA tragédia não aconteceu por acaso. A Legião Ardente quer semear a desordem entre os inimigos, enfraquecendo a presa de dentro para fora.$b$bPrecisamos agir.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39698, 0, 0, 0, 0, 0, 0, 0, 0, 'Do you understand the gravity of our task? If we do not fight for our future, we will not have one.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39746, 1, 274, 0, 0, 0, 0, 0, 0, 'Thank you, $n. You saved me. You saved us all.$b$bI was... outmatched. My relationship with the elements has changed since the events on Draenor. I have some soul-searching to do. $n, you must carry the Earthen Ring forward without me. You are the leader they need.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39771, 1, 66, 0, 0, 0, 0, 0, 0, 'You really are the chosen one, $n. I will gladly lay down my life for you.$b$bWherever you take the Earthen Ring, I will follow!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39799, 1, 0, 0, 0, 0, 0, 0, 0, 'Ótima escolha, $ct.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39801, 0, 0, 0, 0, 0, 0, 0, 0, 'Hey, you\'re alive! I guess Withers isn\'t as crazy as I thought.$B$BIt\'s a miracle you escaped the Skyfire alive! The pieces of that thing must be scattered all over these mountains after that explosion!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39832, 1, 0, 0, 0, 0, 0, 0, 0, '$gSenhor da morte:Senhora da morte;, preparei um breve relatório com tudo que descobrimos sobre essa nova terra.$b$bAcredito que as informações serão do seu interesse.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (39864, 2, 0, 0, 0, 0, 0, 0, 0, '<Nathanos glances over at you, pausing for a moment before acknowledging your presence.>$B$BYes?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40051, 273, 0, 0, 0, 0, 0, 0, 0, 'Surprising. I agree. Well chosen, $n.\n\nNow, you must teach the rest of us.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40077, 1, 0, 0, 0, 0, 0, 0, 0, 'That ought to get their attention.\n\nNow, onto the business of summoning in the rest of our forces.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40222, 6, 0, 0, 0, 0, 0, 0, 0, 'Can you feel the power emanating from the book? I cannot wait to learn its secrets.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40225, 1, 2, 0, 0, 0, 0, 0, 0, 'The Earthen Ring rises once more to defend Azeroth. You will be our Farseer, $n, and we will be your instruments. Only by working together can we preserve our world.$b$bWe are yours to command, Farseer.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40247, 1, 0, 0, 0, 0, 0, 0, 0, 'Que bom ver você de novo. Se houver algo que eu possa fazer para ajudá-l$go:a; a combater a Legião, é só falar.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40249, 0, 0, 0, 0, 0, 0, 0, 0, 'Cadê os outros? Eles não...$B$BEntendo. Eles deram a vida por uma causa justa. As Lâminas de Guerra mudarão o curso desta guerra, escreva o que eu digo.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40254, 25, 0, 0, 0, 0, 0, 0, 0, 'Yes... just look at you... surging with new power and knowledge!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40255, 274, 0, 0, 0, 0, 0, 0, 0, 'We escaped the Vault, but our work is far from over.$B$BThe Legion\'s assault on this world is just beginning.$B$BIf Azeroth is to survive, we may have to work with this... Archmage Khadgar.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40373, 274, 0, 0, 0, 0, 0, 0, 0, 'I see. I support you no matter your choice. With this new beginning, we shall rebuild the Illidari.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40378, 0, 0, 0, 0, 0, 0, 0, 0, 'I saw them run by. Not the most impressive of our troops, but the Ashtongue have proven effective in the past.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40379, 0, 0, 0, 0, 0, 0, 0, 0, 'Grim business, but we\'ve all sacrificed just about everything to get to where we are.\n\nWe will do ANYTHING it takes to defeat the Burning Legion. Anything.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40518, 1, 1, 0, 0, 0, 0, 0, 0, 'Glad to see you back on your feet, $n. Many good soldiers who sailed for the Broken Shore were not so fortunate.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40522, 1, 0, 0, 0, 0, 0, 0, 0, 'You have served the Horde well in the past, $n.$b$bMore will be asked of you... much more.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40605, 1, 1, 0, 0, 0, 0, 0, 0, 'Ah, so you\'re the vaunted $c I\'ve heard about. Good.$b$bWe shall see if your reputation holds true.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40607, 1, 1, 0, 0, 0, 0, 0, 0, 'It would seem the demon hunters\' concerns were well-founded. To think the demons would dare worm their way into our city during this period of mourning!$b$bThis does not bode well for the rest of my kingdom.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40714, 1, 0, 0, 0, 0, 0, 0, 0, 'Tenho certeza que você tem muitas perguntas, mas isso vai ter que esperar. Estamos em guerra agora!$b$bPrecisamos nos mover ainda mais rápido, mas tentarei explicar o que eu puder.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40715, 1, 0, 0, 0, 0, 0, 0, 0, 'Ótima escolha.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40740, 1, 0, 0, 0, 0, 0, 0, 0, 'Esse machado me deixa inquieto, $n. O poder dele é inegável, mas sua verdadeira essência é materialização da fome eterna.$b$bNós vamos usá-lo porque temos que fazer o que for preciso para vencer essa guerra. Porém, eu teria cuidado com essa arma se fosse você.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40760, 396, 396, 0, 0, 0, 1000, 0, 0, '<Allari furrows her brow as she speaks.>$B$BYou didn\'t think the leadership of the Horde could gather in one location and go unnoticed by the Burning Legion, did you?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40814, 1, 0, 0, 0, 0, 0, 0, 0, 'Boa escolha. Não há um instante a perder.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40819, 1, 0, 0, 0, 0, 0, 0, 0, 'Não me dou com esses magos e sua gente, mas eles são aliados necessários.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40930, 0, 0, 0, 0, 0, 0, 0, 0, 'Não tenho muita escolha. Não posso enfrentar outra emboscada dos Cavalgantes Negros sem ajuda, e você é formidável, obviamente.$B$BAjudarei a encontrar seu artefato, se você permitir que eu devolva as outras a seu lugar de direito.$B$BTemos um acordo, $gcaveleiro:cavaleira; da morte?', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40931, 0, 0, 0, 0, 0, 0, 0, 0, 'Por mais que sua companhia me incomode, você está provando ser $Gum poderoso aliado:uma poderosa aliada;.$B$BAcredito que a chave para encontrarmos os Cavalgantes Negros esteja aqui em algum lugar. É estranho que os cavalgantes não tenham nos seguido até aqui, mas devemos nos apressar antes que eles mudem de ideia.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40932, 0, 0, 0, 0, 0, 0, 0, 0, 'Energia sombria emana do diário, que parece trazer os escritos de Ariden sobre seu tempo na Trilha do Vento Morto. Um trecho em particular se destaca:$B$B\"Ouço os sussurros. Eles me chamam do solo úmido dos túmulos rasos, relembrando meu legado.$B$B\"Mas que legado? O mercador que eu fui um dia não tinha nenhuma linhagem especial...$B$B\"No entanto, sou atraído a isso. A lãmina do terror... a destruidora de mundos... Apocalipse.\"', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40933, 0, 0, 0, 0, 0, 0, 0, 0, 'O calafrio na minha espinha me diz que você foi bem-sucedido.$B$BO que você descobriu?', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40986, 0, 0, 0, 0, 0, 0, 0, 0, 'Mesmo admitindo que não teria sido capaz de chegar sozinho até aqui, jamais me esquecerei das transgressões ocorridas neste dia.$B$BVocê tem o que veio buscar, $gcavaleiro da morte:cavaleira da morte;. Creio que seja hora de partir.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40987, 0, 0, 0, 0, 0, 0, 0, 0, '$gO campeão:A campeã; do Lich Rei retorna, e com uma arma poderosa para usar contra nossos inimigos demoníacos!$B$BQue a Legião Ardente trema ao ver nossas forças!', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (40992, 0, 0, 0, 0, 0, 0, 0, 0, 'Your skills are... impressive. If it matters at all, I would have taken no pleasure in killing you.$B$BLook, I\'m no fool - noone in their right mind would willingly choose death. I\'ve yet to see what the others clearly see in you, but hey -$B$BAt least I know you can fight.', 21108);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41037, 1, 0, 0, 0, 0, 0, 0, 0, 'O Martelo Vil está operando plenamente.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41062, 1, 0, 0, 0, 0, 0, 0, 0, 'É uma honra servi-$Glo:la;, $n.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41066, 1, 0, 0, 0, 0, 0, 0, 0, 'Nós não podemos subestimar a Legião. Receio que eles sejam mais poderosos do que imaginamos.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41067, 1, 0, 0, 0, 0, 0, 0, 0, '<Kayn suspira.>$B$BEu conheço esse olhar. Eu também contemplei o crisol e vi os horrores da tumba.$B$BNós temos que agir logo.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41069, 1, 0, 0, 0, 0, 0, 0, 0, 'Concordo. Você é $Gum:uma; estrategista tão talentos$Go:a; quanto eu.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41102, 1, 1, 66, 0, 0, 0, 0, 0, 'With each day that passes I see more of Lord Illidan in you, Master $n. You have sacrificed more than any... and the war is far from over.$B$BWe may not see eye to eye at times, but I trust your capability to lead us to finish our mission - no matter the cost.$B$BNo Illidari in your command is as capable as I, if you ever require my skills just say the word and consider it done.', 20994);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41217, 1, 0, 0, 0, 0, 0, 0, 0, 'For a $r who has spent so much time inside Gnomeregan, you look positively fit and healthy, $n. That\'s good. Before we can complete what Operation: Gnomeregan began, we\'re going to need more hunters like you.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41218, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, $n. You\'ve probably heard others speaking of Operation: Gnomeregan. Let me tell you a bit about what happened and why we left the dwarven city of Ironforge.', 26365);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41221, 1, 0, 0, 0, 0, 0, 0, 0, 'A cidadela era uma das posições mais valiosas da Legião. Ela nos servirá bem.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41335, 1, 0, 0, 0, 0, 0, 0, 0, 'A good choice. There is no time to lose - let us begin!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41510, 1, 2, 0, 0, 0, 0, 0, 0, 'A sound decision, Farseer. Behind me I have opened up an astral portal to Dalaran to help our people move quickly between our base of operations and the Broken Isles. Step through to return to Dalaran. You may always use the portal to travel back here whenever you need to augment your artifact or command your followers.$b$bMay the elements guide you, Farseer.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41804, 1, 0, 0, 0, 0, 0, 0, 0, 'Não precisa agradecer, só me prometa que você não vai explodir alguma coisa na cidade por acidente; nossa conta de manutenção já está astronômica.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41806, 6, 0, 0, 0, 0, 0, 0, 0, 'O Arquimago tinha alguma coisa útil?', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41807, 1, 0, 0, 0, 0, 0, 0, 0, 'Nada bom... nada bom mesmo.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (41891, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
