INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (42114, 0, 0, 0, 0, 0, 0, 0, 0, 'I have seen you before, Farseer.$b$b<Lord Neptulon judges you silently for a moment before continuing.>$b$bI sense the potential for great power within you. We shall see if this prophecy holds true...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (42188, 1, 0, 0, 0, 0, 0, 0, 0, 'I hear you have been making good progress down among the Broken Isles. Good. We, too, have been busy here at the Maelstrom.$b$bThere are important matters that await us...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (42197, 1, 66, 0, 0, 0, 500, 0, 0, 'Lord Neptulon has sent me to aid the Earthen Ring in the defense of Azeroth.$b$bI have been commanded to serve the Farseer of the Earthen Ring with the same loyalty I give to the Lord of Tides. I will fulfill this duty until the Burning Legion is driven from this world and the future of Azeroth is secured.$b$bI am yours to command, Farseer.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (42198, 1, 66, 0, 0, 0, 500, 0, 0, 'Uniting the elements won\'t be easy, $ct $n. You\'ll be needin\' all the help you can get!$b$bAye, These are dark times indeed... but we\'ll face \'em t\'gether, don\'t you worry.$b$bOl\' Mylra has yer back, Farseer.$b$bNow, let\'s get a move on then! There ain\'t no time to waste!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (42383, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. Stormcaller Mylra and Duke Hydraxis are worthy champions. We can trust them to carry out any task we ask of them.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (42671, 1, 0, 0, 0, 0, 0, 0, 0, 'Você selecionou seus dois primeiros campeões. Grandes coisas nos aguardam, $n.\n\n<Kor\'vas murmura alguma coisa em voz baixa sobre ela mesma não ser uma delas.>', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (42677, 1, 0, 0, 0, 0, 0, 0, 0, 'Bem, pelo menos vamos ter algumas caras novas por aqui. Isso se a cara de um degradado pode ser chamada de nova.\n\n<Kor\'vas ri.>', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (42679, 273, 0, 0, 0, 0, 0, 0, 0, 'Fabuloso. Esses novos Grislíngua lhe oferecerão a flexibilidade necessária para posicionar tropas onde você achar melhor.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (42695, 1, 273, 0, 0, 0, 0, 0, 0, 'Uma honra, $n. É claro que servirei como seu campeão e segundo-em-comando. Poderia ter sido de outra maneira?\n\nApenas começamos a avançar com os planos do mestre, e no entanto, você já fez grandes avanços. Para derrotarmos a Legião Ardente, precisamos nos manter vigilantes. Qualquer outro objetivo não é nada comparado a este. O futuro de tudo está na balança.\n\nNo fim do dia, seremos vitoriosos... custe o que custar!', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (42697, 6, 1, 0, 0, 0, 1000, 0, 0, 'Você quer que eu seja um de seus campeões?\n\n<Asha contempla cuidadosamente o seu pedido por um momento.>\n\nEu aceito, mas com uma condição. Quero que me coloque em trabalho de campo. Enquanto sou útil aqui no Martelo Vil, meus talentos serão melhores aproveitados em Azeroth.\n\nAlém disso, eu acredito que o plano é colocar a Legião Ardente de joelhos?', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (42869, 1, 0, 0, 0, 0, 0, 0, 0, 'Tenho notícias animadoras para compartilhar, $n.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (42872, 1, 0, 0, 0, 0, 0, 0, 0, 'Mal posso esperar para me juntar ao restante das nossas forças.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (43334, 1, 0, 0, 0, 0, 0, 0, 0, 'Welcome Shaman! You seek the weapons of the storm god Ra-den?$b$bMany have traveled here to Pandaria, but none have proven themselves worthy.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (43338, 1, 6, 0, 0, 0, 0, 0, 0, 'That tiger is serious about this, isn\'t he?$b$bWhile you were here, others came to the Temple of the White Tiger to present their offerings to him. It looks like we\'re going to have some competition.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (43926, 1, 1, 0, 0, 0, 0, 0, 0, 'The ships are leaving from the dock in Bladefist Bay.$B$BBefore you leave, prepare yourself. I have the feeling that this fight won\'t be an easy one.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (43962, 1, 0, 0, 0, 0, 0, 0, 0, 'Muito bem.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (44091, 396, 396, 0, 0, 0, 0, 0, 0, 'An attack on the gates of Orgrimmar! Thank goodness you defended the Warchief!$b$bI must inform the Council of Six at once. This is grave news indeed.$b$bPlease, feel free to look around Dalaran. The advantage you seek against the Legion just might turn up.', 21737);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (44281, 1, 1, 0, 0, 0, 0, 0, 0, 'You look quite prepared for battle. Do not let the bloodlust overtake you out there.$b$bThe Warchief wishes to present you with this ring before you head to war. May you return with the heads of many demons.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (44383, 1, 0, 0, 0, 0, 0, 0, 0, 'Sábia escolha. Precisamos de todas as armas que pudermos reunir para derrotar a Legião de uma vez por todas.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (44401, 1, 0, 0, 0, 0, 0, 0, 0, 'Muito bem.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (44463, 0, 0, 0, 0, 0, 0, 0, 0, 'Devo admitir, você nos salvou do que seria uma terrível tragédia. Você tem minha gratidão, $c.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (44471, 0, 0, 0, 0, 0, 0, 0, 0, 'É claro que a Legião tentaria se infiltrar neste funeral. Tendo todos os líderes da Aliança reunidos em um só lugar e ao mesmo tempo, é tentação demais para que os demônios resistam.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (44473, 1, 1, 0, 0, 0, 0, 0, 0, 'Acredito que o jovem rei está seguro.$b$bTemos mais trabalho à frente, $c. Muito mais trabalho.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (45753, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, a sigil. From the Abyssal High Council, no less!$B$B<Warlord Gorchuk wipes a bead of sweat from his brow.>$B$BI... I had assumed that it was... that you were... oh, never mind.$B$BFor the Horde!', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (45758, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, I see, it is a hard-earned reward from one of your many battles. Well done, $C!', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (45762, 0, 0, 0, 0, 0, 0, 0, 0, 'A sigil from the Abyssal High Council? You\'ve outdone yourself today, $R.$B$BFor the Alliance!', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (46062, 0, 0, 0, 0, 0, 0, 0, 0, 'Glrrrrms!', 0);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (46064, 0, 0, 0, 0, 0, 0, 0, 0, 'Mrrga mr. Gll-mrrr!', 0);
