INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (46926, 0, 0, 0, 0, 0, 0, 0, 0, 'You have my thanks. I think having someone of de Horde dispense dis justice was extra fitting.$b$bDe fact dat we have left our harbor so unprotected worries me greatly.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (46927, 11, 0, 0, 0, 0, 0, 0, 0, 'It will not be long before these disciples are replaced with new ones. Hopefully, de next ones will learn from de past.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (46928, 11, 0, 0, 0, 0, 0, 0, 0, 'Ha ha ha. You can tell a lot about a tribe by what dey worship.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (46929, 1, 0, 0, 0, 0, 0, 0, 0, 'When visitors do not understand de language of your laws, it becomes necessary to explain your laws in a language dey do understand.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (46930, 1, 0, 0, 0, 0, 0, 0, 0, 'Your deeds had reached my ears before your feet ever set foot in my land. It is time we spoke.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (46931, 1, 1, 0, 0, 0, 0, 0, 0, 'Welcome to de Vault of Kings, $n. We move new treasures dat we find into dis room, all because of dis object on de wall.\n\nDis object dates back farther than anything we know. It is my hope dat I can one day discover its true purpose.\n\nI should not keep you, however. I am sure you have pressing business with Princess Talanji.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (46957, 0, 0, 0, 0, 0, 0, 0, 0, 'Dat is far enough, Horde scum.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47103, 603, 0, 0, 0, 0, 0, 0, 0, '$n! You ready ta go ta a cursed swamp?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47105, 603, 0, 0, 0, 0, 0, 0, 0, 'De blood trolls are monsters, $n. Dey are all around us. We stop dem today.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47130, 273, 0, 0, 0, 0, 0, 0, 0, 'My people deserve better dan dis. When dis is done, we must properly honor dem in Zuldazar.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47188, 25, 0, 0, 0, 0, 0, 0, 0, 'Getting de loa\'s help may be de only chance we have at stopping de blood trolls. \n\nLet\'s get moving.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47198, 1, 0, 0, 0, 0, 0, 0, 0, 'It worked? Good! I shall have some of de wounded help me craft more.', 31478);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47200, 273, 0, 0, 0, 0, 0, 0, 0, 'At last, some breathing room. Now we must press our advantage.', 31478);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47201, 11, 0, 0, 0, 0, 0, 0, 0, 'Ha! Thought I be singin\' with Bwonsamdi soon. But no.', 31478);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47204, 1, 0, 0, 0, 0, 0, 0, 0, 'It may seem like a lot, but trust me when I say we saw a lot more comin\' when on our way here.', 31478);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47205, 1, 0, 0, 0, 0, 0, 0, 0, 'Dat vile creature took out half da scouts who came with me. Ya did good.', 31478);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47221, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'m glad you were among those called for the expedition to Argus. There will be plenty of fighting ahead.', 27101);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47226, 1, 0, 0, 0, 0, 0, 0, 0, 'Are you de Hordemon I have been hearing so much about? I expected you to be a bit larger. Ha!$b$bHey! What do you have dere?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47228, 273, 0, 0, 0, 0, 0, 0, 0, 'I hear this region is very supportive of such life. Their numbers will recover... elsewhere.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47229, 4, 0, 0, 0, 0, 0, 0, 0, 'Thank you! Though we must not stay here. More blood trolls are coming. Where is my father?', 31478);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47235, 273, 0, 0, 0, 0, 0, 0, 0, 'Excellent work. Allow me to review the results and we will be on our way.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47241, 1, 11, 0, 0, 0, 0, 0, 0, 'You are looking for Bwonsamdi\'s temple? You must be desperate to be going dere.$b$bLucky for you, I am headed dat way myself.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47244, 603, 1, 0, 0, 0, 0, 0, 0, 'Dis should do it. Her soul is perfect for an offering.$b$bNow, before we go to de temple, there is one more thing to do...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47245, 603, 18, 0, 0, 0, 0, 0, 0, 'Goblins... of all da worthy units in da Horde, why da warchief have ta be sendin\' us goblins?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47247, 11, 1, 273, 0, 0, 0, 0, 0, 'Ah, I feel their souls strengthening me powers. I knew ya be worth Bwonsamdi\'s time.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47248, 18, 0, 0, 0, 0, 0, 0, 0, '<Salazae looks distraught.>$b$bOh! Valjabu... why did you go on without me?$b$b<A ghostly tear drops from her eye.>$b$bThank you, stranger. Now I can pass on with no regrets.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47249, 1, 661, 11, 0, 0, 0, 0, 0, 'Dese blood trolls and their \"god\" G\'huun, dey be far more of a threat than I realized. Da ritual ta seal da pact between ya and I be incomplete, but considerin\' da circumstances, I tink we can skip da formalities.$b$bAs long as da price be paid, ya can count on Bwonsamdi\'s aid.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47250, 6, 603, 11, 0, 0, 0, 0, 0, 'So, you formed a pact with Bwonsamdi, too? Good, de more souls for Bwonsamdi, de stronger he is, and dat works out better for de both of us. Just leave some hunting for Hanzabu. I have my own pact to pay for!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47258, 0, 0, 0, 0, 0, 0, 0, 0, 'Though our warchief may balk at further delays, I believe we are honor-bound to assist the Zandalari. $B$BThis is about more than gaining an ally in wartime. The Horde was founded on the idea of aiding those facing their hour of need.$B$BJust as Thrall once came to the defense of my people, so we must help save Zandalar.', 31478);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47259, 1, 0, 0, 0, 0, 0, 0, 0, 'Not bad. Dat baby is looking stronger already.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47261, 1, 0, 0, 0, 0, 0, 0, 0, 'Whoops. At least dat was quite entertaining to watch! Ha ha ha!$b$bI guess you two have earned your places here for sure!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47262, 273, 1, 0, 0, 0, 0, 0, 0, 'Ateena will not get away from us, we have to keep moving.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47263, 274, 0, 0, 0, 0, 0, 0, 0, 'De blood troll threat is bigger than I ever imagined. Dat construct is enough to destroy all of Zuldazar\n\nWe need more than de Horde to stop dis.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47264, 273, 0, 0, 0, 0, 0, 0, 0, 'Are you finished? Good, because we are close to ending dis menace.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47272, 1, 0, 0, 0, 0, 0, 0, 0, 'Meat is pretty miraculous stuff, you know!$b$bEr, you might want to keep the details about this to yourself for the time being.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47310, 1, 0, 0, 0, 0, 0, 0, 0, 'I will look after your little one for de time being. Go on your adventures. Learn de ways of de Zandalari. When you are both ready, I will have an offer for you.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47311, 1, 0, 0, 0, 0, 0, 0, 0, 'I see a gleam of confidence in your hatchling\'s eyes. Good. It will need it.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47312, 1, 0, 0, 0, 0, 0, 0, 0, 'Good. Now dat de bloodthirsty matriarch is gone, maybe we can plant one of ours back into de brood as a replacement... Hmmm.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47313, 1, 0, 0, 0, 0, 0, 0, 0, 'You are going to make more enemies than allies asking questions like dat around here.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47314, 1, 0, 0, 0, 0, 0, 0, 0, 'We will not let Rakera and whatever she knows be lost to de sands.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47315, 1, 0, 0, 0, 0, 0, 0, 0, 'These snakemen will pay for what they\'ve done to my home.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47316, 1, 0, 0, 0, 0, 0, 0, 0, 'Dis is Rakera\'s handwriting. I\'d know it anywhere.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47317, 1, 0, 0, 0, 0, 0, 0, 0, 'Jorana knew de risks when she agreed to enter de sands, but I still can\'t help but feel responsible for her death.$B$BI can only hope dat Tarkaj somehow made his way to safety.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47319, 6, 0, 0, 0, 0, 0, 0, 0, '<Kiro quickly inspects the glands.>$B$BThese should work well.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47320, 1, 0, 0, 0, 0, 0, 0, 0, 'Did these creatures come to our aid?$B$BDe last thing I remember, we were flying over de sethrak empire. There was a blinding flash of light before things went dark...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47321, 1, 0, 0, 0, 0, 0, 0, 0, 'You\'re more resourceful than you look.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47322, 1, 0, 0, 0, 0, 0, 0, 0, 'Thanks, $n. We owe you our lives.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47324, 1, 0, 0, 0, 0, 0, 0, 0, 'We cannot let the Faithless take this temple!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47327, 1, 0, 0, 0, 0, 0, 0, 0, 'Their blood flows down the dunes back to their fortress. The sethrak will think twice before attacking our burrows again.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47329, 273, 0, 0, 0, 0, 0, 0, 0, 'You used your hands! How quaint. The nightborne have devised a number of ways to avoid such petty labors.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47418, 1, 0, 0, 0, 0, 0, 0, 0, 'Wow! That worked even better than I thought! I\'m gonna make a fortune!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47422, 661, 0, 0, 0, 0, 0, 0, 0, 'I will not have dat blood in my temple!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47423, 1, 0, 0, 0, 0, 0, 0, 0, 'What? Dat sneering, vile raptor zealot is calling me out for not doing my share? Bah!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47432, 1, 0, 0, 0, 0, 0, 0, 0, 'So, you have chosen de path of Pa\'ku. May de winds of war be ever in your favor.$b$bFor myself, I follow Rezan, de devilsaur loa of kings. He and I have not spoken for many years, though.$b$bPerhaps I shall pay him a visit one of dese days.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47433, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, so dat bent-tusk nose-breather is trying to win you over with a little flight and some charm, eh?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47434, 6, 0, 0, 0, 0, 0, 0, 0, 'Why are you scaring away my flock?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47435, 0, 0, 0, 0, 0, 0, 0, 0, 'Did you have to kill so many?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47437, 5, 0, 0, 0, 0, 0, 0, 0, 'Dis is worse than we thought.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47438, 6, 0, 0, 0, 0, 0, 0, 0, 'Your choice is made.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47440, 11, 0, 0, 0, 0, 0, 0, 0, 'Oh, good. She did not have you dropped. Dat is a good sign. Come, let us show de public your choice.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47441, 0, 0, 0, 0, 0, 0, 0, 0, 'A thief has come to steal from thieves?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47442, 0, 0, 0, 0, 0, 0, 0, 0, 'Haha! Well done, wealthy stranger. When ya out and about in dese lands, keep ya eyes open. Ya never know when one of de children might be pointin\' ya ta a useful place.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47445, 11, 0, 0, 0, 0, 0, 0, 0, 'Welcome to de steps of Zanchul, $p. You will be treading on sacred ground soon.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47491, 1, 273, 0, 0, 0, 0, 0, 0, 'Mmm, dese will do nicely.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47497, 1, 0, 0, 0, 0, 0, 0, 0, 'Don\'t mind half of what those trolls say. Dey don\'t really mean it.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47498, 22, 0, 0, 0, 0, 0, 0, 0, 'Thank de spirits you found Zulsan. Sandfury trolls kidnapped de rest of de gang!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47499, 1, 0, 0, 0, 0, 0, 0, 0, 'Dese be cursed and evil things.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47501, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'ll hold onto dis blood until we can get back to de inn to make de drinks.\n\nDon\'t worry, I won\'t let you miss out on dem. I know you\'re excited to try my famous scorpid blood drinks!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47502, 1, 0, 0, 0, 0, 0, 0, 0, 'Ya one of us now, $p.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47503, 1, 0, 0, 0, 0, 0, 0, 0, 'Dat\'ll teach de Sandfury to fear de Goldtusk Gang!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47509, 1, 0, 0, 0, 0, 0, 0, 0, '$p, glad to see you are not dead yet.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47512, 0, 0, 0, 0, 0, 0, 0, 0, 'The swamp of Nazmir represents the largest threat to my people. I am glad you chose to help me in this.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47513, 0, 0, 0, 0, 0, 0, 0, 0, 'Jakra\'zet poses a serious threat to my kingdom and your Horde. It would be good for you to investigate his activities.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47514, 0, 0, 0, 0, 0, 0, 0, 0, 'My father\'s trust is not easily earned.$b$bBelieve me.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47518, 1, 0, 0, 0, 0, 0, 0, 0, 'Aya... did you have to bring Loti? She is never going to let me forget about dis.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47520, 1, 0, 0, 0, 0, 0, 0, 0, 'Well done. Dat should blind Yazma.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47521, 0, 0, 0, 0, 0, 0, 0, 0, 'From here we can strike back at our enemies.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47522, 1, 0, 0, 0, 0, 0, 0, 0, 'They seek to kill us. They seek to break us.$b$bAll they have done is unite us in what will be the greatest hunt the Zandalari have ever known.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47525, 1, 603, 0, 0, 0, 0, 0, 0, 'I can fight no more. De blood trolls, dey are making a move on Krag\'wa in a big way. Dey are going to feed him to their bloody god!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47528, 0, 0, 0, 0, 0, 0, 0, 0, 'Shadra was... consumed? I have heard of such heresies in Zul\'Drak, but to hear of it in Zuldazar...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47540, 0, 0, 0, 0, 0, 0, 0, 0, 'I feel like myself again, back when I had hundreds of followers pouring their faith into me. My power will fade, but the trolls shall learn to fear Krag\'wa once more!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47564, 1, 0, 0, 0, 0, 0, 0, 0, 'Breakfast is served!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47570, 0, 0, 0, 0, 0, 0, 0, 0, 'According to the invasion plans, the naga are after an ancient relic of extraordinary power known as the Ring of Tides. Apparently, it was lost hundreds of years ago in a battle not far from here...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47571, 0, 0, 0, 0, 0, 0, 0, 0, 'The temple is not far away, but it lies in ruin... abandoned and overgrown.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47573, 0, 0, 0, 0, 0, 0, 0, 0, 'Now that the spider infestation has been removed, we can safely relocate the rest of our tribe to the temple. Thank you, $n.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47574, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for rescuing our trapped tribesman. We\'ll be more careful in dealing with the infestation in the future.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47576, 0, 0, 0, 0, 0, 0, 0, 0, 'You fought bravely against the naga army and defended my temple. From this day forward, you will be an honored guest at my temple.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47577, 0, 0, 0, 0, 0, 0, 0, 0, 'Thanks to your efforts, Darkwood Shoal is much safer now. Still, I fear that the naga will return in force to search for this Ring of Tides.$b$bWe must find a more permanent solution to this problem...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47578, 0, 0, 0, 0, 0, 0, 0, 0, 'So the choice has been made.\n\nIf you ever wish to change your mind, you can commune at any loa\'s shrine to receive their blessing.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47580, 0, 0, 0, 0, 0, 0, 0, 0, 'At long last, my ancient followers have found peace. You have served well, $n.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47581, 0, 0, 0, 0, 0, 0, 0, 0, 'Now that you have the Ring of Tides, you\'ll be able to enter the temple and make contact with the loa.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47583, 0, 0, 0, 0, 0, 0, 0, 0, '<It does not seem as though Tracker Burke was prepared for what befell him. He appears to be covered in small punctures and lacerations.$B$BFortunately for you, he appears to have stashed his wager in his hat.>', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47584, 0, 0, 0, 0, 0, 0, 0, 0, '<The fresh corpse of Erak appears to have been slain from behind by darts and is disturbingly missing some pieces. Conveniently, the prize money of his wager still adorns the body.>', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47585, 1, 0, 0, 0, 0, 0, 0, 0, 'Hmmm. Dese were left behind on purpose. Someone wanted us to find dem.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47586, 0, 0, 0, 0, 0, 0, 0, 0, 'Ye shouldn\'t have come.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47587, 1, 0, 0, 0, 0, 0, 0, 0, 'Ye have my thanks, $c. I\'ll be on the look out for ye in the future.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47596, 4, 273, 0, 0, 0, 0, 0, 0, 'Looks like you\'ve got everything, well... we\'ve got the important stuff at least. Now we\'ve got a big repair job to do.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47597, 603, 21, 0, 0, 0, 0, 0, 0, 'Everyone\'s present and accounted for. Outstanding job!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47598, 4, 1, 0, 0, 0, 0, 0, 0, 'Nice haul! This should keep the cartel off our backs.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47599, 1, 11, 0, 0, 0, 0, 0, 0, 'There you go! Let\'s have a moment of silence for the not so dearly departed.$b$bAaaaaaand... done!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47601, 21, 1, 0, 0, 0, 0, 0, 0, 'Great job! The A.M.O.D. needs a few more tweaks but it\'ll be nothing we can\'t handle.$b$bOh, and hey... good shooting!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47602, 6, 21, 0, 0, 0, 0, 0, 0, 'Naga, you say?$b$bSounds like quite de crisis dere. I am glad you were able to get things back together!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47621, 1, 0, 0, 0, 0, 0, 0, 0, '<Krag\'wa consumes all of the meat in a couple of gulps.> Yes! I have been hungry for so long. As my followers died, my hunger increased.$b$bPerhaps when more come to follow, eating will once again be just an enjoyable pastime.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47622, 1, 0, 0, 0, 0, 0, 0, 0, 'I should have done this long ago as my followers were being murdered, but the blood trolls were always too strong.$b$bWith you lies the last and best chance of our survival.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47623, 0, 0, 0, 0, 0, 0, 0, 0, 'Your ritual was done well.$b$bWith your help, I will not be fed to the blood trolls\' god. It is I who will do the eating!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47631, 5, 1, 273, 0, 0, 0, 0, 0, 'Our mission is a disaster, a complete disaster!$b$bWe were about to drop anchor when naga swarmed the ship! The battle was hard, but we had the upper hand until a sea giant came out of nowhere. It beat the ship harder than a foreman beats a lazy peon.$b$bDon\'t worry, $n. We\'ll get this plan back on track. But first, we need to get the crew together.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47638, 1, 0, 0, 0, 0, 0, 0, 0, 'You are a Goldtusk now, $p. Welcome home.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47659, 0, 0, 0, 0, 0, 0, 0, 0, 'Your deeds do not go unnoticed. Zentimo spoke of you, before his passing.$b$bWhen I did not bend to the blood trolls\' corruption, they attacked. Now they seek to sacrifice me to their god.$b$bI have no more followers to work through, but perhaps you will help more.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47660, 0, 0, 0, 0, 0, 0, 0, 0, 'My totems! So many taken, so many broken. I know you saved what you could. It will not be enough.$b$bToo much of their once great power has been drained.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47696, 0, 0, 0, 0, 0, 0, 0, 0, 'The savages flee at the sight of Krag\'wa! This pleases me! \n\nBut, they will come back in larger numbers soon unless their dark god is stopped. As long as you oppose them, I will grant you my boon.\n\nWhen the day of reckoning comes, you will have my aid.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47697, 1, 0, 0, 0, 0, 0, 0, 0, 'Krag\'wa will make a powerful ally in de coming battle. When de blood trolls are defeated, I have no doubt many Zandalari will come to worship him.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47706, 66, 0, 0, 0, 0, 0, 0, 0, 'Impressive. De King has been dethroned.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47711, 4, 66, 0, 0, 0, 0, 0, 0, 'Good job, that should take some of the heat off us.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47716, 1, 0, 0, 0, 0, 0, 0, 0, 'Serrik is weak from poison. We\'re running out of time.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47733, 1, 14, 0, 0, 0, 0, 0, 0, 'Dat snake of a womon helped Zul find de king! It is good dat she is dead, but I would have liked to kill her myself.\n\n<Zolani spits.>\n\nBwonsamdi can keep her.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47734, 1, 0, 0, 0, 0, 0, 0, 0, 'Good. De way forward is clear.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47735, 1, 0, 0, 0, 0, 0, 0, 0, 'They are satisfactory. I will explain in due time what they are for.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47736, 273, 0, 0, 0, 0, 0, 0, 0, 'When leaving a message, it is always good to use a language dey can easily understand.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47737, 274, 0, 0, 0, 0, 0, 0, 0, '<Rastakhan glowers at the priest\'s corpse before him.>\n\nZul will pay for dis. Those who follow him will suffer, mark my words.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47738, 11, 0, 0, 0, 0, 0, 0, 0, '<Rastakhan smiles grimly at you.>\n\nYou look like you have seen a loa. I know de feeling. Rezan will have words for me when I see him again.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47739, 273, 0, 0, 0, 0, 0, 0, 0, 'De scent of blood is a reliable way to wake a devilsaur. De blood of traitorous wretches, even moreso.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47740, 273, 0, 0, 0, 0, 0, 0, 0, 'My loa is not dead yet. Now ensure he stays dat way.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47741, 0, 0, 0, 0, 0, 0, 0, 0, '<Rezan\'s great sides heave as he recovers from his rampage. He is covered in gore.>\n\nHeretics.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47742, 1, 0, 0, 0, 0, 0, 0, 0, 'Your hands are bloody. Good. Mutiny has no other price.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47868, 5, 4, 0, 0, 0, 0, 0, 0, 'Ah, there you are. We are ready to start.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47870, 1, 0, 0, 0, 0, 0, 0, 0, 'So it\'s done then? I never really liked Hendricks, anyway.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47871, 1, 0, 0, 0, 0, 0, 0, 0, 'This should all help me get to where I need to go.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47873, 0, 0, 0, 0, 0, 0, 0, 0, '<The chest creaks open.>', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47874, 1, 0, 0, 0, 0, 0, 0, 0, 'Talanji was right not to trust de general. If he succeeds in finding de weapon, our great city will fall.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47880, 1, 0, 0, 0, 0, 0, 0, 0, 'Ya be brave ta come here, but ya need ta have more than just bravery if ya be wantin\' Bwonsamdi\'s favor.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47897, 1, 0, 0, 0, 0, 0, 0, 0, 'You killed how many? Well, dat is a start.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47915, 1, 0, 0, 0, 0, 0, 0, 0, 'Free at last! Oh, de pain is coming for dem now.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47918, 25, 0, 0, 0, 0, 0, 0, 0, '$n! Krag\'wa himself sent ya? By da loa, I be blessed dis day ta have him recognize me. I need ya help.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47919, 25, 0, 0, 0, 0, 0, 0, 0, 'Good work. The Natha\'vor trolls should be in disarray now that those... things are out of the picture.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47924, 25, 0, 0, 0, 0, 0, 0, 0, 'Looks like that dunce got himself captured by blood trolls.$B$BThis is going to get violent really fast.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47925, 11, 0, 0, 0, 0, 0, 0, 0, 'Thanks again, I thought I was done for!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47928, 0, 0, 0, 0, 0, 0, 0, 0, 'It has been many years since I\'ve had a visitor to my temple and been found worthy to wear the Ring of Tides.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47939, 1, 0, 0, 0, 0, 0, 0, 0, 'None of these keys work! We\'re going to have to find another way...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47943, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve arrived just in time, our supplies were getting dangerously low. The Tortaka tribe thanks you, traveler.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47945, 4, 0, 0, 0, 0, 0, 0, 0, 'Thank the Light someone came!$B$BI can\'t handle this myself!', 27481);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47959, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, you\'re looking for de warguard? She\'s de only reason I\'m still breathing.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47963, 0, 0, 0, 0, 0, 0, 0, 0, 'Heretics and ingrates! All of them! I will drop every last one of them on a ROCK!\n\nGo, little $r, and make sure the other loa are not falling into the same trap I did.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47965, 0, 0, 0, 0, 0, 0, 0, 0, 'I am afraid that we haven\'t had much success contacting the loa. The entrance to the temple is sealed and the rest of the ruins are overrun with jungle spiders.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (47998, 25, 0, 0, 0, 0, 0, 0, 0, 'I could hear their screams from here.$B$BThese blood trolls are monsters, $rt. Who eats their own people?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48014, 1, 0, 0, 0, 0, 0, 0, 0, 'Maybe, some day, they will learn to think for themselves. Or at least choose a master more wisely.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48015, 1, 0, 0, 0, 0, 0, 0, 0, 'These scrolls must be kept safe.$b$bI dare not think how the naga could have used the secrets within, should they have deciphered them.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48025, 1, 0, 0, 0, 0, 0, 0, 0, 'Naga may be long lived, but their attention spans are not what they once were.$b$bThese antiquities should remain safe and preserved until we return.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48026, 1, 0, 0, 0, 0, 0, 0, 0, 'The naga are trying to hide something. My strength may be waning, but one should never underestimate a loa.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48090, 25, 0, 0, 0, 0, 0, 0, 0, 'Ya did it! Krag\'wa gonna be pleased wit\' dis, I be sure of it.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48092, 25, 0, 0, 0, 0, 0, 0, 0, 'Wit\' Zaldraxia dead, da naga should be leavin\' soon. Ya did what I couldn\'t, and for dat ya have me thanks.$b$bKrag\'wa\'s blessin\' be on ya, I be hopin\' soon he recognizes me devotion ta him.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48313, 1, 0, 0, 0, 0, 0, 0, 0, 'These cactus roots will do nicely.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48314, 1, 0, 0, 0, 0, 0, 0, 0, 'Be gentle with the scorpashi stingers, it will only complicate matters worse if one of us become poisoned. The venom of the scorpashi is highly toxic.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48315, 0, 0, 0, 0, 0, 0, 0, 0, '<The gems slot perfectly into place.>', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48317, 1, 0, 0, 0, 0, 0, 0, 0, 'I seem to have overestimated the ah... accuracy... of my scrying.$b$bAnd thus, our enemies have located the Eye first.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48320, 1, 0, 0, 0, 0, 0, 0, 0, 'Dere are still plenty of things for our guests to kill for recreation. At least now, dey won\'t be hunted on their way in.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48321, 1, 0, 0, 0, 0, 0, 0, 0, 'Oh yeah, I can work with dis.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48322, 1, 0, 0, 0, 0, 0, 0, 0, 'De Goldtusk Inn be open to all exiles. We stick together out here.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48324, 1, 0, 0, 0, 0, 0, 0, 0, '<Meeki has been stabbed multiple times. He is clearly dead.>', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48326, 1, 0, 0, 0, 0, 0, 0, 0, 'Yarr! Vengeance and treasure!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48327, 1, 0, 0, 0, 0, 0, 0, 0, '<Rhan\'ka closely inspects the pineapple.>$B$B This... is... the perfect pineapple!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48330, 1, 0, 0, 0, 0, 0, 0, 0, 'With Zak\'rajan out of the way, we\'ve got first claim on all the treasure he\'s been hoarding here!$B$BYou get your fair share, of course. A worker\'s worth his wage.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48331, 1, 0, 0, 0, 0, 0, 0, 0, 'See? I told you that the soulcatcher totem would make it easy. A tool for every problem, I say.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48332, 1, 0, 0, 0, 0, 0, 0, 0, 'Can\'t say I\'ve seen someone carry that many stomachs at once.$B$BI don\'t know if I\'m impressed or disgusted.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48334, 1, 0, 0, 0, 0, 0, 0, 0, 'The golems are powered by souls? Hmm... $B$BWe\'re dealing with a very powerful enemy. We should take extra precautions.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48335, 1, 0, 0, 0, 0, 0, 0, 0, 'Not every treasure is gold, $p. We vulpera have learned to recognize value wherever it hides in Vol\'dun.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48399, 273, 0, 0, 0, 0, 0, 0, 0, 'Let there be no confusion on where we stand, $n.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48400, 11, 0, 0, 0, 0, 0, 0, 0, 'Ahhh... it must be so disheartening to be at the mercy of such magics. Well done.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48402, 0, 0, 0, 0, 0, 0, 0, 0, 'I see, You\'ve brought meaning to Mag\'ash\'s sacrifice.$b$bThank you for your valor, $n.$b$bPlease, take the weapon with you and go with my blessing.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48404, 0, 0, 0, 0, 0, 0, 0, 0, 'It pains me to pay you for something I could have done myself when I was younger, but these legs do not move as swiftly as they once did.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48405, 1, 0, 0, 0, 0, 0, 0, 0, 'Excellent. One less evil troll on the streets. Of course, you can assume they will organize again soon. They always do.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48452, 0, 0, 0, 0, 0, 0, 0, 0, 'Blood magic is an infection. It spreads like a disease if not purged.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48454, 5, 0, 0, 0, 0, 0, 0, 0, 'Blood magic being practiced in open daylight? Dis cannot stand.$b$bYou have done well in bringing me dis information. I will present it to King Rastakhan at de first opportunity when his council is away.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48468, 25, 0, 0, 0, 0, 0, 0, 0, 'Ya did good, I can tell Bwonsamdi be pleased by ya releasin\' his followers.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48473, 25, 0, 0, 0, 0, 0, 0, 0, 'Good! Did ya feel Bwonsamdi\'s presence, even if it just be for a second?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48478, 25, 0, 0, 0, 0, 0, 0, 0, 'Good! I be savin\' dis for later. I want to be lookin\' Kel\'vax in da eye when we be destroyin\' his phylacteries.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48479, 25, 0, 0, 0, 0, 0, 0, 0, 'Good! I be workin\' on usin\' dese bones to counter Kel\'vax\'s dark voodoo.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48480, 25, 0, 0, 0, 0, 0, 0, 0, 'It be over. As ya can see, Kel\'vax be trapped in his phylacteries.$B$BAll dat\'s left for us ta do is ta destroy da containers ta send him ta Bwonsamdi for judgment.$b$bWe couldn\'t have done this without ya. May Bwonsamdi be wit ya... and may ya be wise enough ta know when ta make a deal with him.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48492, 25, 0, 0, 0, 0, 0, 0, 0, 'That feels a lot better, I think. Time to get down to the business of some proper revenge.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48496, 25, 0, 0, 0, 0, 0, 0, 0, 'Fine work! It\'s good to be reunited, both with my body and with my squad.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48497, 11, 0, 0, 0, 0, 0, 0, 0, 'Good! I could hear their screams from here. They will remember the Horde!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48498, 4, 0, 0, 0, 0, 0, 0, 0, 'Excellent work freeing Ingrid. She has told me about this particular gem, and the company has voted that you should test it out on our snake enemies.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48499, 1, 2, 0, 0, 500, 0, 0, 0, 'Good work. The sethrak will be back, but we\'ve proven our point, which is don\'t cross the Horde.$b$bWith that business over with, my squad will be off. Hopefully the next time we meet will be under... nicer circumstances.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48527, 1, 0, 0, 0, 0, 0, 0, 0, 'You are good at dis. Almost like you have done it before.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48529, 6, 1, 0, 0, 0, 3000, 0, 0, 'A newcomer, eh? You picked a bad time to join our tribe. You\'d find better luck wandering de wastes in search of mirages.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48530, 6, 1, 0, 0, 0, 3000, 0, 0, 'Razgaji sent you? I\'m not sure why he cares so much. When Mojambo and his gang come back, we\'ll all be dead anyway...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48531, 1, 0, 0, 0, 0, 0, 0, 0, 'Just look at all dese juicy intestines! We\'ll have enough pies to feed de entire camp!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48532, 1, 0, 0, 0, 0, 0, 0, 0, 'Looks like you recovered most of de herd. Good work... even though Mojambo and his henchmons will probably murder us in our sleep and take de alpacas for demselves...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48533, 1, 0, 0, 0, 0, 0, 0, 0, 'It may not be chicken, but it\'s food! With enough seasoning nobody will know de difference.$b$bEverything tastes like chicken, right?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48534, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to finally see dat hyena got what he deserved! If you\'re strong enough to slay Snarltooth, maybe we stand a chance against Mojambo\'s gang...$b$bWho am I kidding, we\'re dead meat.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48535, 1, 0, 0, 0, 0, 0, 0, 0, 'Good, you made it. We had to move fast to avoid getting caught up in any politics.$b$bLet us proceed.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48549, 6, 1, 0, 0, 0, 3000, 0, 0, 'You saw Jakra\'zet? What would he be doing trying to break into de old pyramid?\n\nWhatever de reason, it can\'t be good.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48550, 1, 0, 0, 0, 0, 0, 0, 0, '<Jorak quickly digs through the satchels.>$B$BMuch is missing, but hopefully dere\'s enough here for a worthy offering.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48551, 1, 0, 0, 0, 0, 0, 0, 0, 'Look how happy those plants are! You made that happen.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48553, 1, 0, 0, 0, 0, 0, 0, 0, 'Hrm, this fountain in front of me still isn\'t functioning? But my work was impeccable!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48554, 1, 0, 0, 0, 0, 0, 0, 0, 'Well then, snakes! That explains it. I\'m glad you cleared up that clog. The gulch looks greener already!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48555, 1, 0, 0, 0, 0, 0, 0, 0, 'Look how much you\'ve helped bring this place to life! You\'ve got quite the green thumb.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48573, 11, 0, 0, 0, 0, 0, 0, 0, 'Ah, dat looks good! Dis will make de ritual go by real nice.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48574, 4, 0, 0, 0, 0, 0, 0, 0, 'Lemme count dem... good! Dis will work.$b$bShh, I said it will work, not dat we\'d be done. Stop annoying me, Sur\'jan!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48576, 53, 0, 0, 0, 0, 0, 0, 0, 'Good! I am feeling stronger already. De three of us make a good team.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48577, 11, 0, 0, 0, 0, 0, 0, 0, 'I feel better already! Maybe I should destroy more of those eggs in de future.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48578, 1, 0, 0, 0, 0, 0, 0, 0, 'You got de eye!$b$bI feel de power coming from dis thing. Can you see it?$B$BShut up, Sur\'jan. I know YOU can see it, I was talking to $ghim:her;!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48581, 1, 0, 0, 0, 0, 0, 0, 0, 'You\'re certainly de talk of de kraal, $n!$b$bDat Pinky will be great in battle when he grows up, don\'t you think?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48584, 11, 0, 0, 0, 0, 0, 0, 0, 'Dis will work. We are so close to getting my power back!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48585, 1, 0, 0, 0, 0, 0, 0, 0, 'I see you made a scavenging run and came back alive!$b$bWe will put these supplies to good use among the tribe. You have my thanks.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48590, 661, 0, 0, 0, 0, 0, 0, 0, 'My gear! Ah, dey smell brand new, too. Did you clean dem?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48591, 4, 0, 0, 0, 0, 0, 0, 0, 'We did it! Urok is gone for good. It was a shame to put him down, but dat sort of creature isn\'t supposed to be.\n\nI have lots of other things to do, like finding de two other trolls out here and killing dem.\n\nWhat do you think, Sur\'jan? Should I tell $ghim:her;? No? Okay, fine.\n\nI\'ll be seeing you. Oh, and don\'t stay in Nazmir. Only crazy people come here.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48655, 1, 0, 0, 0, 0, 0, 0, 0, 'You can\'t fool me, saurolisk! I know you\'re out dere waiting to eat me! Oh wait... you\'re a grown up! Please, get me out of here! Help!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48656, 6, 0, 0, 0, 0, 0, 0, 0, 'De saurolisks are all gone and it\'s safe to come out? Thank you so much! I never thought I\'d make it out of here alive!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48657, 1, 0, 0, 0, 0, 0, 0, 0, 'You have de eggs! Be careful not to break dem... just roll dem under da box... gently... gently now...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48669, 11, 0, 0, 0, 0, 0, 0, 0, 'Now you see? We must beat Urok. I know how to do it, but I can\'t do it alone.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48684, 1, 0, 0, 0, 0, 0, 0, 0, 'Dis temple is not quite what I expected it to be.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48699, 603, 0, 0, 0, 0, 0, 0, 0, 'Good, we will be safe in here. I am happy dat you are here, because I am not able to stop dis alone.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48715, 1, 0, 0, 0, 0, 0, 0, 0, 'Dis is Rakera. I have no doubt. But for some reason, she does not remember me... or herself, it would seem...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48790, 1, 0, 0, 0, 0, 0, 0, 0, 'You\'ve located de supplies and dey appear mostly intact! You have done a great service for our tribe, $n.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48800, 1, 273, 0, 0, 0, 0, 0, 0, 'You did it! Just need to grind dem up like dis... and done. Either dis poison will work... or we will both be dead.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48801, 5, 273, 0, 0, 0, 0, 0, 0, 'Hah! Now we have plenty of room to mess with dis camp. Dere will be no reinforcements from de other blood trolls.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48823, 1, 0, 0, 0, 0, 0, 0, 0, 'I saw dem fires burnin\' from here. Zardrax gonna be hurtin\' from dis one.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48825, 273, 0, 0, 0, 0, 0, 0, 0, 'Good job on gettin\' dis fetish. Dere be a lot dat I can be doin\' with dis ting.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48840, 661, 0, 0, 0, 0, 0, 0, 0, '<Kenzou looks skeptically at the leaf you handed him.>$B$BNo, I will not be going there. But are you looking for some real work?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48846, 6, 1, 0, 0, 0, 3000, 0, 0, 'Mojambo is teaming up with an outside gang to attack us? I knew he meant business, but I didn\'t expect him to go dis far. We need to come up with a plan as soon as possible...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48847, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'s a good thing you armed de tribe! Mojambo and his gang are attacking as we speak!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48850, 1, 0, 0, 0, 0, 0, 0, 0, 'With Tongo out of de way, all we have to worry about now is Mojambo...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48854, 1, 0, 0, 0, 0, 0, 0, 0, 'Zardrax be temptin\' us ta join him, but dat ain\'t gonna work. We gonna stop dis lich and anyone else who wants ta help da blood trolls!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48855, 0, 0, 0, 0, 0, 0, 0, 0, 'Good work. We be savin\' lots a people in Zuldazar from bein\' crushed by dem zombies.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48856, 0, 0, 0, 0, 0, 0, 0, 0, 'I can be tellin\' from here dat Zardrax\'s power be gettin\' weaker. We close ta endin\' dis.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48857, 0, 0, 0, 0, 0, 0, 0, 0, 'We got no time ta be mournin\' Kal\'dran. He made his choice, so now we be makin\' ours ta defeat Zardrax.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48869, 0, 0, 0, 0, 0, 0, 0, 0, 'Ya did it! Zardrax be gone, and now da blood trolls be losin\' a major source of their power.$B$BI be makin\' my way back ta da camp ta let Princess Talanji know \'bout Kal\'dran.$B$BShe deserves ta be knowin\' dat he lost hope in our cause... might be good ta be seein\' if anyone else is havin\' doubts about what we be doin\'.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48871, 1, 0, 0, 0, 0, 0, 0, 0, 'Perfect. The power of nostalgia is fascinating. These relics will hold great sentimental value. The monetary value won\'t be bad, either.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48872, 5, 0, 0, 0, 0, 0, 0, 0, 'Well done! They must be so irritated. This is great fun!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48883, 0, 0, 0, 0, 0, 0, 0, 0, 'Is he dead?\n\nYes... now I can catch... and gut... and catch...\n\nMore entrails... only the best for the sisters...', 27481);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48887, 1, 0, 0, 0, 0, 0, 0, 0, 'I can tell you found success. You are at peace.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48888, 1, 0, 0, 0, 0, 0, 0, 0, 'De spring is a gift from de loa and must be preserved at all costs.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48889, 1, 0, 0, 0, 0, 0, 0, 0, 'I seek your help, $n.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48890, 1, 273, 0, 0, 0, 0, 0, 0, 'Good work, nobody suspects a thing. Did any of dem crack under de pressure?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48894, 1, 0, 0, 0, 0, 0, 0, 0, 'You have proven worthy of entering our temple, Akunda.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48895, 1, 0, 0, 0, 0, 0, 0, 0, 'Dis is a generous offering, but a gift alone isn\'t enough to enter our temple. \n\nRedemption can\'t be bought, it must be earned. \n\nLucky for you, dere is much work to be done.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48896, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'s clear dat Jakra\'zet won\'t stop until I\'m dead.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48948, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'m not lost! My grandson and his friends ran off and I\'ve been looking for them. I wanted to find them before the troggs did!', 27481);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48987, 1, 0, 0, 0, 0, 0, 0, 0, 'Who are you? Have we met before?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48988, 1, 0, 0, 0, 0, 0, 0, 0, 'It is as I feared. De loa\'s own memories have been taken from him.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48991, 1, 0, 0, 0, 0, 0, 0, 0, 'Akunda smiles upon you. He knows what you have done here today.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48992, 1, 0, 0, 0, 0, 0, 0, 0, 'Dese bones still resonate with power.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48993, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes... dis will work well.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (48996, 1, 0, 0, 0, 0, 0, 0, 0, 'You wield my gift well, $n. \n\nNow that my power has returned, I can heal the minds of my children.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49001, 1, 0, 0, 0, 0, 0, 0, 0, 'Well, you\'re good at killing. I could tell that even from back here. Welcome to the crew.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49002, 0, 0, 0, 0, 0, 0, 0, 0, '<Rakjan nods in approval. That\'s one less Fangcaller to worry about.>', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49003, 1, 0, 0, 0, 0, 0, 0, 0, 'Emperor Korthek is certainly on his way to us by now. We must prepare quickly.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49005, 1, 0, 0, 0, 0, 0, 0, 0, 'You did what was right. Memories like those have no place in dis world.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49040, 18, 1, 0, 0, 0, 2000, 0, 0, '<B\'wizati reads the note and wipes a tear from her eye.>\n\nHe may choose to forget, but I choose to remember, no matter what pain it brings me. \n\nAt least he is safe. Loa watch over him.', 27404);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49064, 603, 0, 0, 0, 0, 0, 0, 0, 'Dis is de work of de blood trolls, I am certain of it. We will get to de bottom of dis.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49067, 11, 0, 0, 0, 0, 0, 0, 0, 'For better or worse, we have Bwonsamdi\'s aid. Let us get to de killing.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49070, 11, 0, 0, 0, 0, 0, 0, 0, 'Good! I be collectin\' all their souls now.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49071, 661, 0, 0, 0, 0, 0, 0, 0, 'I can rest a little easier knowing there are fewer of those... things in the world.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49078, 1, 273, 0, 0, 0, 0, 0, 0, 'I knew my poison would work! I just need to put a bit more of my voodoo in dere for it to be ready for Hir\'eek.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49079, 0, 0, 0, 0, 0, 0, 0, 0, '<Cough> Well... at least my spine wasn\'t snapped.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49080, 661, 0, 0, 0, 0, 0, 0, 0, 'We are too late? Den we can only hope Torga can help us destroy... whatever it is dat you saw.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49081, 273, 18, 0, 0, 0, 0, 0, 0, 'I know. It is done. Hir\'eek is dead. Nazmir is a cursed place, $n. We never were meant to kill a loa... but here we are.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49082, 603, 1, 2, 0, 0, 0, 0, 0, 'Sorry. You did what you had to do... I can\'t believe dat dis G\'huun could  corrupt a loa, of all things.$b$bNazmir is a cursed place... for everybody.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49120, 661, 0, 0, 0, 0, 0, 0, 0, 'We may not have Torga\'s aid, but we can stop whatever de blood trolls are planning.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49122, 1, 0, 0, 0, 0, 0, 0, 0, 'Things are worse than we thought.$B$BMuch worse.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49125, 273, 0, 0, 0, 0, 0, 0, 0, 'Good! Looks like that herald was weakened by what you did, and you got the scroll! Be careful, it\'s ancient! \n\nHolbruk always talked about how powerful that scroll was. We\'re going to see if he was right.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49126, 273, 0, 0, 0, 0, 0, 0, 0, 'I saw the destruction from here! Hrm, maybe I should try to fix that scroll up, you know, for future use.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49130, 273, 0, 0, 0, 0, 0, 0, 0, 'This is no way for Torga\'s remains to be treated, $n. I\'ll make sure this is properly destroyed.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49131, 273, 0, 0, 0, 0, 0, 0, 0, 'De blood magic is dissipating from dis place. It is time for us to strike, before de blood trolls gain any more advantages.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49132, 273, 0, 0, 0, 0, 0, 0, 0, 'Princess Talanji was saying something about feeling the blood magic dissipating from inside of Torga. I sure hope she\'s right.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49136, 4, 0, 0, 0, 0, 0, 0, 0, 'It is done, $n. De blood trolls no longer have de blood of Torga as a resource to fuel their army.\n\nI know we were hoping to have Torga\'s aid, but at least we stopped de blood trolls.\n\nWe must find Rokhan and de others, along with Krag\'wa, if he is still alive.\n\nEh? It looks like Lashk wants something...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49138, 0, 0, 0, 0, 0, 0, 0, 0, '<The chest is filled with shiny treasure.>', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49139, 1, 0, 0, 0, 0, 0, 0, 0, 'You and I make a good team, $p.$B$BHave a little extra reward, on me.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49141, 1, 0, 0, 0, 0, 0, 0, 0, '<Vorrik smiles as he takes the staff back from you.>\n\nThank you, $p. I must confess I was anxious for your return. It\'s been many years since this staff has left my side.\n\nMy trust in you was well placed, however. The staff is practically rippling with power.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49144, 113, 0, 0, 0, 0, 0, 0, 0, 'You fight well, but our work is far from over.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49145, 396, 0, 0, 0, 0, 0, 0, 0, 'Our warriors won\'t soon forget you risking your own life to save theirs. Thank you.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49146, 396, 11, 0, 0, 0, 300, 0, 0, 'You have done well. My friends on de Other Side are pleased.$B$BNow for de fun part.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49147, 669, 0, 0, 0, 0, 0, 0, 0, 'Dey deserve worse fates, I assure you.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49148, 0, 0, 0, 0, 0, 0, 0, 0, 'Dey will reinforce their lines if we do not act soon. Dis may be our only opportunity.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49149, 153, 0, 0, 0, 0, 0, 0, 0, 'Can you feel it? De spirits are flowing through you, and dey are pleased.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49160, 1, 0, 0, 0, 0, 0, 0, 0, 'I guess we\'re in this for the long haul. Lashk and I have a lot to take care of if we\'re going to help.$B$BI even know where you could go to find that troll friend of yours... and Krag\'wa.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49185, 603, 0, 0, 0, 0, 0, 0, 0, 'Our luck may be looking up. Perhaps we can get Torga\'s aid.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49227, 1, 0, 0, 0, 0, 0, 0, 0, 'Sure am glad to see you again, $n. A reliable rescuer has been hard to come by.$B$BI\'ll pay you as promised, I but won\'t be sticking around for long!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49261, 1, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done good, $n. We\'ll be eatin\' like kings tonight!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49262, 1, 0, 0, 0, 0, 0, 0, 0, 'Glad I can count on you to keep things from gettin\' out of hand.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49278, 1, 0, 0, 0, 0, 0, 0, 0, 'De spirits watch over Zo\'bal again!$b$bNow, about our deal...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49284, 1, 0, 0, 0, 0, 0, 0, 0, 'Well met, neighbor! Batu must have faced such a conundrum when we did not return.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49285, 1, 0, 0, 0, 0, 0, 0, 0, 'Koba\'s favorite things! I do hope he stayed put. He is filled with such haste.$B$BThank you, $r, my boy and I will be reunited soon.$B$BAlso, Spitzy asks that you take this little sluglet with you on all your adventures.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49286, 1, 0, 0, 0, 0, 0, 0, 0, 'We best get moving. I do not believe we can wait for the naga to move on, this time.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49287, 1, 0, 0, 0, 0, 0, 0, 0, 'As long as my friends are safe, I can continue without worry. Many thanks, $c.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49288, 1, 0, 0, 0, 0, 0, 0, 0, 'Looks like everything is here, and with little damage done! Very impressive!$b$bWhat do we have in here? Let\'s see...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49289, 1, 0, 0, 0, 0, 0, 0, 0, 'Well fought, adventurer. Well fought, indeed! $b$bWhatever this stone is those naga were after, they did not get it this day!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49309, 6, 5, 0, 0, 0, 0, 0, 0, 'De king is in danger!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49310, 1, 0, 0, 0, 0, 0, 0, 0, 'We are betrayed.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49314, 0, 0, 0, 0, 0, 0, 0, 0, 'About time... someone... showed up...! Ya gotta get... this lich... away from us!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49327, 1, 0, 0, 0, 0, 0, 0, 0, 'The faithless will never stop their siege, but your efforts have bought us time.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49333, 1, 0, 0, 0, 0, 0, 0, 0, 'You\'ve brought enough to put a weapon in the hands of everyone willing and able to fight.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49334, 1, 0, 0, 0, 0, 0, 0, 0, 'I owe you my life, $n.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49335, 1, 0, 0, 0, 0, 0, 0, 0, 'Without their skycallers, the faithless\' incursion will falter.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49340, 1, 0, 0, 0, 0, 0, 0, 0, 'Thanks to you, we now have two of the keys. Korthek still holds the third.$B$BIf we can get our hands on that key, then Mythrax will remain sealed in his tomb forever.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49348, 21, 1, 0, 0, 0, 0, 0, 0, 'Ya done good, mon. Da Necropolis already startin\' ta feel more like home again.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49366, 1, 0, 0, 0, 0, 0, 0, 0, 'Thanks, stranger. Dat was too close.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49370, 5, 0, 0, 0, 0, 0, 0, 0, 'Well, dat was terrifying. Just a few more pieces, and we can be forever away from dis place.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49377, 5, 0, 0, 0, 500, 0, 0, 0, '<Satao spits on the ground.>$b$bDe world is better without dat monster in it.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49378, 5, 0, 0, 0, 0, 0, 0, 0, 'He is sated now! Well done!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49379, 1, 0, 0, 0, 0, 0, 0, 0, 'Their suffering is over now.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49380, 274, 1, 0, 0, 0, 0, 0, 0, 'Ugh! Even I feel de corruption. I am sure Jabari will be able to find de secrets locked away in here. $B$BThank you, $n.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49382, 1, 0, 0, 0, 0, 0, 0, 0, 'This young one is no longer one of them, but he can never be one of us. Take care of him!$B$BThank you, $n.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49421, 0, 0, 0, 0, 0, 0, 0, 0, 'When hunting one who has foreseen de future, it is best to proceed with a measure of caution.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49422, 1, 11, 0, 0, 0, 0, 0, 0, 'With de sky clear of ballistae fire, Pa\'ku can start doing what she loves: dropping those who cross her from a really great height.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49424, 6, 0, 0, 0, 0, 0, 0, 0, 'Zul has fallen so low as to wish to raise Dazar himself? Can Zul even be called a Zandalari anymore?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49425, 53, 0, 0, 0, 0, 0, 0, 0, 'Zul thinks to bar me from MY domain? I will consume his soul.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49426, 661, 0, 0, 0, 0, 0, 0, 0, 'He... he\'s gone...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49432, 1, 1, 0, 0, 0, 0, 0, 0, 'Keula\'s ma\'da be foolish ta tink she never gonna pass on to da Other Side, but now dey be together an\' I be havin\' two more souls in me realm.$b$bYou done good, mon.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49437, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'ve heard the other vulpera whisper Shath\'kar\'s name in fear. Thank you for ridding us of him.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49440, 603, 0, 0, 0, 0, 0, 0, 0, 'De ritual is done, and you are ready.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49479, 1, 2, 0, 0, 500, 0, 0, 0, 'That gem appears ready to do some damage. Let\'s just... point it elsewhere, for now.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49488, 273, 0, 0, 0, 0, 0, 0, 0, 'Ah, yes, my new escort!$b$bYou are here to enforce de king\'s will. I would hate for ol\' Rastakhan to become disappointed with you, especially after your performance in Stormwind.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49489, 1, 0, 0, 0, 0, 0, 0, 0, 'Fight fire with fire, dey say. Or in dis case, poison with idiots who chew de ankles of their betters.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49490, 274, 0, 0, 0, 0, 0, 0, 0, 'All according to plan. Do not worry for dem... dis was always their fate.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49491, 0, 0, 0, 0, 0, 0, 0, 0, '<As the fire chews away at the totems, Gurubashi spirits rise from the ashes.>', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49492, 273, 0, 0, 0, 0, 0, 0, 0, 'As I said, his truth is distorted by fear.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49493, 0, 0, 0, 0, 0, 0, 0, 0, 'Weakness or pride... I think often on de Zandalari\'s priorities.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49494, 0, 0, 0, 0, 0, 0, 0, 0, 'Good. Dis thread has been severed.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49495, 273, 0, 0, 0, 0, 0, 0, 0, '<Zul rubs his hands together.>\n\nGood. Now we are ready to draw him out.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49569, 4, 0, 0, 0, 0, 0, 0, 0, 'We made it, $n. Now comes de hard part. Are you ready?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49615, 0, 0, 0, 0, 0, 0, 0, 0, 'Very prompt. Now, I have need of your hands.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49662, 396, 0, 0, 0, 0, 0, 0, 0, 'We are surrounded by Faithless out here, $p. We must move quickly.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49663, 0, 0, 0, 0, 0, 0, 0, 0, 'Perhaps Zul will remember his place going forward.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49664, 1, 0, 0, 0, 0, 0, 0, 0, '$p?! We gotta stop meeting in places like this.$B$BActually, let\'s ALWAYS meet in places like this!$B$BYou up for killing some Faithless?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49665, 396, 0, 0, 0, 0, 0, 0, 0, 'Look at my people fighting! The Faithless don\'t stand a chance!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49666, 396, 0, 0, 0, 0, 0, 0, 0, 'And that\'s just the start! Now the Faithless know who\'s coming for \'em.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49667, 1, 0, 0, 0, 0, 0, 0, 0, 'Thank you, thank you, thank you!\n\nThese children have already experienced more hate and pain than anyone ever should. I\'ll make sure they feel love in equal measure.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49668, 1, 0, 0, 0, 0, 0, 0, 0, 'The fires are spreading quickly. Let\'s hurry, while the Faithless are distracted.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49669, 1, 0, 0, 0, 0, 0, 0, 0, 'Crawgs are violent, filthy things... Yet another reason to avoid Nazmir.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49676, 1, 0, 0, 0, 0, 0, 0, 0, 'Now we are ready for a real challenge!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49677, 1, 0, 0, 0, 0, 0, 0, 0, 'These plans will be our key to taking the fight to the sethrak.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49678, 273, 0, 0, 0, 0, 0, 0, 0, 'Not so much static in de air now. That\'s good. Means we did something right.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49679, 273, 0, 0, 0, 0, 0, 0, 0, 'Won\'t bring back de ones we lost, but it show them that not even little Dol\'watha be going down without a fight.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49680, 1, 0, 0, 0, 0, 0, 0, 0, 'Good, very good...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49681, 6, 11, 0, 0, 0, 0, 0, 0, 'Why is she covered in blood?$b$bOn secon\' thought, nevermind.$b$bPraise all deh loa, at least dis little one made it!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49754, 1, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n. I wish that put a bigger dent in the threat they pose.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49758, 5, 0, 0, 0, 0, 0, 0, 0, 'Ya got here just in time!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49768, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to tha hunt.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49773, 6, 0, 0, 0, 0, 0, 0, 0, 'Are you the new one? I\'m supposed to welcome you. Here, take this letter.\n\n<Blackguard lets out an exasperated sigh.>', 26972);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49774, 603, 0, 0, 0, 0, 0, 0, 0, 'You have de sweetleaf! Looks like you gathered just enough, too!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49775, 1, 0, 0, 0, 0, 0, 0, 0, 'Garza was lost long ago. It sounds that Maset is as feisty as ever. We will get out to de ships!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49776, 4, 0, 0, 0, 0, 0, 0, 0, 'Good job! Dis is exactly de kind of stuff we need!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49777, 0, 0, 0, 0, 0, 0, 0, 0, 'You have my thanks... if I die here, at least I can face my ancestors with my oath fulfilled.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49778, 274, 0, 0, 0, 0, 0, 0, 0, 'Zaluto is free from death\'s door, but he\'s still weak. We have more to do.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49779, 273, 0, 0, 0, 0, 0, 0, 0, 'You have done well taking care of de bone raptors. Even if dis road is only for criminals, dey at least deserve to make it to Vol\'dun alive.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49780, 4, 0, 0, 0, 0, 0, 0, 0, 'De fetish is brimming with de fire magic! Good, because you will need it for what comes next.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49781, 4, 273, 0, 0, 0, 0, 0, 0, 'I knew you\'d take care of Zulajin, no problem! I guess it is time for me to say goodbye to de jungle once and for all...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49785, 1, 0, 0, 0, 0, 0, 0, 0, 'Dat was unexpected.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49801, 1, 0, 0, 0, 0, 0, 0, 0, 'Just what I needed! Boy do they smell! $b$bWith these bile sacs, I can make enough Aggression Formula for a creature as big as a brutosaur.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49810, 1, 0, 0, 0, 0, 0, 0, 0, 'We need to help Ben\'jin to get his mojo back and Maka\'fon to be in de mood. Is voodoo or science de better way to go?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49814, 1, 0, 0, 0, 0, 0, 0, 0, 'Well done! I will weave dese rushes into incense bundles and cast my best spells upon dem.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49870, 603, 1, 0, 0, 0, 3000, 0, 0, 'Dat was incredible! I could hear de snorting and stomping from here. $b$bWell done, $n.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49871, 1, 0, 0, 0, 0, 0, 0, 0, 'Not bad, $n! Our work is nearly complete!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49884, 4, 1, 0, 0, 0, 200, 0, 0, 'You have saved de fleet and protected de Bay of Kings!$B$BYa did well, $n! We captains are a wise lot.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49902, 1, 0, 0, 0, 0, 0, 0, 0, 'Good ta see ya, $n. Princess Talanji be tellin\' me all about Torga and da tortollans. Guess we lucky dat we got sometin\' outta dis.$B$BWe be here, waitin\' on reinforcements from da Horde.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49905, 273, 0, 0, 0, 0, 0, 0, 0, 'Death is a tool, $n. Do not forget dat.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49917, 0, 0, 0, 0, 0, 0, 0, 0, 'You came just in time pal. There\'s loads of work to do if there\'s really kaja\'mite here in Zuldazar!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49919, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you chump! I mean chum.\n\nThere\'s enough kaja\'mite there to get us started. Too bad about all the talking gorillas in the way.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49920, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you my friend! I saw the bravery with which you granted freedom to my comrades. We shall speak more anon.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49922, 0, 0, 0, 0, 0, 0, 0, 0, 'Good. One less mook to deal with. That kaja\'mite is as good as ours!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49932, 1, 0, 0, 0, 0, 0, 0, 0, 'Your aid is required. We must begin.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49935, 1, 0, 0, 0, 0, 0, 0, 0, 'Cores recovered. Data validation and merge will begin shortly.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49937, 1, 0, 0, 0, 0, 0, 0, 0, 'Sufficient titan plating. Will begin forging process once functionality is restored.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49938, 1, 0, 0, 0, 0, 0, 0, 0, 'Vegetation corruption reduced to an acceptable level.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49940, 5, 0, 0, 0, 0, 0, 0, 0, 'Rastakhan finally sent someone!$b$bI hope you not be afraid of snakes, mon.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49941, 1, 0, 0, 0, 0, 0, 0, 0, 'Destruction of bones will begin.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49949, 1, 0, 0, 0, 0, 0, 0, 0, 'Undead levels lowered to acceptable amount. Still require method to bypass targets in order to access containment.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49950, 1, 0, 0, 0, 0, 0, 0, 0, 'Scanners did not pick up evidence of corrupted energy. Must continue repairs, cannot make same mistake again.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49955, 1, 0, 0, 0, 0, 0, 0, 0, 'Old God minions temporarily reduced. Will not last long without proper containment.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49956, 1, 0, 0, 0, 0, 0, 0, 0, 'Void portals no longer detected. Must continue.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49957, 25, 0, 0, 0, 0, 0, 0, 0, 'Containment protocol obtained. System reboot initiated.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49965, 0, 0, 0, 0, 0, 0, 0, 0, 'Blood calls out for blood.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49969, 0, 0, 0, 0, 0, 0, 0, 0, 'Now this! This is an offering worthy of my attention!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49977, 1, 0, 0, 0, 0, 0, 0, 0, 'About time you arrived, $n. The very future of the Horde may depend on what I am about to tell you...', 27101);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49980, 25, 0, 0, 0, 0, 0, 0, 0, 'New protocol initiated. Defeat of blood trolls critical priority.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (49985, 273, 0, 0, 0, 0, 0, 0, 0, 'We have everything we need to go after de blood trolls. We need to get moving.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50043, 1, 0, 0, 0, 0, 0, 0, 0, 'Hm. These are a little... broken, don\'t you think?$b$bAh, well, nothing a little magic cannot fix.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50074, 603, 1, 0, 0, 0, 3000, 0, 0, 'I spent several moments working out that formula! It should have worked! The reason these brutosaurs are endangered is clearly their unreasonable resistance to science!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50076, 1, 603, 0, 0, 0, 0, 0, 0, 'We are ready. Now, it is time for us to bring our fight to de blood trolls!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50078, 1, 273, 0, 0, 0, 0, 0, 0, 'Good work, dat should make things easier on us.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50079, 1, 273, 0, 0, 0, 0, 0, 0, 'Good job! Now we can move deeper into dis den of evil.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50081, 273, 378, 0, 0, 0, 0, 0, 0, 'Our soldiers will return home stronger, wiser, and most importantly, alive.\n \nOn behalf of my people, I thank you.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50082, 1, 273, 0, 0, 0, 0, 0, 0, 'She is... so much stronger than I expected. But, we have some surprises of our own! \n\nLet us bring up dis gun of yours and see how well it performs.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50083, 1, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n! \n\nDa blood trolls gonna be hurtin\' wit\' da death of their general. Now, let\'s keep da pressure on dem!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50085, 1, 0, 0, 0, 0, 0, 0, 0, 'Good job, mon. Justice be served.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50087, 1, 0, 0, 0, 0, 0, 0, 0, 'Finally, our battle here is done!\n\nMore importantly, you have made good friends with de tortollans and have gained powerful allies in de loa. I feel dat we will need to call upon their aid in de future.\n\nIt is time for me to return to Zuldazar and let my father know of our victory. \n\nDo you hear dat? Rokhan appears to have a message for us.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50138, 1, 603, 0, 0, 0, 0, 0, 0, 'You fought well! \n\nDe blood trolls are on de run. We must keep de momentum!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50150, 603, 1, 0, 0, 0, 3000, 0, 0, 'I am an expert on love and all of my previous mates can attest to dat. I have misread de reason for Maka\'fon\'s mood. $b$bDe razorwings ate her last clutch of eggs and she will make no more until she feels safe and avenged.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50154, 1, 0, 0, 0, 0, 0, 0, 0, 'De pods may be unpleasant to us, but de brutosaurs like dem just fine.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50235, 274, 0, 0, 0, 0, 0, 0, 0, 'I will try to help dem secure their village.\n\nStay sharp, $n. Zul will not stop while ol\' Rastakhan\'s fate hangs in de balance.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50252, 603, 1, 0, 0, 0, 3000, 0, 0, 'Thank you, $n, for your help. Dis is de best of both worlds and de brutosaurs may benefit from it.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50268, 603, 1, 0, 0, 0, 3000, 0, 0, 'The consistency is the same... does it seem a little carbonated to you now? $b$bCan\'t wait to see if this works better than the original formula which, I feel like I need to say, SHOULD have worked fine.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50297, 2, 1, 0, 0, 0, 3000, 0, 0, 'Dis is a wonderful moment and I feel hopeful dat dis species of brutosaur will survive and thrive here in de jungle.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50319, 1, 0, 0, 0, 0, 0, 0, 0, 'So many tauren! I think I\'ve seen more tauren since I got here than in my entire life in Highmountain.\n\nThey all ask to touch my antlers. It\'s weird at first, but now I kind of like it.', 26972);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50328, 1, 0, 0, 0, 0, 0, 0, 0, 'Thank de loa dat de Sandfury didn\'t kill you and take dese.\n\nDe drinks are almost ready and dey really add the perfect flavor.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50331, 1, 0, 0, 0, 0, 0, 0, 0, 'I fear those orders were found too late to help those that served here... about five years too late.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50371, 0, 0, 0, 0, 0, 0, 0, 0, 'At last you have arrived, $n. The very future of the Alliance may depend on what I am about to tell you...', 27101);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50381, 0, 0, 0, 0, 0, 0, 0, 0, 'Haha! Well done, richmon. You bruised Tik\'su\'s pride and his backside. He gonna be hurtin\' for a while. You have earned my favor.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50466, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for helping him on his way.$b$bDat Wildtusk, he has been watching dese hills for years long past. Some beast will have big shoes to fill!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50535, 432, 1, 274, 0, 0, 0, 0, 0, 'Hmmm. I am tempted to sell this stone, it must be quite valuable. $b$bHowever, with what you have shown us today, I think there may be more value in having it with us.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50536, 1, 0, 0, 0, 0, 0, 0, 0, 'Oh, thank you, danger noodle. This will give us just the angle we need to get the most out of what we find here. Your arrival was most fortuitous!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50538, 0, 0, 0, 0, 0, 0, 0, 0, '<Handler Bazkoji rests with his sword still held in hand. He died fighting.>', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50539, 1, 0, 0, 0, 0, 0, 0, 0, '<The ancient stones hum with magic.>', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50550, 1, 0, 0, 0, 0, 0, 0, 0, 'Do not waste time mourning my old friend now. His foolish crusade has left us a larger problem that demands our full, immediate attention.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50561, 1, 0, 0, 0, 0, 0, 0, 0, 'This stone... I\'ve seen one like it before.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50599, 0, 0, 0, 0, 0, 0, 0, 0, 'Good work!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50600, 0, 0, 0, 0, 0, 0, 0, 0, 'The Order of Embers thanks you.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50601, 0, 0, 0, 0, 0, 0, 0, 0, 'The tides predicted your triumphs. Thanks.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50605, 0, 0, 0, 0, 0, 0, 0, 0, 'For the Alliance!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50617, 1, 0, 0, 0, 0, 0, 0, 0, 'We must hurry. Your friends are already inside!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50641, 1, 0, 0, 0, 0, 0, 0, 0, 'Our temple will not fall at the hands of the faithless!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50656, 1, 0, 0, 0, 0, 0, 0, 0, 'We are in your debt, $n.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50700, 0, 0, 0, 0, 0, 0, 0, 0, 'We might have a wee problem on our hands.', 27481);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50702, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'s a miracle we survived...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50703, 1, 0, 0, 0, 0, 0, 0, 0, 'I shall inform the Dark Lady of your activities in Vol\'dun at once. She will know how to best proceed against this  new threat.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50739, 5, 0, 0, 0, 0, 0, 0, 0, 'Oh thank goodness! We\'ll have to keep these little guys away from anything with a big mean stinger out here. Man, I\'m going to be busy. $b$bIf you could protect Coda\'s little brother, that would help us out a lot!$b$bThis is Poda!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50745, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'s unconventional, but I believe Sethraliss would be pleased to see her shrine used to protect innocents in need.$B$BAnd if these vulpera are fighting the Faithless, they will be useful allies.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50746, 1, 0, 0, 0, 0, 0, 0, 0, 'I can hear the vulpera battle cries from here. It seems your friends have given us a window of opportunity. We musn\'t waste it.\n\nI have a plan.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50748, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent! Rakjan appears willing to aid us, so we will certainly make good use of these.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50749, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'ll have some of the vulpera stable that krolusk outside, in case we need to use it again.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50750, 1, 0, 0, 0, 0, 0, 0, 0, 'Excellent. Now we may focus on Emperor Korthek without distractions.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50751, 1, 0, 0, 0, 0, 0, 0, 0, 'We\'re too late. The keys are gone. \n\nSethraliss... What have I done?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50752, 1, 0, 0, 0, 0, 0, 0, 0, 'Incredible! I remember when some of these were first found...\n\nYou\'ve done all followers of Sethraliss a kindness this day, $p.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50755, 6, 1, 0, 0, 0, 3000, 0, 0, 'I don\'t think I\'ve ever seen these buzzards so satiated. How much did you feed them exactly? \n\nDoesn\'t matter. You saved my tail, for sure.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50757, 1, 0, 0, 0, 0, 0, 0, 0, 'The Faithless will be nursing that wound for awhile. Well done.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50769, 0, 0, 0, 0, 0, 0, 0, 0, 'Well, here we are. Zuldazar. We had better stick with Talanji and Zul for now. The Warchief will be interested to know what happens next.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50770, 1, 0, 0, 0, 0, 0, 0, 0, 'Thank you for saving my life. I\'m afraid we don\'t have time for formal introductions.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50771, 1, 0, 0, 0, 0, 0, 0, 0, 'Not bad, chief! The crew should be wrapping it up soon.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50775, 1, 0, 0, 0, 0, 0, 0, 0, 'That should fill us up a few more bags. If they run out again, I\'ll make \'em break out the pickaxes.$b$bI bet then a little self-fueling won\'t sound so bad!$b$bUngrateful sons of bellybutton lint...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50794, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes, Julwaba told me to expect you.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50808, 1, 603, 1, 0, 0, 0, 0, 0, 'So, an Old God lurks beneath the swamp and it\'s barely being contained?\n\nUnbelievable. Princess Talanji was right in calling her father a fool. How could he let such a thing stay so close to his empire?\n\nI will ensure that the warchief knows of your victory... and of our new problem.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50812, 1, 0, 0, 0, 0, 0, 0, 0, 'That\'s better. Not sure how we were going to turn a profit if we had to keep replacing workers.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50817, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes! Thanks, $n! They are so much happier now!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50818, 5, 0, 0, 0, 0, 0, 0, 0, 'My flute! I\'ve been looking everywhere!$b$bWell, sort of...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50834, 5, 0, 0, 0, 0, 0, 0, 0, 'Dangerous? Violence incoming? Tell us somethin\' we don\'t know!$b$bWe ain\'t goin\' nowhere until we get what we came for!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50835, 0, 0, 0, 0, 0, 0, 0, 0, 'Let us get to work.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50881, 66, 0, 0, 0, 0, 0, 0, 0, 'Hmm. Zolani speaks highly of your deeds. Perhaps we may not need to kill you yet.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50904, 1, 0, 0, 0, 0, 0, 0, 0, 'Jakra\'zet has already begun the ritual. We must stop him before it\'s too late!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50913, 1, 0, 0, 0, 0, 0, 0, 0, 'If at any time you require my gift, commune at a shrine built in my honor to receive my boon.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50933, 0, 0, 0, 0, 0, 0, 0, 0, 'You do not look like one of de prisoners. I... I could use your help.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50934, 0, 0, 0, 0, 0, 0, 0, 0, '<The Zandalari next to this antidote is barely alive.>', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50954, 0, 0, 0, 0, 0, 0, 0, 0, 'We have won the battle, not the war.', 31478);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50963, 1, 0, 0, 0, 0, 0, 0, 0, 'Rezan is... dead?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50979, 1, 0, 0, 0, 0, 0, 0, 0, 'Thanks, $n! That should do the trick!$b$bAt least it will tide me over until they realize we\'re on the same side.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50980, 4, 0, 0, 0, 0, 0, 0, 0, 'It seems he found you indigestible. Excellent work!$b$bWe all thank you!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50992, 2, 0, 0, 0, 0, 0, 0, 0, '', 39427);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51053, 1, 0, 0, 0, 0, 0, 0, 0, 'Dat was where it all began. I could\'ve stopped Zem\'lan right dere... I could\'ve prevented dis fate for de crew.\n\nInstead, I led dem straight into it.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51054, 1, 0, 0, 0, 0, 0, 0, 0, 'Dis won\'t be easy, $p. But it be worth it.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51055, 1, 0, 0, 0, 0, 0, 0, 0, 'It looks like we all be of one accord. It be mutiny!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51056, 1, 0, 0, 0, 0, 0, 0, 0, 'Now you know how desperate we were on dat day, $p.$B$BI don\'t be making excuses, but you should understand why we did what we did.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51057, 1, 0, 0, 0, 0, 0, 0, 0, 'Good. We be ready to sail.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51059, 396, 0, 0, 0, 0, 0, 0, 0, 'Here we are. De Golden Isle. It\'s beautiful, ain\'t it?$B$BA pirate\'s paradise.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51060, 1, 0, 0, 0, 0, 0, 0, 0, 'I suppose dis little amount of gold won\'t be cursin\' ya for too long. I\'ll carry de rest.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51061, 1, 0, 0, 0, 0, 0, 0, 0, 'I guess de idols can\'t be destroyed... even by a mortal. Curses be fickle things.$B$BI was de first Zem\'lan cursed, and I did nothin\' to stop him from cursin\' de rest of de crew.$B$BI regret it every day...$B$BBut enough livin\' in de past. It\'s time to be free.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51062, 1, 0, 0, 0, 0, 0, 0, 0, 'What have you gotten us into, $p? We asked you to find our friend, not bring monsters into our camp.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51071, 66, 0, 0, 0, 0, 0, 0, 0, 'Thank you, champion. Dat is one less threat to de area.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51072, 66, 0, 0, 0, 0, 0, 0, 0, 'You faced down those Thumpknuckles? Dat is worth a lot to us. Thank you.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51087, 5, 0, 0, 0, 0, 0, 0, 0, 'A spirit? How terrible!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51088, 1, 0, 0, 0, 0, 0, 0, 0, 'I have fought death, $r. It is the most boring confrontation you can imagine.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51089, 1, 603, 11, 0, 0, 0, 0, 0, 'Hah! He be dead? Really? Dat be wonderful!\n\nHere be your reward, you earned it!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51091, 66, 0, 0, 0, 0, 0, 0, 0, 'Those two have been fighting for supremacy as long as can be remembered. They will not be bothering de jungles of Zandalar anymore.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51101, 273, 0, 0, 0, 0, 0, 0, 0, 'It is good dat you have come. De king is dying, and we do not have much time to change dat.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51111, 1, 0, 0, 0, 0, 0, 0, 0, 'You have gone beyond our wildest expectations!$b$bThank you, $n, for upholding the honor of the Horde. We must remember who we were, to realize what we may become.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51129, 1, 0, 0, 0, 0, 0, 0, 0, 'He was not prepared for such a fall! I think the only part of my body that doesn\'t hurt is the hat!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51150, 1, 0, 0, 0, 0, 0, 0, 0, 'I will keep these plates and give them to their families. Let\'s keep our heads shut on the details of their deaths, shall we? There is no need to cause them even more sadness.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51161, 1, 0, 0, 0, 0, 0, 0, 0, 'Za\'roco got what he deserved, and now, so will you.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51162, 1, 0, 0, 0, 0, 0, 0, 0, 'A bounty worth paying. Thank you, $p.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51164, 6, 1, 0, 0, 0, 3000, 0, 0, 'You killed de giant snake? Congratulations!\n\nGuess I\'d better update the brochure excursion list.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51165, 66, 0, 0, 0, 0, 0, 0, 0, 'Vesarik\'s death will be a devastating blow to the Faithless forces.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51167, 1, 0, 0, 0, 0, 0, 0, 0, 'I always believed that witches performed their rituals without clothes. Hey, where\'s your broom?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51168, 1, 0, 0, 0, 0, 0, 0, 0, 'The worst places in the world are all dark holes and no artifacts!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51169, 1, 0, 0, 0, 0, 0, 0, 0, 'The name Fort Victoria does not seem appropriate to me. It should be called something like Fort Crap.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51170, 5, 1, 6, 0, 0, 0, 0, 0, 'Thank the Light that you came with us! We have lost Private Leonard, and the filthy snakemen have captured Private James!$b$bThe important thing is, I think you chased the others away when you started to shoot them.$b$bSomething is something, right?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51177, 1, 0, 0, 0, 0, 0, 0, 0, 'A terrible business, $n. I had embarked on a mission to rescue or recover a corpse. Seeing one of our own in those conditions was ... disturbing.$b$bWhat done is done. I\'ll send a report to Halford. Don\'t worry about the heavy lifting this time.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51190, 1, 0, 0, 0, 0, 0, 0, 0, 'Few less cretins to worry about.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51191, 378, 0, 0, 0, 500, 0, 0, 0, 'You could be an excellent doctor. Or at least a decent nurse.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51192, 5, 0, 0, 0, 0, 0, 0, 0, 'It is amazing what we can do when we have the necessary supplies!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51193, 66, 1, 0, 0, 500, 500, 0, 0, 'I know it doesn\'t look like a special hammer. But for me it has a lot of value.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51229, 1, 0, 0, 0, 0, 0, 0, 0, 'The crew is setting up our camp in record time! You did a good job securing this outpost on the coast.Now, as for Sergeant Ermey ...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51244, 1, 0, 0, 0, 0, 0, 0, 0, 'No time to waste. Area ahead exhibits immense corruption triggered by G\'huun.\n\nScanners indicate corruption caused by leak within G\'huun containment structure.\n\nMust push forward to contain leak. Cannot perform function alone.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51246, 1, 661, 0, 0, 200, 500, 0, 0, 'We have an infestation of Alliance. We\'ve done what we can. I will need to message for reinforcements.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51247, 1, 0, 0, 0, 500, 0, 0, 0, 'Intelligence isn\'t always gathered from the most obvious things.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51248, 4, 1, 0, 0, 500, 500, 0, 0, 'These are going to be great!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51249, 1, 4, 0, 0, 500, 500, 0, 0, 'This will do nicely. Now all we need is some butter. And maybe some lemon.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51279, 5, 0, 0, 0, 0, 0, 0, 0, 'Do you know that feeling when you think something is wrong and you can\'t figure out what it is? It is exciting!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51280, 6, 0, 0, 0, 0, 0, 0, 0, 'There doesn\'t seem to be much of value in this cursed jungle. Why would Captain Conrad think they are worth trading with?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51281, 1, 0, 0, 0, 0, 0, 0, 0, 'I begin to think that this has turned into a search for a corpse. I don\'t think we\'ll find the famous Captain Conrad alive at this point.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51282, 1, 0, 0, 0, 0, 0, 0, 0, 'I wondered when they would come looking for me. I must admit that I am not a prisoner here, as you surely believe. In fact, quite the opposite.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51283, 1, 273, 0, 0, 0, 0, 0, 0, 'I hope the trip didn\'t make you dizzy, $n. Our mission is just beginning.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51286, 0, 0, 0, 0, 0, 0, 0, 0, 'Finally something goes our way. We\'ve stopped them escaping, for now.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51309, 1, 0, 0, 0, 0, 0, 0, 0, 'They have a couple of burns here and there, but nothing out of the ordinary. They will serve me.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51331, 5, 0, 0, 0, 0, 0, 0, 0, 'If they don\'t like mole machines, they should have chosen another profession!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51332, 1, 0, 0, 0, 0, 0, 0, 0, 'Hey $n. You ever been to Drustvar?\n\nWe\'re goin\' to Drustvar.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51340, 1, 0, 0, 0, 0, 0, 0, 0, 'Here we are. Time to see what the Alliance is up to.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51349, 661, 603, 0, 0, 0, 0, 0, 0, 'Wyrm Terrier sent you to find me? Well, it will have to wait.This thing knows where Private James was taken!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51350, 1, 273, 0, 0, 0, 0, 0, 0, 'These roots will do!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51351, 1, 273, 0, 0, 0, 0, 0, 0, 'Yes, that\'s it! I hope you weren\'t stung too.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51357, 1, 0, 0, 0, 0, 0, 0, 0, 'Using their own weapons against them. I like it.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51359, 11, 0, 0, 0, 0, 0, 0, 0, 'Ah! Haha! Hahahahaha! Wow! I have to admit I\'m a little jealous that I let you take her out before me. Those goblins weren\'t expecting that, huh', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51364, 1, 0, 0, 0, 0, 0, 0, 0, 'This is the troll you flew in with, is it not? She lives, for now.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51366, 1, 273, 0, 0, 0, 0, 0, 0, 'I can feel life returning to my limbs. Thank you, stranger!$b$bI\'m afraid there is no time for introductions. The faithless have captured your friend.$b$bDon\'t worry, my people will be happy to help you in return for your kindness.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51369, 1, 273, 0, 0, 0, 0, 0, 0, 'Oh no! The faithless are attacking the temple!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51389, 1, 2, 0, 0, 0, 0, 0, 0, 'I can\'t believe you and the sergeant tried so hard to rescue me! I ... I am speechless! Thank you!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51391, 1, 2, 0, 0, 0, 0, 0, 0, 'They already told me how you managed to make the faithless flee in terror!$b$bOn behalf of all my people, thank you very much.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51394, 1, 2, 0, 0, 0, 0, 0, 0, 'The faithless will never stop, but your efforts have bought us some time.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51395, 1, 2, 0, 0, 0, 0, 0, 0, 'The faithless will never stop, but your efforts have bought us some time.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51402, 1, 1, 0, 0, 0, 0, 0, 0, '<Wyrmbane listens to your report.>$b$bThe news of this c\'thraxxi is disturbing, but you did well to earn the trust of the local population. It will not be the last time we need your help.$b$bWell done, $n.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51407, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes... yes... I see here. Dire words indeed, young one.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51418, 1, 0, 0, 0, 0, 0, 0, 0, 'Are you $n, the $r $c. How much does Kelsey talk? We\'ve heard a lot about you.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51421, 1, 0, 0, 0, 0, 0, 0, 0, 'Welcome ta Tiragarde, boss-mon. Ya ready to be parleyin\'?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51435, 1, 0, 0, 0, 0, 0, 0, 0, 'Now we be talkin\', boss-mon! Ya look ready ta do some privateerin\'!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51436, 1, 0, 0, 0, 0, 0, 0, 0, 'I don\'t care about your faction wars, $n. If you are the folks that will help save my crew, you are the folks that will get my help.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51437, 1, 0, 0, 0, 0, 0, 0, 0, 'That is not what I...\n\nYou know what? Sure. That works.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51438, 1, 0, 0, 0, 0, 0, 0, 0, 'Dis place be feelin\' like home in no time.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51439, 1, 0, 0, 0, 0, 0, 0, 0, 'Oh da goblins gonna be lovin\' dese, mon.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51440, 1, 0, 0, 0, 0, 0, 0, 0, 'I hoped that Taryn would see the truth. Ah, well. So it goes.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51441, 11, 0, 0, 0, 0, 0, 0, 0, 'Now we be havin\' ourselves a mutiny!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51442, 1, 0, 0, 0, 0, 0, 0, 0, 'Captain Owings...\n\nIt has a certain ring to it, don\'t you think?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51443, 0, 0, 0, 0, 0, 0, 0, 0, 'The Dark Lady has placed her trust in us. We will not fail her, $n.', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51446, 1, 0, 0, 0, 0, 0, 0, 0, 'Oh, you\'d like to learn about harvesting winter\'s kiss?  Well that\'s very fortunate, for both of us...', 27404);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51464, 1, 0, 0, 0, 0, 0, 0, 0, 'You have done well. I will begin the ritual as soon as I can.$b$bIn de mean time, you are owed a lesson on how to harvest star moss.$b$bThe magic of star moss is sensitive to emotions, which is why it helps ground those dat have passed on.$B$BWhen you go to harvest, clear your mind and think of those you\'ve lost. Dat feeling of loss, reverence, and de joy dey once brought will keep de bloom from wilting.', 27404);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51465, 0, 0, 0, 0, 0, 0, 0, 0, 'Y así como así, todo vuelve a la normalidad. Gracias a la Trituramática Mk1, recuperarás todos los materiales que uses para crear un objeto.$b$b¡Está bien, está bien! Quizá no recuperes TODOS los materiales, pero, vamos... algo es algo, ¿no crees?$b$bAh, y préstale mucha atención al expulsom. Según hemos investigado, es un derivado mágico del proceso de desarmado.$b$bEs muy potente y solo aparece cuando desarmamos las cosas más lindas.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51526, 1, 0, 0, 0, 0, 0, 0, 0, 'Hurry up and get yourself squared away, $n. We\'ve got a battle to get to.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51532, 274, 5, 0, 0, 0, 0, 0, 0, 'I don\'t know what foul sorcery those Kul Tirans used upon our ships, but right now our men are being pushed to the shore with nowhere to evacuate!\n\nNo time to puzzle things over, we have to act now!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51536, 1, 0, 0, 0, 0, 0, 0, 0, 'Thank you, friend.$b$bThese humans have made a grave error today.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51538, 1, 0, 0, 0, 0, 0, 0, 0, 'It seems the naga have moved up in threat level from \"mere nuisance\". These are dark days indeed.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51539, 1, 0, 0, 0, 0, 0, 0, 0, 'It was wise of you to bring this to me, $n.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51544, 0, 0, 0, 0, 0, 0, 0, 0, 'Now we can bring our wounded to safety.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51555, 1, 0, 0, 0, 500, 0, 0, 0, 'You\'ve been sent to check up on me?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51571, 0, 0, 0, 0, 0, 0, 0, 0, 'You have chosen to sail to Nazmir.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51572, 0, 0, 0, 0, 0, 0, 0, 0, 'You have chosen to sail to Vol\'dun.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51573, 1, 0, 0, 0, 0, 0, 0, 0, 'Mojambo will come back with all his minions. And when he does... dere will be blood.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51574, 1, 0, 0, 0, 0, 0, 0, 0, 'That\'s a lot of juice!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51587, 1, 0, 0, 0, 0, 0, 0, 0, 'I thought I was going to meet the Earthmother along with the rest of my men.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51602, 1, 0, 0, 0, 0, 0, 0, 0, 'Dese ancient blades may not be in de best condition, but dey are finely forged. I am sure dey will hold up in battle just fine.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51643, 1, 0, 0, 0, 0, 0, 0, 0, 'Good job, that\'ll buy our troops some breathing room.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51663, 1, 0, 0, 0, 0, 0, 0, 0, 'Good. I did not need to tell you to do dis thing.\n\nDe Jambani prepare for de fall of Zuldazar as foretold by Vol\'jamba. Dis will not happen, but destroying their stockpiles will make dem easier to eradicate once Rastakhan decides to deal with dis situation.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51668, 1, 0, 0, 0, 0, 0, 0, 0, 'At last dis war be over and we can live in peace. I do not know how to thank you for all you\'ve done.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51674, 1, 0, 0, 0, 0, 0, 0, 0, 'Fire, blood, war. Nothing will stop us.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51675, 1, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done us a great service, $n.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51677, 1, 0, 0, 0, 0, 0, 0, 0, '<Jol wheezes.>\n\nThat did not... work how I imagined it would.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51678, 273, 0, 0, 0, 0, 0, 0, 0, 'I will display dis behind my throne as a reminder. No one double-crosses Rastakhan and lives to speak of it.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51679, 1, 0, 0, 0, 0, 0, 0, 0, 'I think I know what dis means, $n, and it does not bode well for my loa.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51680, 1, 0, 0, 0, 0, 0, 0, 0, 'Now you understand the precariousness of this situation. We will need your hands if we are to succeed in this complex bit of mending ahead of us.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51691, 1, 0, 0, 0, 0, 0, 0, 0, 'Good work, $n. We\'ll have need of their labor when we rebuild.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51696, 1, 1, 0, 0, 0, 0, 0, 0, 'Good work, $n$b$bMost of the buildings were easily repaired but we\'ll have to send to the Warchief for more troops.$b$bWe\'re going to have to fight for our place in this land... of this I\'m certain.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51717, 1, 0, 0, 0, 0, 0, 0, 0, 'Rhan\'ka sent you? He still owes me for the last batch of honey I sold him!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51718, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'ll just take me a moment to get the honey out of these.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51753, 1, 1, 0, 0, 0, 0, 0, 0, 'Through countless wars, I\'ve learned that those who stand alone will fall.$b$bLet us stand as one, $n. Together, we will face anything our foes send our way.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51770, 274, 0, 0, 0, 0, 0, 0, 0, 'Our task is not to be taken lightly, $n.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51772, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re from the troll tribe at the arena? I\'m afraid we don\'t have much to trade right now...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51773, 0, 0, 0, 0, 0, 0, 0, 0, 'You look like the kind of $r that likes to make some coin. Am I wrong or am I right?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51775, 0, 0, 0, 0, 0, 0, 0, 0, 'So you\'ve come from the trolls at the arena and you\'re looking for work? I\'d be happy to do business with you in exchange for a little help...', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51796, 1, 1, 0, 0, 0, 0, 0, 0, 'Our war has only begun.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51800, 0, 0, 0, 0, 0, 0, 0, 0, '<You have chosen to set sail for Tiragarde Sound.>', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51801, 0, 0, 0, 0, 0, 0, 0, 0, 'You have chosen to sail to Drustvar.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51802, 0, 0, 0, 0, 0, 0, 0, 0, 'You have chosen to sail to Stormsong Valley.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51803, 1, 0, 0, 0, 0, 0, 0, 0, 'You have the Queen\'s trust. For now.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51810, 0, 0, 0, 0, 0, 0, 0, 0, 'A toast to the former captain of The Siren\'s Call. He was a good man, but good men don\'t live long around here!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51829, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'ve been looking everywhere for this!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51870, 6, 0, 0, 0, 0, 0, 0, 0, 'You da Horde champion I\'m waitin\' for? Let\'s get to work.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51888, 6, 0, 0, 0, 0, 0, 0, 0, 'Nice work, $n. We make a good team. Let\'s do this again sometime.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51961, 1, 0, 0, 0, 0, 0, 0, 0, 'Excellent decision, $n.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51967, 1, 0, 0, 0, 0, 0, 0, 0, 'The lands of Nazmir are cruel to all who enter, but Fort Victoria will stand firm in the face of danger.$b$bWell done, $n.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51968, 1, 66, 0, 0, 0, 0, 0, 0, 'Then we will establish our base of operations outside Xibala. And I will order reinforcements to help Brigadier Thom.$b$bThanks for the report, $n. Well done.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51969, 1, 0, 0, 0, 0, 0, 0, 0, 'The waters around Vol\'dun are ruthless, but Jes-Tereth is one of the best helmsmen in the sea!$b$bLet\'s continue our winning streak.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51975, 1, 1, 0, 0, 0, 300, 0, 0, 'You be impressin\' me wit ya skills in parleyin\', boss-mon.$b$bI tink da two of us should be stickin\' togetha. Da spirits insist!$b$bDon\'t be worryin\'. I\'ll get changed.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51979, 1, 0, 0, 0, 0, 0, 0, 0, 'You have made your choice.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51980, 66, 0, 0, 0, 0, 0, 0, 0, 'You have my thanks. He was a slippery one, dat poacher.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51982, 2, 1, 0, 0, 0, 3000, 0, 0, 'You have done a great service to the people of Stormsong Valley and the Alliance today. Thanks, $n. If we persevere, we will drive the Horde from this land.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51984, 1, 0, 0, 0, 0, 0, 0, 0, 'That\'s quite a location Ty\'jin scoped out.\n\nThe pirates are another boon. If we ever need some bodies to fall on swords for us... they will do.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51986, 1, 0, 0, 0, 0, 0, 0, 0, 'Warfang Hold is an important strategic base for the Horde.\n\nIt is imperative that it stays safe. Rexxar will see to that.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51990, 1, 0, 0, 0, 0, 0, 0, 0, 'I will get dese to a handler. We should be de first thing dey see if we want to keep dem.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51991, 5, 0, 0, 0, 0, 0, 0, 0, 'Hey! Not bad for your first time! They\'re so ugly they\'re cute, aren\'t they?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (51998, 1, 0, 0, 0, 0, 0, 0, 0, 'Let\'s not talk about this ever again. Deal?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52003, 1, 4, 0, 0, 0, 300, 0, 0, 'You know, $n, I\'ve been following your adventure for a while, and I think you\'re just great!$b$bI was hoping that you would allow me to accompany you, perhaps I can be of assistance? I am not a great warrior, but I am the best at mechanics!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52008, 1, 4, 0, 0, 0, 300, 0, 0, 'I had my reservations when High Commander Wyrmbane asked me to accompany him to this vast desert. I did not think there would be many interesting things.$b$bMy assumptions, fortunately, were wrong. The Sethrak who inhabit this region wield powerful magic and use it with unconventional techniques.$b$bI cannot wait to study their equipment to reveal the secrets of their power, but my priority is to help you and the high commander in whatever you need.$b$bPlease don\'t hesitate to come to me.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52013, 1, 4, 0, 0, 0, 300, 0, 0, 'Now that we\'ve settled here ... I want to aim a little higher.$b$bYou took care of Conrad. You\'ve taken care of the blood trolls. I wonder where your path will take you now?$b$bThe truth is that I don\'t know, but you can count on me. You will need an experienced, strong and stalwart combatant like me if you want to win this war!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52034, 1, 0, 0, 0, 0, 0, 0, 0, 'I hope you left a few alive.\n\nThey need to learn to fear us.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52035, 1, 0, 0, 0, 0, 0, 0, 0, 'Excellent reconnaissance. Well-done.\n\n<Halford begins distributing water and rations to the others in your group.>', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52038, 0, 0, 0, 0, 0, 0, 0, 0, '<Shandris licks her lips in anticipation.>', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52039, 669, 0, 0, 0, 0, 0, 0, 0, 'It\'s done? Good. Let\'s get a move on.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52040, 1, 0, 0, 0, 0, 0, 0, 0, 'Won\'t Halford be happy to find out that we\'ve done his work for him?', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52041, 1, 0, 0, 0, 0, 0, 0, 0, 'Arlethal is already dead?\n\nThat\'s good, I suppose... though it does take some of the fun out of my plan.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52042, 11, 0, 0, 0, 0, 0, 0, 0, 'Ha ha! That should set the Reliquary back a few months.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52067, 1, 0, 0, 0, 0, 0, 0, 0, 'Calvin sent you? We can use all the help you can provide!', 27481);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52068, 5, 0, 0, 0, 0, 0, 0, 0, 'Did he owe you any money? He always sends me people he owes money to!', 27481);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52073, 1, 1, 0, 0, 0, 0, 0, 0, 'This is it, no more running around! \n\nNow, we will end this threat to my homeland once and for all!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52129, 1, 0, 0, 0, 0, 0, 0, 0, 'If Merd wants you to help, I\'ve got just the thing!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52131, 0, 0, 0, 0, 0, 0, 0, 0, 'Make your choice when you are ready.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52139, 1, 1, 0, 0, 0, 0, 0, 0, 'Good, you have returned. Dere is no time to waste.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52146, 1, 0, 0, 0, 0, 0, 0, 0, 'Guzbert\'s... what? No!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52154, 1, 0, 0, 0, 0, 0, 0, 0, 'I am grateful for Alleria\'s knowledge and the willingness of our new allies to take the lead. This new two-pronged strategy presents us with new strategic options that we must carefully evaluate.$b$bI must ask Alleria to stay with me to coordinate our efforts on both fronts. I will need you to accompany Magister Umbric to ensure the success of the mission. Good luck.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52210, 1, 0, 0, 0, 0, 0, 0, 0, 'You\'re just de $Gmon:ladymon; I was looking for! I have heard tales.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52443, 1, 0, 0, 0, 0, 0, 0, 0, 'Ready to leave? So, we go.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52444, 1, 0, 0, 0, 0, 0, 0, 0, 'I trust you are sufficiently prepared, then.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52450, 1, 273, 0, 0, 0, 0, 0, 0, 'The people of Kul Tiras are diverse and capable. Now that their forces are loyal to the Alliance, the Horde will not be able to stop us.$b$bNow the real work begins.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52451, 1, 273, 0, 0, 0, 0, 0, 0, 'The people of Zandalar are diverse and capable. With their forces loyal to the Horde, the Alliance has no chance of stopping us.\n\nNow, the real work begins.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52472, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n!', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52477, 1, 378, 0, 0, 0, 0, 0, 0, 'It\'s good to see that snake got what it deserved!\n\nHere\'s your reward, you earned it.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (52746, 273, 0, 0, 0, 0, 0, 0, 0, 'You have gathered enough war resources to begin the assault on Kul Tiras.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (53028, 1, 0, 0, 0, 0, 0, 0, 0, 'Ye made it. We don\'t have any time tae lose.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (53031, 1, 1, 0, 0, 0, 0, 0, 0, 'It would seem you were in the right place at the right time, $n. This Heart of Azeroth the dwarf gave you may prove useful indeed.\n\nBut the war must remain our sole focus. We can spare no forces to aid Magni on his grand crusade.\n\nThe Alliance will stop at nothing to destroy the Horde. Fortunately, the Dark Lady has a plan to make us stronger.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (53056, 1, 0, 0, 0, 0, 0, 0, 0, 'It is time to establish our final major foothold in Kul Tiras.', 27404);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (53333, 273, 0, 0, 0, 0, 0, 0, 0, '<Nathanos looks over the gathered resources.>\n\nThis should do it.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (53372, 1, 1, 0, 0, 0, 0, 0, 0, 'The Alliance moves against the Undercity, seeking retribution for the burning of Teldrassil.\n\nWe will not allow them to take what is ours, $c.', 27377);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (53583, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. We will start working on this immediately.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (53602, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. We will see to it right away.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (53711, 5, 1, 0, 0, 0, 3000, 0, 0, 'Everyone in Boralus celebrated when they saw the explosions on that warship! The Horde will think twice before attacking Boralus again.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (54363, 1, 0, 0, 0, 0, 0, 0, 0, 'Thanks for your input.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (54969, 1, 0, 0, 0, 0, 0, 0, 0, 'Thanks for trusting me.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (54972, 1, 0, 0, 0, 0, 0, 0, 0, 'We are extremely lucky to have met the Ankoans. Perhaps the situation is not as dire as it seems.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55095, 1, 0, 0, 0, 0, 0, 0, 0, 'Finally, good news. Our champion rises amid the chaos.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55175, 1, 0, 0, 0, 0, 0, 0, 0, 'So far, the Ankoans have proven to be great allies.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55361, 0, 0, 0, 0, 0, 0, 0, 0, 'Good to see you. We must speak quickly. I fear for the safety of my teacher ...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55362, 0, 0, 0, 0, 0, 0, 0, 0, 'You have acted well. I can perceive that the elemental balance is close to returning to its natural state.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55363, 0, 0, 0, 0, 0, 0, 0, 0, 'I tremble at the thought of the fate that awaited me if you had not rescued me.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55374, 1, 0, 0, 0, 0, 0, 0, 0, 'We must hurry. I feel like the essence of mine is calling me.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55400, 1, 0, 0, 0, 0, 0, 0, 0, 'I was hoping the trip wasn\'t as… hard for you as it was for me.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55407, 1, 0, 0, 0, 0, 0, 0, 0, 'Have the fissures healed? Well. We can proceed.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55425, 1, 0, 0, 0, 0, 0, 0, 0, 'Now that we have the essence, I pray that the earth will find peace once more.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55497, 1, 6, 0, 0, 0, 300, 0, 0, 'I like this tauren friend you have. Hopefully you stay away from corruption!$b$bWell, time to see what MADRE can do with that essence!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55533, 1, 0, 0, 0, 0, 0, 0, 0, 'Complete analisis. To prevent planetary annihilation, the Heart of Azeroth must be upgraded.$b$bObjective: Find compatible essences. Priority: Critical.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55618, 1, 0, 0, 0, 0, 0, 0, 0, 'Accepted power source. Forge activation completed.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55625, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done. I feel a powerful energy emanating from within those pearls.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55683, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah… I remember everything. It happened so long ago. I think the time has come for me to rest ...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55719, 0, 0, 0, 0, 0, 0, 0, 0, 'Thanks for your input.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55786, 0, 0, 0, 0, 0, 0, 0, 0, 'Thanks for your input.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55801, 0, 0, 0, 0, 0, 0, 0, 0, 'Thanks for your input.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (55851, 1, 0, 0, 0, 0, 0, 0, 0, 'I welcome you back! We have a lot to do now that MOTHER has finished her calculations.$b$bLet\'s get started right away.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56043, 1, 1, 0, 0, 0, 0, 0, 0, 'I never would have expected this ...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56119, 0, 0, 0, 0, 0, 0, 0, 0, 'May Neptulon shower you with his blessings, friend.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56156, 0, 0, 0, 0, 0, 0, 0, 0, 'It was an honor to fight by your side, $n. If you ever need me, you just have to ask me.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56162, 1, 0, 0, 0, 0, 0, 0, 0, 'Thanks for helping us again. There is still much left to do.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56304, 1, 0, 0, 0, 0, 0, 0, 0, '$n, that shadowy naga thaumaturge is torturing Corin.$b$bWe have to do something!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56321, 1, 0, 0, 0, 0, 0, 0, 0, 'Thanks, $n. My father and I will always be grateful for what you did.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56350, 1, 0, 0, 0, 0, 0, 0, 0, 'The Maretista! Of course...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56422, 1, 0, 0, 0, 0, 0, 0, 0, 'Thanks, $n.$b$bThanks to your efforts, we regain some of the peace of mind that we lost so long ago.$b$bMy father and I are at your service.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56561, 1, 1, 0, 0, 0, 0, 0, 0, 'I\'ve seen these crystals in a couple of places, often with weird looking slime around them. There must be some link between slime and crystal, but I haven\'t figured it out yet.$b$bAnyway, the only use I\'ve found for these crystals is to make tiny creatures follow me for a bit.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56640, 1, 0, 0, 0, 0, 0, 0, 0, 'Our army was devastated, but we will raise it again.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56641, 1, 0, 0, 0, 0, 0, 0, 0, 'Sometimes it is very satisfying to break other people\'s toys.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56642, 1, 0, 0, 0, 0, 0, 0, 0, 'The ritual ended as soon as we killed the mistress of the tides.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56643, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'m tired of this place.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56644, 1, 0, 0, 0, 0, 0, 0, 0, 'We have been watching them. Your people seem very capable.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56829, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, bien. No comieron a nadie.\n\nEl carruaje está listo, cuando quieras.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56942, 1, 0, 0, 0, 0, 0, 0, 0, 'El carruaje no podrá atravesar todo eso.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56955, 1, 0, 0, 0, 0, 0, 0, 0, 'Tus talentos en combate son prometedores. Tal vez con algunos siglos más y un poco de humildad podamos convertirte en alguien de valor.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (56978, 1, 0, 0, 0, 0, 0, 0, 0, 'Quizá no parezca, pero esta rebelión cuelga de un hilo. Quienes desafían al amo son unos tontos que conocerán una nueva forma de dolor y sufrimiento.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57004, 1, 0, 0, 0, 0, 0, 0, 0, 'There is a link between the ocean and what lies beneath the waves.$b$bThe Resplendent Man will be able to use Prismatic Mana Pearls to enhance benthic items.$b$bThis will be very helpful.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57007, 1, 0, 0, 0, 0, 0, 0, 0, 'Ay, bien, ¿aún no has muerto?\n\n¿Ves dónde aterrizaron el Lord Chambelán o Garrotero?', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57010, 1, 0, 0, 0, 0, 0, 0, 0, 'Well, well ... It seems that the heart of Azeroth has new capabilities, just like MOTHER said!$b$bStay alert to find new essences. They have to be scattered all over Azeroth.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57025, 1, 0, 0, 0, 0, 0, 0, 0, 'Pido disculpas por el estado de las cosas. Normalmente, habría un venthyr para recibirte. Hacía mucho tiempo que no veíamos a alguien atravesar esa puerta. \n\nNo pasa nada, porque conozco el discurso de bienvenida mejor de lo que conozco un azote por la mañana.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57026, 1, 0, 0, 0, 0, 0, 0, 0, 'Espero que estas criaturas miserables no te hayan aburrido con su parloteo inútil.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57037, 669, 0, 0, 0, 0, 0, 0, 0, 'His song played again. It is a joy to my ears, but the voice is still louder. We must silence her before she destroys everything we love.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57098, 1, 0, 0, 0, 0, 0, 0, 0, '$GBienvenido:Bienvenida; a la Arboleda del Terror.\n\nAquí verás el comienzo del proceso para el ánima que buscas.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57102, 273, 0, 0, 0, 0, 0, 0, 0, '<The centurion chimes softly.>', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57113, 669, 0, 0, 0, 0, 0, 0, 0, 'The entire temple is overrun with Forsworn. We must find the survivors!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57114, 1, 0, 0, 0, 0, 0, 0, 0, 'Fortunate that you both found us. \n\nThere are many wounded. I have been able to keep them alive, but it has taken all of my focus. You must help us push back against the Forsworn.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57115, 1, 0, 0, 0, 0, 0, 0, 0, 'It seems they are gathering information on performing one of our strongest rituals. But for what purpose?', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57116, 1, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. These aspirants will live to improve themselves and serve Bastion.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57117, 1, 0, 0, 0, 0, 0, 0, 0, 'Fighting the Forsworn, our brothers and sisters... it never gets easier.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57118, 1, 0, 0, 0, 0, 0, 0, 0, 'Do not worry about the collectors. With the drought, they have been barely functioning for quite some time.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57120, 1, 0, 0, 0, 0, 0, 0, 0, 'Compassion!\n\nThe Paragon chased Lysonia in such a rush, he left his sword behind. This may be key to expelling the Forsworn from the temple.\n\nTake it with you, and may it serve you well.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57121, 1, 0, 0, 0, 0, 0, 0, 0, 'I can feel the oppression here, as if it were a real weight on my body.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57122, 1, 0, 0, 0, 0, 0, 0, 0, 'Exiled by the Archon... failed by Lysonia...\n\nWhere can they go?', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57123, 1, 0, 0, 0, 0, 0, 0, 0, 'If my mind were ever to be twisted by the Maw... I ask that you cut me down.\n\nI cannot bear the thought.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57124, 0, 0, 0, 0, 0, 0, 0, 0, 'It seems that... I am the fool.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57125, 1, 0, 0, 0, 0, 0, 0, 0, 'It is unfortunate to hear that Lysonia has escaped us again... The revelations of both her master and her new powers are quite concerning.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57131, 1, 0, 0, 0, 0, 0, 0, 0, 'Bien hecho. Tal vez antes de morir logres ser $Gun gran cazador:una gran cazadora;.\n\nYa puedo saborear el temor que causarás.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57136, 1, 0, 0, 0, 0, 0, 0, 0, 'Bien hecho. El temor que infligimos nos permitió extraer mucha ánima para Revendreth.\n\nAhora, el objetivo final.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57159, 1, 0, 0, 0, 0, 0, 0, 0, 'Lo conseguimos por poco. Denathrius seguirá nuestro rastro pronto.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57161, 1, 0, 0, 0, 0, 0, 0, 0, '<Isquelión gruñe.> Llegas tarde.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57164, 1, 0, 0, 0, 0, 0, 0, 0, 'Cuando dominas el miedo, todos caen ante ti.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57173, 1, 0, 0, 0, 0, 0, 0, 0, '¿Eso es lo mejor que tienes?', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57174, 1, 0, 0, 0, 0, 0, 0, 0, '¿Esto es todo lo que queda de nuestro diezmo? Al señor del diezmo no le gustan las excusas. Puede que debamos conseguir un segundo diezmo rápidamente o nos condenará a todos al Distrito de Brasas.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57175, 1, 0, 0, 0, 0, 0, 0, 0, '<Mientras Isquelión sostiene los fragmentos de la piedra de pecados, el ánima hace que se atraigan entre sí para formar una piedra de pecados completa.>\n\nEstamos listos para continuar.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57176, 1, 0, 0, 0, 0, 0, 0, 0, 'En realidad, esta es una de las piedras de pecados más grandes que he visto.\n\nSerá fácil de aprovechar.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57178, 1, 0, 0, 0, 0, 0, 0, 0, '¡Espera! Por favor, Surcafauces, permíteme anunciarte ante el amo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57179, 1, 0, 0, 0, 0, 0, 0, 0, 'Ahora, Surcafauces, levántate como un arma del poder decretado en mi dominio.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57180, 1, 0, 0, 0, 0, 0, 0, 0, '<Lord Chambelán sonríe con ironía.>\n\nLos Salones de Expiación están bajo mi control.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57182, 1, 0, 0, 0, 0, 0, 0, 0, '<Sir Denathrius sonríe cuando le entregas el medallón.>\n\nMe complace saber que puedo confiarte este gran poder.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57189, 1, 0, 0, 0, 0, 0, 0, 0, 'Parece que salió mejor de lo que esperaba. \n\nPerfecto. La Acechamiedos no tiene oportunidad.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57190, 1, 0, 0, 0, 0, 0, 0, 0, 'Los devoradores los harán pedazos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57207, 1, 0, 0, 0, 0, 0, 0, 0, 'I am weakened, but I will heal.\n\nShe searched for pride that does not exist within me.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57240, 0, 0, 0, 0, 0, 0, 0, 0, 'Tú no perteneces aquí. ¡Aquí para malos colegas!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57261, 669, 0, 0, 0, 0, 0, 0, 0, 'Your journey has just begun, $n. Hope we can help each other', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57263, 1, 0, 0, 0, 0, 0, 0, 0, '<Sika touches the soft substance with her finger.>$b$bIt shines. That means it is spirited. The Little Forge will be pleased.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57264, 669, 0, 0, 0, 0, 0, 0, 0, 'We appreciate your help. Thanks.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57265, 1, 0, 0, 0, 0, 0, 0, 0, 'Somehow ... this is more and less than I expected. We must conform.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57266, 669, 669, 0, 0, 0, 0, 0, 0, 'This is a moment worth celebrating, $n.$b$b<Kleia grins you from ear to ear.>$b$bYou did it. They came.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57267, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah. They smell good! And they shine a lot.$b$bThis is fine, yes.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57269, 669, 669, 0, 0, 0, 0, 0, 0, 'Your appearance at this moment, on the brink of this betrayal, is curious to me.$b$b<The Paragon of Purity studies you with a questioning gaze.>$b$bYou did a good job. The Archon will find out about your contribution.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57270, 669, 0, 0, 0, 0, 0, 0, 0, 'Thank you for offering.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57288, 669, 0, 0, 0, 0, 0, 0, 0, 'Impressive. I see that I don\'t have much left to teach.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57291, 669, 0, 0, 0, 0, 0, 0, 0, 'Concentrate. This is the way you will get to speak to the Archon. I am convinced.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57301, 0, 0, 0, 0, 0, 0, 0, 0, 'You can keep adding all the ingredients you want. The more we add, the more we create!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57316, 1, 603, 0, 0, 0, 0, 0, 0, '¡Buena pelea, $Ghermano:hermana;! Ya empezaste tu ascenso a la cima.\n\nPero no pares de entrenar, tienes que estar $Gpreparado:preparada;. En Maldraxxus, siempre hay un contendiente esperando para quedarse con tu lugar.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57380, 1, 0, 0, 0, 0, 0, 0, 0, 'Este papel malo parece muy malo...', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57381, 1, 1, 0, 0, 0, 0, 0, 0, '¡Eso fue DIVERTIDO! No me había sentido tan viva en siglos. Todo en ti parece... diferente y vigorizante.\n\nPuedo seguir ayudándote hasta que desees abandonar el Pantano del Fin, pero después tendremos que separarnos por un tiempo. Tengo algunos asuntos que resolver con alguien que me encerró en una jaula.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57386, 1, 0, 0, 0, 0, 0, 0, 0, 'Well? Why aren\'t you out there fighting?', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57390, 0, 0, 0, 0, 0, 0, 0, 0, 'You survived? Really...\n\nI admit, that was unexpected.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57405, 1, 0, 0, 0, 0, 0, 0, 0, 'Bien, no te esperaba.\n\nMe temo que no tengo té para ofrecerte en este momento. Disculpa mis malos modales.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57425, 1, 0, 0, 0, 0, 0, 0, 0, 'In ages past, the Primus founded that tournament to showcase the might of Maldraxxus. \n\nToday, it has sparked an all-out war.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57426, 1, 0, 0, 0, 0, 0, 0, 0, '<Es evidente que la exposición a la Luz hace que Theotar pierda el control de sí mismo.>\n\nJusticia... para todas... y todos... los parasoles...', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57427, 1, 0, 0, 0, 0, 0, 0, 0, '<Es evidente que la exposición a la Luz hace que Theotar pierda el control de sí mismo.>\n\nMis... más sinceras disculpas...', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57428, 1, 0, 0, 0, 0, 0, 0, 0, '<Es evidente que la exposición a la Luz hace que Theotar pierda el control de sí mismo.>\n\nGubín... ¿Dónde estás?', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57442, 1, 0, 0, 0, 0, 0, 0, 0, '<Gubín y Tubín hablan al unísono.>\n\nEsto no es bueno.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57444, 669, 0, 0, 0, 0, 0, 0, 0, 'I saw them die. They deserved worse.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57446, 669, 0, 0, 0, 0, 0, 0, 0, 'Our fight is not over yet, $n.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57447, 669, 0, 0, 0, 0, 0, 0, 0, 'We won\'t have a second of rest today, will we?$b$b<Kleia looks at the fragment suspiciously.>', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57460, 1, 0, 0, 0, 0, 0, 0, 0, '¡El té está listo! ¡Tubín se lo dará a Theotar!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57461, 1, 0, 0, 0, 0, 0, 0, 0, '<Theotar bebe su té lentamente.>\n\n¡Es un paseo maravilloso!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57471, 1, 0, 0, 0, 0, 0, 0, 0, 'Я даже отсюда чувствую этот запах. Спасибо, $n.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57474, 1, 0, 0, 0, 0, 0, 0, 0, 'На нем осталась слизь от пожирателей, но, думаю, сойдет.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57477, 1, 0, 0, 0, 0, 0, 0, 0, 'Бутус готов.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57481, 1, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $n. \n\nЗдоровяки часто во всем... перебарщивают.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57511, 0, 0, 0, 0, 0, 0, 0, 0, 'And now we test them.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57512, 1, 0, 0, 0, 0, 0, 0, 0, 'Everything in this land is dangerous. There is no room for weakness.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57514, 1, 0, 0, 0, 0, 0, 0, 0, 'Mark my words, recruit. We will crush the other houses for their acts of treason.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57515, 1, 0, 0, 0, 0, 0, 0, 0, 'The Chosen are all that remains of what the Primus stood for.\n\nWe are the last hope of this realm.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57516, 1, 0, 0, 0, 0, 0, 0, 0, 'This blade was inside the stone all this time... \n\nMargrave Krexus was right about your potential.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57529, 6, 0, 0, 0, 0, 0, 0, 0, 'Oh, you\'re here to lend a hand? Wonderful!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57538, 1, 0, 0, 0, 0, 0, 0, 0, 'We can now better fulfill our duties here. I look forward to resuming my meditations. Thank you.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57545, 1, 0, 0, 0, 0, 0, 0, 0, 'Kala! Look! $n brought back your favorite things!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57547, 1, 0, 0, 0, 0, 0, 0, 0, 'I told you she can be quite the troublemaker. Ugh, Kala!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57549, 1, 0, 0, 0, 0, 0, 0, 0, 'It is an eternal struggle between the cosmic forces of existence.$b$bThey are constantly vying for power, but if one fell, chaos would dominate all worlds.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57551, 669, 0, 0, 0, 0, 0, 0, 0, 'To arms, Kyrian! We must be strong against the forces of the Void!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57552, 1, 0, 0, 0, 0, 0, 0, 0, 'Wherever you walk, you leave behind a trail of defeated Void forces.$b$bYou give your companions time to breathe.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57553, 1, 0, 0, 0, 0, 0, 0, 0, 'My disciples show determination, ready to fight one more day.$b$bIt will not be in vain.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57554, 669, 0, 0, 0, 0, 0, 0, 0, 'Don\'t celebrate our victory. More will come.$b$bWe must use this opportunity to attack.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57555, 1, 0, 0, 0, 0, 0, 0, 0, 'You have witnessed the great sacrifice of Agthia.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57568, 1, 0, 0, 0, 0, 0, 0, 0, 'With the crook and Korinna\'s guidance, I can begin to bring this place and its creatures back to their former state of respite. Thank you, $n.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57584, 1, 0, 0, 0, 0, 0, 0, 0, 'Hope that was informative and relaxing.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57644, 1, 0, 0, 0, 0, 0, 0, 0, 'Gracias por traerme de vuelta con mi hermana.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57648, 0, 0, 0, 0, 0, 0, 0, 0, 'Bien hecho, Surcafauces.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57649, 1, 0, 0, 0, 0, 0, 0, 0, 'La baronesa inspira gran dedicación. Confío en sus planes.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57676, 669, 0, 0, 0, 0, 0, 0, 0, 'The things that bind us may be violent. As much as they teach us, it is no reason to let them run wild.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57677, 669, 0, 0, 0, 0, 0, 0, 0, 'I am in great debt to you, $n.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57686, 0, 0, 0, 0, 0, 0, 0, 0, 'Las vidas mortales son tan frágiles... Y aún así, cualquiera de ellas puede alterar el entramado de la realidad.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57689, 1, 0, 0, 0, 0, 0, 0, 0, 'Conque esto es las Fauces. Siento que absorbe mi esencia. No bajes la guardia.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57690, 1, 0, 0, 0, 0, 0, 0, 0, 'Te reconozco. Ha habido varios problemas en Azeroth en los últimos tiempos, y sus campeones han estado al frente en cada situación. Debemos hablar tan pronto como lo permita el tiempo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57691, 1, 0, 0, 0, 0, 0, 0, 0, 'Bueno, eres bastante $Ghabilidoso:habilidosa;. Dame veinticinco de tu especie y un equipamiento apropiado y podríamos derrotar al amo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57693, 1, 0, 0, 0, 0, 0, 0, 0, 'Será cuestión de tiempo que el amo detecte mi presencia. Debemos actuar con velocidad y violencia si queremos detener a sir Denathrius.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57694, 1, 0, 0, 0, 0, 0, 0, 0, 'Quedan tan pocos, pero nos las ingeniaremos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57709, 669, 0, 0, 0, 0, 0, 0, 0, '<The Littleforge takes a few seconds to examine you, then nods.>$b$bWelcome. You\'re not what I expected, but we\'re still glad you\'re here.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57710, 669, 0, 0, 0, 0, 0, 0, 0, 'I confess that I was waiting for some show to lift the spirits of the applicants I have in charge.$b$bBut I didn\'t expect you to cheer me on too. Thanks for that.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57711, 1, 0, 0, 0, 0, 0, 0, 0, 'Hello again! How good to see you!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57712, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'ll make sure they go back to the right place for everyone to enjoy.$b$bI thank you for getting them back, and I hope you found them useful.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57713, 1, 0, 0, 0, 0, 0, 0, 0, 'I hope you like it. It is far from my best work, but it will serve you while you are here.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57714, 669, 0, 0, 0, 0, 0, 0, 0, 'This lesson is always painful. This does not change the way we see you.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57715, 669, 0, 0, 0, 0, 0, 0, 0, 'You are worthy of Bastion\'s grace, $n. May it serve you well.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57716, 669, 0, 0, 0, 0, 0, 0, 0, 'Thanks for bringing it here, $n. He is in a worse shape than any of us could have imagined.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57717, 669, 0, 0, 0, 0, 0, 0, 0, '<Eridia holds her hammer tighter, tensed.>$b$bHow ...? Why are there so many?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57719, 669, 0, 0, 0, 0, 0, 0, 0, 'These words ... This voice ...$b$bWhere do I know this voice from?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57724, 1, 0, 0, 0, 0, 0, 0, 0, 'Ahora que acabamos con las fuerzas del amo, ¡continuemos buscando al príncipe Renathal!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57778, 0, 0, 0, 0, 0, 0, 0, 0, 'Nos complace verte regresar.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57787, 0, 0, 0, 0, 0, 0, 0, 0, 'Plumaligera dice que has cumplido con tu parte del trato. ¡Eso es genial!\n\n¡Y tengo buenas noticias! Quizás podamos conseguirte una audiencia con la reina cuando llegue aquí, a Tirna Vaal. Qué emoción, ¿no es cierto?', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57816, 1, 273, 0, 0, 0, 200, 0, 0, '¿Eres $gnuevo:nueva;, no? No eres $gun feérico:una feérica;, no eres un alma y tampoco estás $gatado:atada; a una semilla silvestre.\n\n¿Y los droman te enviaron para ayudarme? Vaya, esto se puso muy interesante.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57895, 1, 0, 0, 0, 0, 0, 0, 0, 'Welcome home, $n.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57897, 0, 0, 0, 0, 0, 0, 0, 0, 'I see even now that your soul burns brighter than most.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57898, 0, 0, 0, 0, 0, 0, 0, 0, 'Your journey together is just beginning.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57899, 2, 0, 0, 0, 0, 0, 0, 0, 'I am Koros, head communicator of the hold. A pleasure.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57900, 1, 0, 0, 0, 0, 0, 0, 0, 'You see, my mortal friend, it truly is that simple!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57901, 1, 0, 0, 0, 0, 0, 0, 0, 'The others speak of you! Come! Let me know you too!', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57903, 1, 0, 0, 0, 0, 0, 0, 0, 'Good, anima is flowing again. You will want to return to the device periodically and channel more anima.\n\nAs you gather more anima and channel it into Bastion, the flows will grow stronger. Though they will not last for long right now, eventually you will be able to reinforce the channel, making a permanent connection.\n\nOnce, the skies of Bastion were alight with the flows of anima. With your help, they will be again.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57904, 1, 0, 0, 0, 0, 0, 0, 0, 'On behalf of our glorious Archon, may you follow the path always towards a brighter tomorrow.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57908, 669, 0, 0, 0, 0, 0, 0, 0, 'The forjalita\'s gratitude runs deeper than you think.$b$bWell. Now you must refocus on our goal.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57909, 1, 0, 0, 0, 0, 0, 0, 0, 'Facing so many in such a short time is an indisputable show of endurance.$b$bI got tired of just looking at you.$b$b<Pelagos laughs.>', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57912, 1, 0, 0, 0, 0, 0, 0, 0, 'I expect much of you, mortal. Help lead our frontal assault on the construct forces!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57931, 669, 669, 0, 0, 0, 300, 0, 0, 'This unit seems to be malfunctioning. We will have to perform some repairs.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57932, 1, 1, 0, 0, 0, 300, 0, 0, 'This is enough to repair Gatamatos, and then some!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57933, 1, 1, 0, 0, 0, 300, 0, 0, 'He may appear to be returned to perfect shape, but he must be re-tested and trained.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57934, 1, 1, 0, 0, 0, 300, 0, 0, 'Excellent. His combat routines are acceptable.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57935, 1, 1, 0, 0, 0, 300, 0, 0, 'I would like to see a larion track that accurately!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57936, 1, 1, 0, 0, 0, 300, 0, 0, 'Can be at peace, no wild larion to eat me. Hoo...', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57937, 1, 1, 0, 0, 0, 300, 0, 0, 'And with that, I believe that Gatamatos is once again functioning at standard.\n\nThank you for your aid.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57947, 0, 0, 0, 0, 0, 0, 0, 0, '¿Y esto? ¿Qué es esto?\n\n¿Te envió el Gran Árbol para ayudarnos? ¡Ayúdanos!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57948, 0, 0, 0, 0, 0, 0, 0, 0, '¡Los encontraste! ¡Encontraste a mis amigos! ¡Tienen sus formas!\n\nAhora solo nos queda una cosa por hacer. ¡La última!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57949, 0, 0, 0, 0, 0, 0, 0, 0, '¡Ladrones! ¡Se robaron el ánima! Corrieron como locos y…\n\nUn momento, eso es normal. Siempre se comportan así de mal. ¡Spriggans estúpidos!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57950, 0, 0, 0, 0, 0, 0, 0, 0, '¡Acabado! ¡El spriggan malo está acabado! La Cañada Espiritual será nuestra nuevamente.\n\nAhora, vayámonos. Debemos irnos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57951, 0, 0, 0, 0, 0, 0, 0, 0, 'De las bellotas crecen bosques, de las almas proviene el ánima vital. No importa su tamaño, de ellas dependen nuestros destinos.\n\nEl bosque suspira con gratitud.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57952, 0, 0, 0, 0, 0, 0, 0, 0, '¿Kota te ha pedido gres de gorm?\n\n<El maestro Sha\'lor brilla de entusiasmo.>\n\n¡Con gusto cumpliré con su pedido! Ven, te mostraré cómo es esto.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57976, 1, 0, 0, 0, 0, 0, 0, 0, 'That should strike fear into their stolen hearts.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57977, 669, 0, 0, 0, 0, 0, 0, 0, 'You have proven to be competent in this task. You have my gratitude.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57979, 0, 0, 0, 0, 0, 0, 0, 0, 'Your leadership is inspiring. These troops would follow you anywhere!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57982, 0, 0, 0, 0, 0, 0, 0, 0, 'It seems these kyrian are more stalwart than I expected. Let us avenge their fallen kin.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57983, 6, 0, 0, 0, 0, 0, 0, 0, 'Only four survivors... I know I should be grateful that there were any at all, but...\n\nWhat sort of monsters would do this to anyone, let alone to their own people?', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57984, 1, 0, 0, 0, 0, 0, 0, 0, 'It seems one of the house\'s barons is overseeing this operation... and he is unaware that we are free. \n\nWe have a chance to strike him down!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57985, 1, 0, 0, 0, 0, 0, 0, 0, 'Slowing the production of new constructs should earn our soldiers some reprieve, however brief.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57986, 6, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $p. Though they are lost, at least their remains will not be defiled.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57987, 1, 0, 0, 0, 0, 0, 0, 0, 'Complacency is every soldier\'s worst enemy. No one is safe in times of war, not even within the walls of their own fortress.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57993, 1, 0, 0, 0, 0, 0, 0, 0, 'I sense something within this construct... something familiar...', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (57994, 0, 0, 0, 0, 0, 0, 0, 0, 'We escaped!\n\nWe will recuperate here for now. At least until we find a way to return to Bastion.\n\nThank you for showing me what courage is, my friend.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58007, 1, 5, 0, 0, 0, 0, 0, 0, 'Nuestros enemigos se arrepentirán de habernos enfrentado.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58011, 5, 0, 0, 0, 0, 0, 0, 0, 'You found my venom sacs! What a helpful apprentice you are!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58016, 21, 0, 0, 0, 0, 0, 0, 0, 'Such marvelous spores, my apprentice!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58027, 5, 0, 0, 0, 0, 0, 0, 0, 'What a precious slime!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58031, 6, 0, 0, 0, 0, 0, 0, 0, 'Oh--and what are these?', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58036, 5, 0, 0, 0, 0, 0, 0, 0, 'You and your slime are bonding!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58037, 1, 0, 0, 0, 0, 0, 0, 0, 'My pride is generally wary of newcomers, but they seem to accept your presence.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58038, 1, 0, 0, 0, 0, 0, 0, 0, 'The larions will be ecstatic to have more antlers to chew on!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58039, 1, 0, 0, 0, 0, 0, 0, 0, 'Incredible! It seems Andira has taken a liking to you. Perhaps you would be interested in bonding with her further?', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58040, 669, 0, 0, 0, 0, 0, 0, 0, 'It is such a satisfying experience to see your larion take wing to help take down your prey.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58041, 669, 0, 0, 0, 0, 0, 0, 0, 'Providing for the pride is an important step on your path to earning your loyalty here.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58042, 0, 0, 0, 0, 0, 0, 0, 0, 'The feeling of soaring through the air, the gentle flap of your larion\'s wings...\n\nEven though I have my own wings, there\'s no feeling quite like it.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58045, 21, 0, 0, 0, 0, 0, 0, 0, 'Our slimes will be safe, my dear apprentice!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58060, 1, 0, 0, 0, 0, 0, 0, 0, 'А, тернорозы! Прекрасно. \n\nНе уходи так быстро. Есть разговор.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58062, 1, 0, 0, 0, 0, 0, 0, 0, 'Я надеялась, что ты рано или поздно заглянешь ко мне, пилигрим Утробы. \n\nС Драко мы больше не сотрудничаем. В обычных обстоятельствах мы бы и тебя наказали за попытку обойти наши правила. Однако твой статус... многое меняет.\n\nПредлагаю сделку. \n\nТы будешь приносить ценные вещи сразу мне. Никаких посредников. Никаких уловок. \n\nВзамен мы будем тебе платить и предоставим свои товары. Ну как, по рукам?', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58068, 0, 0, 0, 0, 0, 0, 0, 0, 'We would be so lost without your help. Really.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58088, 5, 0, 0, 0, 0, 0, 0, 0, 'I know the Minoire Junkyard sent you. Always use buffs to win the fight.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58090, 5, 0, 0, 0, 0, 0, 0, 0, 'The Minoire scrapyard will have to give up its place in the competition.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58103, 669, 0, 0, 0, 0, 0, 0, 0, 'It is done.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58174, 669, 0, 0, 0, 0, 0, 0, 0, '<Kleia smiles at you fondly.>$b$bDoes that mean you\'ll stay?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58175, 1, 0, 0, 0, 0, 0, 0, 0, '$n! Can you believe what the Forsworn have done to my forge?\n\nMust find Phaestus and halt their operation!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58176, 1, 0, 0, 0, 0, 0, 0, 0, 'You did it! Many forgehands return today. Maybe one day the rest return too?\n\nWe will help.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58177, 1, 0, 0, 0, 0, 0, 0, 0, 'We shall use this metal for many purposes. For the Crest, and for my goliath!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58178, 1, 0, 0, 0, 0, 0, 0, 0, 'A partner that embodies my inner strength.\n\nA worthy centurion indeed!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58179, 1, 0, 0, 0, 0, 0, 0, 0, 'Sika sent you to retrieve my tools?\n\nShe knows what I need to be productive.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58180, 1, 0, 0, 0, 0, 0, 0, 0, 'Phaestus returns! Glorious day.\n\nMany thanks, friend. This hammer has been passed down for eons! It belongs in my masterful hands.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58181, 1, 0, 0, 0, 0, 0, 0, 0, 'Perfection!\n\nIt is beautiful, yes? Not done yet, but much work led to great reward.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58182, 1, 0, 0, 0, 0, 0, 0, 0, 'Exhilarating! Bron grows stronger with your power.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58183, 1, 0, 0, 0, 0, 0, 0, 0, 'Mikanikos returned not long ago with his new centurion. Quite the unique unit.\n\nOf course, the return of Phaestus to kyrian hands is good news indeed. The Archon will be pleased.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58268, 0, 0, 0, 0, 0, 0, 0, 0, 'With that ridge clear, we\'ll be safe to advance.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58351, 1, 0, 0, 0, 0, 0, 0, 0, 'If they cannot properly evade detection, then they do not deserve to survive.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58431, 0, 0, 0, 0, 0, 0, 0, 0, 'We shall all share in this fabulous brew you have created!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58433, 1, 0, 0, 0, 0, 0, 0, 0, 'Bien hecho, mortal. Los petronato necesitan ánima para su existencia. Sin ella, vuelven a sus formas de piedra. Esta incursión les costó más que si hubiéramos destruido a treinta de sus rebeldes.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58473, 0, 0, 0, 0, 0, 0, 0, 0, 'Jamás pensé que volvería a Tirna Noch. Un lugar con tantos recuerdos... Tanto dolor...\n\nPero no es hora de sentimentalismos. Es hora de ponernos en acción.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58480, 0, 0, 0, 0, 0, 0, 0, 0, 'Los drust han robado más ánima de la que imaginaba. Pensar que nos han privado de todo este recurso en medio de una sequía...\n\nDebemos recuperar todo lo que podamos. No solo por el bien de las semillas silvestres, sino también para poner freno a las intenciones de los drust, sean cual fueren.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58483, 0, 0, 0, 0, 0, 0, 0, 0, 'Gracias.\n\nYa sé que estos son solo objetos, pero tienen un significado muy importante. A veces el simple hecho de tener un pedazo de tu propio pasado puede ser un gran consuelo.\n\nSignifica que aún no está todo perdido.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58484, 0, 0, 0, 0, 0, 0, 0, 0, 'Esta ánima ayudará a Tejesueños a completar su ritual... y quizás también salve a los espíritus que de otro modo se habrían perdido.\n\nBuen trabajo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58486, 0, 0, 0, 0, 0, 0, 0, 0, 'Llegamos al corazón. Bien hecho.\n\nSi todos los drust son así, Ardenweald está en una situación muy complicada.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58488, 0, 0, 0, 0, 0, 0, 0, 0, 'Ahora que tenemos el corazón, debemos encontrar una forma de salir de aquí.\n\nYa oigo el sonido inconfundible de los enemigos que se acercan...', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58524, 0, 0, 0, 0, 0, 0, 0, 0, '¿Encontraste su ánima y la tomaste del corazón de la arboleda? Impresionante.\n\nA Tejesueños le encantará la noticia. Pero anticipo que pronto tendremos algunos visitantes disconformes.\n\nDebemos apresurarnos para ayudar al espíritu de esta semilla silvestre... antes de que nos veamos sobrepasados.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58557, 1, 0, 0, 0, 0, 0, 0, 0, 'I am truly sorry I do not have more answers... the Maw scattered my mind quite a bit.\n\nI will help to the best of my ability.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58558, 1, 0, 0, 0, 0, 0, 0, 0, 'Three houses that find themselves in the Countess\'s favor... Peculiar.\n\nSounds like you are going to explore the Redelav District!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58559, 1, 0, 0, 0, 0, 0, 0, 0, 'This city makes my skin crawl. I feel like I am constantly being watched, that people are whispering behind my back.\n\nTaking out the stoneborn leaders should slow the flow of information back to Denathrius\'s loyalists.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58560, 1, 0, 0, 0, 0, 0, 0, 0, '<Pelagos sifts through the clothing you brought him.>\n\nSuch fine fabric... I believe this could work!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58561, 1, 0, 0, 0, 0, 0, 0, 0, 'I am sure you caused quite a ruckus amongst the nobility. We must work quickly.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58562, 1, 0, 0, 0, 0, 0, 0, 0, '<The Curator sifts through the petals.>\n\nIt may be a stretch, but I believe I can make this work.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58563, 669, 0, 0, 0, 0, 0, 0, 0, 'So much power in one stone.\n\nYour past can be used against you. This is why we free ourselves.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58564, 1, 0, 0, 0, 0, 0, 0, 0, 'I shall tell you the story of the Seal of Contrition.\n\nThen you are to leave. Immediately.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58565, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, $n, welcome back to Revendreth!\n\nAllow me to connect you with someone who may be of service.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58566, 0, 0, 0, 0, 0, 0, 0, 0, '<Alwin looks to you, then to Kleia.\n\nHe nods, resolve in his eyes.>', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58567, 1, 0, 0, 0, 0, 0, 0, 0, 'When people fall to their vices, they must be judged accordingly.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58568, 1, 0, 0, 0, 0, 0, 0, 0, 'It is my duty to help those in pain. This salve shall help.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58569, 1, 0, 0, 0, 0, 0, 0, 0, 'Could it really be? The object of our long search...', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58570, 1, 0, 0, 0, 0, 0, 0, 0, 'Concern about Kalisthene\'s report... but not my problem! Seal should work perfectly.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58571, 1, 1, 0, 0, 0, 300, 0, 0, '<You see the Seal of Contrition flash with power as you pull your hand away from it.\n\nOne singular item, connecting the denizens of Bastion and Revendreth in more ways than the obvious.>', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58575, 0, 0, 0, 0, 0, 0, 0, 0, 'Estas armas tendrán un buen fin. Ojalá que no tengamos que volver a buscar más.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58589, 0, 0, 0, 0, 0, 0, 0, 0, 'Tienes un talento natural para estas cosas. Ahora podemos extraer las pesadillas del espíritu y lidiar con ellas directamente.\n\nSi lo hacemos bien, estará mucho más tranquila.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58590, 0, 0, 0, 0, 0, 0, 0, 0, 'Todos empezamos por algún lado. A veces, en lugares inesperados. Yo simplemente surgí de la tierra, como una flor.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58591, 0, 0, 0, 0, 0, 0, 0, 0, 'Este es recién el primero de sus ataques. Pero igual es una derrota de la que les costará recuperarse. Gracias.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58592, 0, 0, 0, 0, 0, 0, 0, 0, '¡Esos drust sí que son crueles con nosotros, los feéricos! Esos protectores de espíritus solo estaban cumpliendo con su trabajo…\n\nGracias por ayudarlos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58593, 0, 0, 0, 0, 0, 0, 0, 0, 'Qué final terrible para un espíritu tan majestuoso… \n\n¡Fue por eso que conectó contigo! ¡Porque provienen del mismo mundo! ¡Azeroth!\n\nPor fin siento cómo se esfuman sus pesadillas. Ahora al menos va a tener un poco de paz.\n\nGracias, $gamigo mío:amiga mía;.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58616, 1, 0, 0, 0, 0, 0, 0, 0, 'This is going to take a bit of work.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58617, 0, 0, 0, 0, 0, 0, 0, 0, 'That should ensure we stand ready for war.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58618, 0, 0, 0, 0, 0, 0, 0, 0, 'Good! I knew that beast would prove no challenge for you.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58654, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, ¿así que los campesinos pidieron ayuda? \n\nTodo el que sea leal sabe que los sacrificios son indispensables para la supervivencia de Revendreth. Sí, esta es una época oscura, con tanta sequía de ánima y almas nuevas.\n\nPero todos debemos cumplir con nuestra parte, sin importar cuánto nos duela.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58714, 0, 0, 0, 0, 0, 0, 0, 0, '¡Estos drust se creen audaces al enviar espías a mi arboleda! Son muy audaces... y muy estúpidos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58719, 0, 0, 0, 0, 0, 0, 0, 0, 'Los Cazadores salvajes acuden en nuestra ayuda... ¿salvo desde el oeste?\n\nAhí es donde la capitana de los Cazadores Korayn perseguía a un enemigo peligroso. Puede que algo la haya demorado a ella y a su grupo de caza.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58720, 0, 0, 0, 0, 0, 0, 0, 0, 'Estos drust creyeron que podían enviar fuerzas para sorprendernos por la retaguardia. \n\n<Korayn sonríe con suficiencia.>\n\nTu llamado atrajo a mis hermanos y hermanas cazadores para reforzar nuestras filas. Y los sorprendidos fueron nuestros enemigos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58721, 0, 0, 0, 0, 0, 0, 0, 0, '<La Reina del invierno gira la cabeza para mirarte.>\n\nEs curioso ver a alguien como tú en mi bosque.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58723, 0, 0, 0, 0, 0, 0, 0, 0, 'El Desterrado comienza a moverse y el poder de Ardenweald se marchita con la sequía.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58724, 0, 0, 0, 0, 0, 0, 0, 0, '¿Qué ha sucedido en Ardenweald? Cuéntamelo todo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58726, 1, 0, 0, 0, 0, 0, 0, 0, 'You work quickly, don\'t you? I could use more assistants like you.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58751, 6, 0, 0, 0, 0, 0, 0, 0, 'Be thankful I am the one you found here.\n\nOthers in Maldraxxus may not have seen the potential in your strength.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58771, 1, 0, 0, 0, 0, 0, 0, 0, 'You found it! And you also bring other interesting tomes. When we\'re done, I\'ll take care of properly registering them.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58775, 1, 0, 0, 0, 0, 0, 0, 0, 'We have lots of work ahead of us.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58787, 669, 0, 0, 0, 0, 0, 0, 0, 'The Forsworn have taken the court. They are blocking us from our trial!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58789, 1, 0, 0, 0, 0, 0, 0, 0, 'The sting of aspirants choosing to walk against the Archon\'s will is a feeling that will never be soothed.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58792, 1, 0, 0, 0, 0, 0, 0, 0, 'I... need some time to process this.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58793, 1, 0, 0, 0, 0, 0, 0, 0, 'I am glad that Kleia has returned successfully to us. Though she questions, she is resolute in her duties.\n\nIf only I could say the same for all of the aspirants.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58795, 669, 0, 0, 0, 0, 0, 0, 0, 'The Forsworn, now?\n\nThis is an all-out attack!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58796, 409, 0, 0, 0, 0, 0, 0, 0, 'And so the tides of battle turn.\n\nElysian Hold may be safe, but the Spires still need our aid!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58797, 1, 0, 0, 0, 0, 0, 0, 0, 'You, me, and our dear Pelagos.\n\nI challenge anybody to name a better team.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58798, 1, 0, 0, 0, 0, 0, 0, 0, 'This attack has taught us much. The Archon shall be convening with the remaining Paragons to discuss this Forsworn threat.\n\nWe shall call upon you once more when you are needed.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58799, 1, 0, 0, 0, 0, 0, 0, 0, 'Excellent. I\'ll put your spirits to good use.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58800, 1, 0, 0, 0, 0, 0, 0, 0, 'I love the sound that anima makes. I haven\'t heard it in abundance for a long time.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58816, 273, 0, 0, 0, 0, 0, 0, 0, 'Mission accomplished! This will be so funny. Ah, sweet revenge.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58820, 574, 0, 0, 0, 0, 0, 0, 0, 'Siento su poder. Quizá nos den la ventaja que necesitamos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58821, 1, 0, 0, 0, 0, 0, 0, 0, 'A simple task for such a storied hero.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58832, 1, 0, 0, 0, 0, 0, 0, 0, 'Nasty spriggan!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58843, 1, 0, 0, 0, 0, 0, 0, 0, 'Nice to meet you. La Bóveda has not received visitors for a long time.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58846, 1, 0, 0, 0, 0, 0, 0, 0, 'The day is ours at last, thanks to you.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58847, 1, 0, 0, 0, 0, 0, 0, 0, 'May Thenios\'s efforts be fruitful. I have never seen a soul so broken in all my eons.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58854, 1, 0, 0, 0, 0, 0, 0, 0, 'Now that the Paragons stand ready, we must rally the rest of our forces.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58856, 273, 0, 0, 0, 0, 0, 0, 0, 'We found him!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58857, 273, 0, 0, 0, 0, 0, 0, 0, 'Okay, I think everything is finally ready, $n.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58869, 0, 0, 0, 0, 0, 0, 0, 0, 'Hemos ganado la batalla...\n\n... Pero temo que hayamos perdido a Ysera.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58900, 0, 0, 0, 0, 0, 0, 0, 0, 'Has viajado desde muy lejos para este momento.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58905, 6, 0, 0, 0, 0, 0, 0, 0, 'Whoops. Wrong seeds. I hope we can still find a use for these.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58908, 1, 0, 0, 0, 0, 0, 0, 0, 'So you are the newcomer that everyone talks about?$b$bMy name is Curly. Nice to meet you, $n!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58909, 0, 0, 0, 0, 0, 0, 0, 0, 'Now that you\'ve learned the basics, you can come back at any time to master more advanced techniques or buy more mounts!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58911, 1, 0, 0, 0, 0, 0, 0, 0, 'As long as The Drunk Pig is your home, you can always come back here after your adventures!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58912, 1, 0, 0, 0, 0, 0, 0, 0, 'Excellent, you arrived. King Wrynn and the others have been waiting for you.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58914, 0, 0, 0, 0, 0, 0, 0, 0, '', 36839);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58915, 6, 0, 0, 0, 0, 0, 0, 0, '', 36839);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58916, 1, 0, 0, 0, 0, 0, 0, 0, 'Esta alma está casi lista. \n\nTenemos una oportunidad para terminar de prepararla y, mientras tanto, encargarnos de un problema.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58923, 0, 0, 0, 0, 0, 0, 0, 0, 'Those who serve the Light will know its blessing. Accept this gift, Paladin.$b$bCall upon her when you have no more strength and when all hope seems lost. The Light will protect you from danger.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58931, 1, 0, 0, 0, 0, 0, 0, 0, 'Bien. Temel sabrá cómo aprovechar esta piedra de pecados.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58932, 1, 0, 0, 0, 0, 0, 0, 0, '<Isquelión observa a Temel con desconfianza.>\n\nNunca me agradó este sujeto.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58937, 1, 0, 0, 0, 0, 0, 0, 0, 'It will be hard for them to return.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58938, 273, 0, 0, 0, 0, 0, 0, 0, 'These seeds aren\'t just useful, they\'re dangerous!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58939, 1, 0, 0, 0, 0, 0, 0, 0, 'The seed!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58941, 1, 0, 0, 0, 0, 0, 0, 0, 'Ahora que Terror Crepuscular ha sido capturada, controlamos un poco más estas tierras.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58946, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice work, $n. Whenever you encounter this vile magic, dispel it.$b$bI have fulfilled my task. The Light calls me.$b$bGood luck, and remember that the warm embrace of the Light is always with you.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58947, 0, 0, 0, 0, 0, 0, 0, 0, '¡Has demostrado tu valía! ¡Veré si podemos enviarte a una lucha pronto!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58962, 0, 0, 0, 0, 0, 0, 0, 0, 'Se ha cerrado nuestro trato.\n\nLlámame desde el Vacío y responderé tu llamado.\n\n<El abisario mira el cadáver del huargo.>\n\nVeamos si eres más fuerte que los anteriores.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58976, 1, 0, 0, 0, 0, 0, 0, 0, '<Mikanikos gestures irritably at the Rejected behind him.>$b$bAre you here to fix THIS?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58977, 669, 0, 0, 0, 0, 0, 0, 0, 'We did it. Hopefully, we will have your memories at our fingertips.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58978, 0, 0, 0, 0, 0, 0, 0, 0, '<Mikanikos snorts softly.>', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58979, 1, 0, 0, 0, 0, 0, 0, 0, 'I hope you can excuse my behavior. I felt that I was not in my right mind.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58980, 1, 0, 0, 0, 0, 0, 0, 0, 'Your search was fruitful. I am delighted to see that you are intact.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58983, 1, 0, 0, 0, 0, 0, 0, 0, 'The Horde has left us with no choice once again.$b$bThis cannot stay that way.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58995, 274, 0, 0, 0, 0, 0, 0, 0, 'She\'s... she\'s gone.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58996, 1, 0, 0, 0, 0, 0, 0, 0, 'Так они потерпели неудачу? Но ты, я смотрю, $gвернулся:вернулась;.\n\nКакой позор.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (58997, 1, 0, 0, 0, 0, 0, 0, 0, 'You have brought the vessel we require for the completion of the Crest of Ascension. I understand it came at a great cost.\n\nWe must endeavor to repay our friends in Ardenweald one day.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59009, 274, 0, 0, 0, 0, 0, 0, 0, 'It is good to have allies. Even ones so stubborn.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59011, 0, 0, 0, 0, 0, 0, 0, 0, 'We are struggling to hold the line. We need to act fast.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59014, 1, 0, 0, 0, 0, 0, 0, 0, '¿Comenzamos?', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59021, 1, 0, 0, 0, 0, 0, 0, 0, 'La protección de la piedra de pecados de la Acusadora está debilitada debido a la muerte de la inquisidora.\n^Pero los dos inquisidores deben morir para que esta protección desaparezca. Después, podremos tomar la piedra de pecados de la Acusadora.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59023, 1, 0, 0, 0, 0, 0, 0, 0, 'Lord Chambelán confirmó la muerte. Debemos apurarnos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59025, 1, 0, 0, 0, 0, 0, 0, 0, 'Cursed lich--may his soul never be reborn.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59130, 1, 0, 0, 0, 0, 0, 0, 0, 'My apprentice! I have been searching everywhere for you!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59147, 669, 0, 0, 0, 0, 0, 0, 0, 'Some stains need to be removed.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59171, 574, 0, 0, 0, 0, 0, 0, 0, 'Yes, these will do nicely.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59172, 1, 0, 0, 0, 0, 0, 0, 0, 'Draka may be content to forget our master, but I will not rest until he is avenged.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59185, 1, 0, 0, 0, 0, 0, 0, 0, 'He will not escape this time.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59188, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes... They will be made to know our pain.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59190, 1, 0, 0, 0, 0, 0, 0, 0, 'Vengeance is nearly ours.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59196, 1, 0, 0, 0, 0, 0, 0, 0, 'The polemarch? He is the Archon\'s right hand!$b$bIt seems like he really believes in us. There is still hope!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59197, 1, 0, 0, 0, 0, 0, 0, 0, 'If Kalisthene allowed you to use the lighthouse, it is because he trusts you. For that reason, you have my attention.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59198, 0, 0, 0, 0, 0, 0, 0, 0, 'The Archon\'s chambers are upstairs. I must ask you to maintain the utmost decorum in his presence.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59199, 1, 0, 0, 0, 0, 0, 0, 0, 'May the Archon\'s grace be with you, mortal.', 38134);
