INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59210, 273, 0, 0, 0, 0, 0, 0, 0, 'Well executed.', 37474);
