INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59231, 6, 0, 0, 0, 0, 0, 0, 0, 'I see you\'ve made a new ally... of a sort. And another rune has been empowered.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59232, 1, 0, 0, 0, 0, 0, 0, 0, 'Soy el ejemplo de la misericordia y la comprensión.\n\nLa Acusadora tendrá todas las oportunidades de corregir sus errores y aprender de ellos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59254, 0, 0, 0, 0, 0, 0, 0, 0, '', 36839);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59256, 1, 0, 0, 0, 0, 0, 0, 0, 'Este medallón nos será muy útil. Pero no tenemos a nuestro líder para que lo use.\n\nDebemos buscar más aliados.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59265, 1, 0, 0, 0, 0, 0, 0, 0, 'Veo cobardía en estos ojos... Has hecho bien.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59327, 1, 0, 0, 0, 0, 0, 0, 0, 'Te arrancaré la cabeza del cuerpo si...\n\nEspera...\n\n¿Aún estás $gvivo:viva;? Eso quiere decir que... ¿eres $gel:la; Surcafauces?', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59426, 1, 0, 0, 0, 0, 0, 0, 0, 'Remember that if you need help, your assistant can provide you with various services.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59430, 1, 0, 0, 0, 0, 0, 0, 0, 'Working together will benefit both of us greatly!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59520, 1, 0, 0, 0, 0, 0, 0, 0, '¡Trabajar juntos nos beneficiará mutuamente!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59554, 1, 0, 0, 0, 0, 0, 0, 0, 'What brings you to our humble territory?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59555, 1, 0, 0, 0, 0, 0, 0, 0, '¡El enemigo se lame las heridas! Nuestras tropas resistirán por ahora, pero debemos estar preparados para más ataques.', 38556);
