INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59603, 1, 0, 0, 0, 0, 0, 0, 0, '¡Pareciera que tú $gmismo:misma; hubieses comandado la batalla en persona! Un trabajo impresionante, debo decir.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59609, 1, 0, 0, 0, 0, 0, 0, 0, 'Espero que te hayas instalado... No hay descanso para los exhaustos en Maldraxxus.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59616, 6, 0, 0, 0, 0, 0, 0, 0, 'Your voice... you are not one of the Maldraxxi?', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59625, 0, 0, 0, 0, 0, 0, 0, 0, 'Estábamos esperando tu llegada. Necesitamos resultados.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59641, 669, 0, 0, 0, 0, 0, 0, 0, 'Back home after all this time.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59645, 0, 0, 0, 0, 0, 0, 0, 0, 'Se ha cumplido nuestra voluntad.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59648, 0, 0, 0, 0, 0, 0, 0, 0, 'Esto es solo el comienzo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59650, 1, 0, 0, 0, 0, 0, 0, 0, 'Esto es solo el principio para enmendar las cosas.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59653, 0, 0, 0, 0, 0, 0, 0, 0, 'Solo puedo acercarte hasta aquí. Haz que cuente.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59659, 0, 0, 0, 0, 0, 0, 0, 0, 'Ahora estamos listos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59674, 669, 0, 0, 0, 0, 0, 0, 0, 'I am honored that you came to me first. Allow me to show you my ways.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59678, 66, 0, 0, 0, 0, 0, 0, 0, 'Ya no pueden sustentarse. Buen trabajo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59698, 0, 0, 0, 0, 0, 0, 0, 0, '¡Por Valor!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59700, 0, 0, 0, 0, 0, 0, 0, 0, 'Que su recuerdo se esfume de este lugar.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59709, 0, 0, 0, 0, 0, 0, 0, 0, 'Espero que nuestros caminos vuelvan a cruzarse.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59710, 6, 1, 0, 0, 0, 0, 0, 0, 'Ты вовремя, нечего сказать. Надсмотрщик только что ушел.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59712, 1, 0, 0, 0, 0, 0, 0, 0, 'Большое спасибо за помощь, $gдруг:подруга;. Продолжай в том же духе – я буду в долгу перед тобой.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59722, 0, 0, 0, 0, 0, 0, 0, 0, 'Es como si nunca nos hubiéramos ido.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59748, 1, 0, 0, 0, 0, 0, 0, 0, 'Ven. Tengo mucho que mostrarte.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59750, 0, 0, 0, 0, 0, 0, 0, 0, 'Buen trabajo. Te estás haciendo $gconocido:conocida;. ¡Escucho a la gente hablar de cómo mataste a todos esos colmisangres!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59751, 6, 1, 0, 0, 0, 0, 0, 0, 'It is not a perfect arrival, but at least we are whole.$b$bI have seen no sign of Tyrande. It must have appeared elsewhere or it continued without us. We should be on the alert.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59752, 1, 25, 0, 0, 0, 0, 0, 0, 'We have been in this place for a short time, but our strength is already exhausted. They will have to be enough.$b$bTake this horn. You can use it to summon the rest of the forces of the Sword of Ebano and ask them to face your enemies.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59753, 1, 0, 0, 0, 0, 0, 0, 0, 'I refuse to abandon some of our knights, but it is obvious that we cannot withstand such imposing enemies.$b$bOur duty is to ensure that your sacrifice is not in vain.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59754, 5, 0, 0, 0, 0, 0, 0, 0, 'Those images will keep our enemies distracted for a time, but they will not give up the chase so easily.$b$bWe must hurry!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59755, 1, 0, 0, 0, 0, 0, 0, 0, 'Rest here while you can, $n.$b$bI will tell you everything I know.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59756, 1, 6, 0, 0, 0, 0, 0, 0, 'I need time to recover from that battle. Thrall also needs to rest.$b$bIn the meantime, maybe you and Mograine can search for answers.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59757, 5, 6, 0, 0, 0, 0, 0, 0, 'It is useless! Those souls are too ruined to give us anything useful.$b$bWait ... what is that?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59758, 1, 25, 0, 0, 0, 0, 0, 0, 'If you find enough mouths, at least one will speak.$b$bLet\'s take this thing back to the cave and find out what it knows.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59759, 1, 0, 0, 0, 0, 0, 0, 0, 'I knew in my heart that the Light would not abandon us.$b$bYour presence here is proof enough.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59760, 1, 0, 0, 0, 0, 0, 0, 0, 'He had almost forgotten what freedom feels like.$b$bNow, it\'s time to take the fight to those evildoers.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59761, 1, 0, 0, 0, 0, 0, 0, 0, 'I knew I could trust you for this task.$b$bAlthough adversity seems insurmountable, we have to try to do the right thing.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59762, 5, 0, 0, 0, 0, 0, 0, 0, 'The Jailer has Baine!$b$bThis is worse than we feared ...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59765, 1, 0, 0, 0, 0, 0, 0, 0, 'We were lucky to find the wizard who poisoned Baine\'s spirit.$b$bI will need your help to break his link with the big boss.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59766, 1, 0, 0, 0, 0, 0, 0, 0, 'It doesn\'t have the balance or grace of a good Frostwolf ax, but I\'ll have to settle.$b$bOn going.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59767, 5, 0, 0, 0, 0, 0, 0, 0, 'We did it!$b$bIf we activate this path stone, maybe we can escape from this place!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59770, 0, 0, 0, 0, 0, 0, 0, 0, '<The path stone seems to be waiting for something.$b$bYou reach out to touch its surface ...>', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59773, 273, 0, 0, 0, 0, 0, 0, 0, 'The way is open again! Praised be the Archon!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59774, 273, 0, 0, 0, 0, 0, 0, 0, 'The Hostess will answer all your questions very soon. After all, that is its purpose.$b$b<Kleia smiles at you.>$b$bHope you\'re excited. I\'m really am excited!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59776, 1, 0, 0, 0, 0, 0, 0, 0, 'I cannot say that I enjoyed wearing the Helm of Domination. The magic it contains is ... disturbing to the dead.$b$bI return it with pleasure.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59781, 0, 0, 0, 0, 0, 0, 0, 0, 'Es una pena lo de los otros, ¡pero tú sigues aquí!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59800, 0, 0, 0, 0, 0, 0, 0, 0, 'Gracias por haberles levantado el ánimo a los combatientes. Sin dudas recordarán que los ayudaste... si sobreviven.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59826, 0, 0, 0, 0, 0, 0, 0, 0, 'Tu desempeño me ha dado una buena ganancia... Zo\'umlo apostó mal.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59837, 1, 0, 0, 0, 0, 0, 0, 0, 'Competir en la arena es solo una de las formas de contribuir con este proyecto. Hay muchas otras cosas para hacer.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59846, 1, 0, 0, 0, 0, 0, 0, 0, 'Жаль, что все они не мои. Но все равно пригодятся.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59874, 1, 0, 0, 0, 0, 0, 0, 0, 'What news from Maldraxxus, $n?\n\n<Bolvar listens to your tale.>', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59879, 0, 0, 0, 0, 0, 0, 0, 0, 'Debes ser $guno:una; de $gesos forasteros:esas forasteras; de $glos:las; que tanto oí hablar.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59894, 0, 0, 0, 0, 0, 0, 0, 0, 'Tenemos una alianza. Ahora hay que planificar el ataque.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59897, 0, 0, 0, 0, 0, 0, 0, 0, '<The area is quiet. There is no sign of your allies.>', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59907, 1, 0, 0, 0, 0, 0, 0, 0, 'Our enemy is relentless, but we will not let him advance.$b$bStay and pay attention, $c.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59914, 5, 6, 0, 0, 0, 0, 0, 0, 'Praise the Light! Almost ... I almost lost hope.$b$b<Jaina looks at the Knights of the Sword of Ebano.>$b$bIs this ... all that remains of your strength?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59915, 1, 0, 0, 0, 0, 0, 0, 0, 'It seems your search was a success.$b$bEnough rest. There is too much to do.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59919, 1, 0, 0, 0, 0, 0, 0, 0, 'Never in all my millennia did I think I would see the day a steward would soulbind with an outsider. Times are certainly changing, are they not?', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59920, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'m really greatfull for your help. Now I can practice my trade again!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59921, 1, 0, 0, 0, 0, 0, 0, 0, '¿Te ha iluminado esta aventura?', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59922, 1, 0, 0, 0, 0, 0, 0, 0, 'Hemos demostrado nuestro honor... y conseguimos un nuevo aliado.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59929, 0, 0, 0, 0, 0, 0, 0, 0, 'Excelente trabajo. Ayudemos a que los sobrevivientes se recuperen.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59930, 0, 0, 0, 0, 0, 0, 0, 0, 'Ya estamos recuperados. Es hora de movernos. La expedición perdida está en algún lugar de esta isla.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59931, 0, 0, 0, 0, 0, 0, 0, 0, 'Tengo tanta... hambre...\n\nSe llevaron... a todos lo\' demá\'... Yo soy... la última...', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59932, 0, 0, 0, 0, 0, 0, 0, 0, '<Won\'sa come la comida que cocinaste con rapidez.>\n\nParece que, despué\' de todo, no golpearé las puerta\' de Bwonsamdi.\n\nSerá mejor que se apuren... ¡Los jabaespine\' se llevaron a Shuja y al reh\'to de la expedición!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59933, 0, 0, 0, 0, 0, 0, 0, 0, '¡Ya estás $glisto:lista; para asesinar a los jabaespines! Hablemos con Won\'sa para averiguar qué sabe.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59935, 0, 0, 0, 0, 0, 0, 0, 0, 'Tengo algunas noticias buenas... y otras muy desafortunadas.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59938, 4, 11, 0, 0, 0, 0, 0, 0, '¡Es evidente que tienes agallas! Y eso lo respeto. Me uní a esta expedición porque creí que sería una manera rápida y fácil de ganar algo de dinero.\n\nPero ahora, con todo esto de la nigromancia y los ogros locos... ¡Quizá consiga ganar MUCHO dinero!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59939, 66, 0, 0, 0, 0, 0, 0, 0, 'Buen trabajo. Parece que los jabaespines se están manteniendo a cierta distancia. Quizás han aprendido su lección.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59940, 4, 0, 0, 0, 0, 0, 0, 0, 'Muy bien, ¡es hora de romper un par de reglas y crear un jabalí gigante!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59941, 397, 0, 0, 0, 0, 0, 0, 0, '¿Así que lo del jabalí gigante era algo imposible y convertiste el dispositivo en un arma?\n\nBuen trabajo. Veamos si puedes encargarte de esto.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59942, 66, 0, 0, 0, 0, 0, 0, 0, 'Gracias, $Gextraño:extraña;. Le fallé a la Horda, no fui lo suficientemente fuerte para detener esta amenaza por mi cuenta.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59943, 2, 0, 0, 0, 0, 0, 0, 0, 'Encontré al miembro perdido de la expedición... pero tenemos muchos problemas por resolver primero.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59944, 4, 11, 0, 0, 0, 0, 0, 0, '¡Gracias por salvarme! Deben estar desesperados por hacer los sacrificios si querían usar a alguien que ya está muerto.\n\nSi hay alguien que drenará mi esencia de vida para realizar un ritual prohibido, ¡ese seré yo!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59945, 1, 0, 0, 0, 0, 0, 0, 0, 'Parece que las arpías están huyendo a su Percha. Eso nos dará tiempo para lidiar con los ogros.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59946, 2, 0, 0, 0, 0, 0, 0, 0, 'Ahora que sus tótems están destruidos, las arpías se debilitan. Esperemos que entiendan que no hay que desafiar a la Horda.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59947, 0, 0, 0, 0, 0, 0, 0, 0, 'Es bueno ver que todos han vuelto sanos y salvos. ¡Son la prueba viviente de la fuerza de voluntad de la Horda!\n\nEsperemos que Estallalumbra sea tan fuerte como volátil.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59948, 1, 6, 25, 0, 0, 0, 0, 0, 'Un foso gigante repleto de energía necrótica y arañas.\n\nAntes odiaba a las arañas. Ahora que soy renegado, no las veo como seres tan malos.\n\nPero te querrán asesinar, de eso no hay dudas.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59949, 0, 0, 0, 0, 0, 0, 0, 0, 'Crenna Hija de la Tierra será fundamental para nuestra causa. Su conexión con la naturaleza nos ayudará a entender qué le hicieron los ogros a esta tierra.\n\nBuen trabajo, recluta.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59950, 0, 0, 0, 0, 0, 0, 0, 0, 'Espero que tengas lo que necesitas.\n\nThrog encontró esta bolsa cerca y creyó que podría serte útil.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59959, 1, 0, 0, 0, 0, 0, 0, 0, 'My, my. Today certainly has been full of surprises!\n\nNow, mortal, I\'m curious what has brought you back to the Maw.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59960, 0, 0, 0, 0, 0, 0, 0, 0, '<Darion is trapped within the cage. He appears unconscious.>', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59962, 0, 0, 0, 0, 0, 0, 0, 0, 'Keep up. We have little time.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59966, 0, 0, 0, 0, 0, 0, 0, 0, 'My father... Alexandros Mograine. The Ashbringer.\n\nI... never expected to see him again.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59967, 0, 0, 0, 0, 0, 0, 0, 0, 'Este es el secreto para fabricar venenos. Me llevó mucho tiempo descubrirlo.\n\nAhora que sabes cómo crearlos, ¡podrás matar a ese ogro por mí!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59968, 0, 0, 0, 0, 0, 0, 0, 0, 'Drizza Traidora murió con honor. Será recordada como una heroína de la Horda.\n\nY ahora tú sabes cómo preparar veneno. Trata de... mantenerlo lejos de mí.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59972, 6, 0, 0, 0, 0, 0, 0, 0, 'Você parece mais forte! Se todos os fantasmas fossem como aquele...', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59973, 1, 0, 0, 0, 0, 0, 0, 0, 'I cannot thank you enough, $n.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59974, 0, 0, 0, 0, 0, 0, 0, 0, 'I cannot allow this wound to fester any longer. I will burn it closed here and now!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59975, 0, 0, 0, 0, 0, 0, 0, 0, 'Nuestro destino es la Ciudadela Mazoscuro.\n\nEl plan de Herbert es... poco convencional.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59978, 669, 0, 0, 0, 0, 0, 0, 0, 'Puede que hayamos encontrado la forma de salvar al señor de la guerra Pasotrueno.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59979, 1, 0, 0, 0, 0, 0, 0, 0, 'Eso debilitará a los ogros el tiempo suficiente para que podamos frustrar sus planes.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59980, 6, 11, 0, 0, 0, 0, 0, 0, '¡Buen trabajo! Por casualidad no te sobró ningún explosivo, ¿verdad?\n\n… Lo imaginé. ¡En fin! Tengo muchos otros modos de hacer estallar a mis enemigos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59981, 2, 5, 0, 0, 0, 0, 0, 0, 'Gracias por tu ayuda, pero haberme salvado no significa que podremos detener a los ogros.\n\n¡Ya tienen suficiente esencia de vida para completar el ritual!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59984, 0, 0, 0, 0, 0, 0, 0, 0, '¡Victoria para la Horda! Ha llegado el momento de que regresemos a Orgrimmar como héroes y conquistadores.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (59985, 0, 0, 0, 0, 0, 0, 0, 0, 'Es bueno volver a Orgrimmar.\n\nDebo informar de mi regreso a la jefa de guerra, pero sé qué puedes hacer tú mientras tanto...', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60005, 669, 0, 0, 0, 0, 0, 0, 0, 'Not exactly the army I expected, but I appreciate your sword being on my side.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60006, 669, 0, 0, 0, 0, 0, 0, 0, 'We have recovered a base, but there is still much to do.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60007, 0, 0, 0, 0, 0, 0, 0, 0, 'How fast! Very good, very good work!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60008, 0, 0, 0, 0, 0, 0, 0, 0, 'You fight with the ease of a true Kyrian, Outsider.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60009, 0, 0, 0, 0, 0, 0, 0, 0, 'You are a true ally, outsider. Thank you.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60013, 669, 0, 0, 0, 0, 0, 0, 0, 'We must hurry. The Archon must know what happened here.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60020, 0, 0, 0, 0, 0, 0, 0, 0, 'Still walking on two legs? My hopes for you are rising.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60021, 1, 0, 0, 0, 0, 0, 0, 0, 'Good, the crowd\'s on your side.\n\nNow there\'s just one last test.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60052, 669, 0, 0, 0, 0, 0, 0, 0, 'The resonator is operating at full capacity and our enemies have been eliminated. Excellent.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60053, 0, 0, 0, 0, 0, 0, 0, 0, 'Not fully loaded, but this will do! You may need to disable some security measures ...', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60054, 669, 0, 0, 0, 0, 0, 0, 0, 'We just have to move on, stranger.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60055, 1, 0, 0, 0, 0, 0, 0, 0, 'Thanikos? he didn\'t survive? did he?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60056, 1, 0, 0, 0, 0, 0, 0, 0, 'I know why you came. This Kyrian has explained a great deal.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60057, 669, 0, 0, 0, 0, 0, 0, 0, 'Word has spread throughout Bastion of your actions at the Temple of Courage, $n. Xandria sent word that you were to be amply rewarded for your heroism.', 36839);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60112, 0, 0, 0, 0, 0, 0, 0, 0, 'Debería estar aquí… pero no está.', 38556);
