INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60136, 1, 1, 0, 0, 0, 0, 0, 0, 'Y bien, Surcafauces, ¿qué estás esperando?', 38556);
