INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60149, 1, 1, 0, 0, 0, 0, 0, 0, 'Perhaps we will find common cause in the Purpose. He has sent you to us.', 38134);
