INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60176, 0, 0, 0, 0, 0, 0, 0, 0, 'Не думала, что такое возможно. Очень впечатляет.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60177, 0, 0, 0, 0, 0, 0, 0, 0, 'Интересную ты $gоткопал:откопала; информацию, ничего не скажешь.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60178, 0, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, что в конце концов все устроилось. Спасибо за невольную помощь.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60179, 1, 0, 0, 0, 0, 0, 0, 0, 'Many strive for greatness in Maldraxxus. Few truly achieve infamy.\n\nLet us see if our names will one day be remembered.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60180, 1, 0, 0, 0, 0, 0, 0, 0, 'I hope it has been a satisfactory experience.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60181, 0, 0, 0, 0, 0, 0, 0, 0, 'A threat removed for the House of the Chosen, and new recruits that show potential.\n\nGood work.', 37474);
