INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60274, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, Ta\'rela deve ter mandado você. Precisamos dos seus serviços.\n\nAcredite, faremos o trabalho valer muito a pena.', 40725);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60275, 1, 0, 0, 0, 0, 0, 0, 0, 'Никто не станет оплакивать этого злодея. У нас нет денег, чтобы тебе заплатить, но возьми вот это. Владелец давно пропал, а эту вещь можно продать за хорошую цену.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60276, 1, 0, 0, 0, 0, 0, 0, 0, 'Вот твоя обещанная награда.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60279, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты $gоказал:оказала; Темному Приюту огромную услугу, убив то чудовище.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60281, 1, 0, 0, 0, 0, 0, 0, 0, 'Fine work. This should keep the both of us safe from the Mawsworn. \n\nI will hold onto the sphere that you found for the purpose of study.\n\nDon\'t you have other matters to attend to?', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60287, 1, 0, 0, 0, 0, 0, 0, 0, 'Parece que tienes un talento para llamar la atención del Carcelero.\n\nEsto cambia los términos de nuestro acuerdo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60289, 6, 0, 0, 0, 0, 0, 0, 0, '¿Llevaron a uno de tus socios a Torghast? \n\nParece que dio pelea. Quizás él también sea útil... si pudiéramos rescatarlo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60313, 1, 0, 0, 0, 0, 0, 0, 0, 'Me alegra que llegaras aquí con vida.\n\nEl general Draven me ha informado de un plan que podría funcionar.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60338, 1, 0, 0, 0, 0, 0, 0, 0, '¡Hace mucho, mucho tiempo que no vemos a nadie atravesar nuestra entrada! Y mucho menos a alguien como tú.\n\n¿Qué te trae a Ardenweald?', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60341, 1, 0, 0, 0, 0, 0, 0, 0, 'Si puedes ocuparte de los spriggans, tal vez pueda darte algunas tareas más interesantes de la lista de Lady Baya Lunar. ¡Excelente!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60343, 1, 0, 0, 0, 0, 0, 0, 0, '¡Hola, $n! ¡Cuánto tiempo!\n\nLa señora de guerra Hacha Macabra me pidió que te enseñara la ciudad. ¡Sígueme!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60344, 1, 0, 0, 0, 0, 0, 0, 0, 'La señora de la guerra Hacha Macabra me ordenó que te diera una montura, pero primero tenemos que hacer algo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60345, 1, 0, 0, 0, 0, 0, 0, 0, 'Ahora que has aprendido lo básico, ¡puedes regresar cuando quieras para dominar técnicas más avanzadas o comprar más monturas!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60352, 1, 0, 0, 0, 0, 0, 0, 0, 'Una sabia elección, $n.\n\nPuedes probar tus nuevas habilidades con los muñecos de práctica que hay afuera, en el piso superior de la edificación.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60357, 1, 0, 0, 0, 0, 0, 0, 0, 'Mandou bem na escolha de especialização, $n.\n\nFica à vontade se quiser testar suas habilidades novas nos bonecos de treino lá no andar de cima.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60359, 1, 0, 0, 0, 0, 0, 0, 0, 'Siempre que la Cola del Dracoleón sea tu hogar, encontrarás un fuego cálido a tu regreso.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60360, 1, 0, 0, 0, 0, 0, 0, 0, 'Bien. Has llegado. La jefa de guerra quiere hablar contigo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60361, 1, 0, 0, 0, 0, 0, 0, 0, 'La Dama Oscura ha depositado su confianza en nosotros. No la decepcionaremos, $n.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60366, 1, 0, 0, 0, 0, 0, 0, 0, 'You have my gratitude for restoring balance to these fascinating creatures. We all have our part to play.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60388, 0, 0, 0, 0, 0, 0, 0, 0, 'Has hecho bien, ahora que descanse. Hoy ha aprendido que incluso en las praderas más calmas siempre hay peligro.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60392, 0, 0, 0, 0, 0, 0, 0, 0, 'The hills speak of your faith and honor, our forces radiate with each victory! Glory to Bastion, glory to the Archon, glory to the Maw Walker!', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60396, 0, 0, 0, 0, 0, 0, 0, 0, '', 39427);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60400, 0, 0, 0, 0, 0, 0, 0, 0, 'Even those that are far from our lands are necessary allies in these dark times. With faith and honor we will serve them, and the Shadowlands, until evil clips our wings!', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60404, 0, 0, 0, 0, 0, 0, 0, 0, 'Let this one rest, now. Facing threats from within, let alone against ones you once considered kin and ally, can take a mighty toll.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60405, 0, 0, 0, 0, 0, 0, 0, 0, 'Vaya, vaya, vaya, me sorprende que hayas regresado con tu aprendiz en una pieza. Supongo que eres mejor $ginstructor:instructora; de lo que pensé. Voy a recordarlo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60420, 0, 0, 0, 0, 0, 0, 0, 0, 'Las casas de Maldraxxus están nuevamente en armonía, y ya no representan una amenaza para nosotros... por ahora, al menos. \n\n¡Lo hiciste muy bien!', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60425, 0, 0, 0, 0, 0, 0, 0, 0, 'The bells chime in the distance, heralding your return. A glorious sound.\n\nYou have performed a great service today, and truly secured your place among us.\n\nHail, victor!', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60428, 0, 0, 0, 0, 0, 0, 0, 0, 'The Primus was always fond of his trials. It seems you\'ve passed this one. Well done.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60429, 0, 0, 0, 0, 0, 0, 0, 0, '', 39427);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60433, 0, 0, 0, 0, 0, 0, 0, 0, 'Denathrius no esperaba nuestro ataque tan pronto, no esperaba que cumpliéramos nuestra noble parte del ciclo. \n\nEs la última vez que subestima nuestra casa, ha pagado el precio de su error con sangre.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60442, 0, 0, 0, 0, 0, 0, 0, 0, 'You have uprooted the seeds of doubt and crushed them with certainty. May their doubts now be about only their own dark paths.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60443, 0, 0, 0, 0, 0, 0, 0, 0, '¡Regresas $Gvictorioso:victoriosa;, sobre una montaña de plumas sangrientas! ¡Ja ja! ¡Glorioso!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60448, 0, 0, 0, 0, 0, 0, 0, 0, 'Justice has been served.', 41031);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60451, 1, 0, 0, 0, 0, 0, 0, 0, 'Good work. I see that our defenses have been shored up.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60453, 0, 0, 0, 0, 0, 0, 0, 0, 'You wield a fine weapon. Now you must put it to use.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60461, 1, 0, 0, 0, 0, 0, 0, 0, 'Show me this runeblade of yours, mortal.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60466, 1, 0, 0, 0, 0, 0, 0, 0, 'These are perfect! Go in service, always.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60489, 669, 669, 0, 0, 0, 0, 0, 0, 'By your hand, the coliseum is powered by anima once more. Glory awaits you and your allies in the Path of Ascension!', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60491, 1, 0, 0, 0, 0, 0, 0, 0, 'Much has culminated in this moment.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60492, 1, 0, 0, 0, 0, 0, 0, 0, 'May this be but the beginning.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60493, 669, 669, 0, 0, 0, 0, 0, 0, 'You have taken the first steps. A commendable feat!', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60494, 669, 669, 0, 0, 0, 0, 0, 0, 'Every time you craft, a piece of yourself is imbued in whatever you create.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60502, 1, 0, 0, 0, 0, 0, 0, 0, 'This naaru shall prove an invaluable ally.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60504, 0, 0, 0, 0, 0, 0, 0, 0, 'May you prove worthy allies to one another in the trials to come.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60506, 0, 0, 0, 0, 0, 0, 0, 0, 'Nos volvemos a encontrar. Aunque me temo que en circunstancias terribles.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60517, 1, 0, 0, 0, 0, 0, 0, 0, 'Что это? Дорожная пошлина! Варабосс, это коварное чудовище, так и не одолел свою жадность. Насколько я знаю, ваш народ предпочитает в качестве вознаграждения этот драгоценный металл.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60519, 0, 0, 0, 0, 0, 0, 0, 0, 'Ya se fue... el bosque debe estar en una situación peor de lo que yo creía.\n\n<Baya Lunar se ve decaída por un momento, y luego pone las manos en la cadera con firmeza.>\n\nBueno, entonces tendremos que ayudar a aliviar la carga de la reina. ¡Y así tendrá tiempo de darte una audiencia!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60520, 0, 0, 0, 0, 0, 0, 0, 0, 'Bien, parece que eso la ha calmado. Siento que sus sueños empiezan a calmarse.\n\n¡Eres muy $Gbueno:buena; para esto!\n\nPero me preocupa que solo estamos ganando tiempo. Necesitamos una solución drástica.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60521, 0, 0, 0, 0, 0, 0, 0, 0, 'Lady Baya Lunar dijo que enviaría a alguien muy fuerte para asegurar esta arboleda.\n\nBien, ¿sabes pelear?', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60522, 0, 0, 0, 0, 0, 0, 0, 0, '¡Ah, tienes una cuna de espíritus! Será muy útil.\n\nSi es que podemos calmar este caos, claro. La semilla silvestre con la que te enlazaste está teniendo pesadillas intensas... ¡y se están manifestando en toda la arboleda!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60545, 1, 0, 0, 0, 0, 0, 0, 0, 'How good that you have arrived.$b$bWe need $Gheroes:heroines; like you in these dire times.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60557, 1, 0, 0, 0, 0, 0, 0, 0, 'Our forces stand ready to fight once more!', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60563, 1, 6, 0, 0, 0, 0, 0, 0, 'Buen trabajo. No podemos dejar de cuidar las semillas silvestres que la Reina del invierno ha elegido para salvar.\n\nAhora, quizás puedas darme una mano con otro asunto.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60566, 1, 0, 0, 0, 0, 0, 0, 0, '<Puñoletal está malherido.>\n\nDemasiados… No pude con todos…', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60567, 6, 274, 1, 0, 0, 0, 0, 0, 'Normalmente, no tendría que pedirle ayuda a un miembro de tu raza... cualquiera sea.\n\nAh... lo siento. No debería haber sido tan maleducado.\n\nArdenweald es un hogar para almas que llegan desde todos los rincones del reino mortal. Es difícil llevar un registro de todas las razas que llegan a este sitio.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60572, 0, 0, 0, 0, 0, 0, 0, 0, '¿Estás $Glisto:lista; para empezar los preparativos del ritual? Espléndido.\n\n¡Yo también!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60575, 273, 6, 0, 0, 0, 0, 0, 0, 'Excelente trabajo. Así las Pozas Tranquilas deberían volver a la normalidad.\n\nLo admito, cuando me di cuenta de que no encontraba al resto de los cuidadores, pensé lo peor. Es un alivio que solo se hayan convertido en comida para la fauna local.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60577, 273, 1, 0, 0, 0, 0, 0, 0, '¡Las bellotas de ánima! En esta época de sequía, tenemos que aprovechar hasta la más mínima cantidad de ánima que podamos conseguir. Gracias.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60578, 0, 0, 0, 0, 0, 0, 0, 0, 'Que un viejo amigo se ponga en tu contra de la manera más terrible… Cuánto dolor. \n\nPero ya estamos muy cerca de aliviar al espíritu dentro de la semilla silvestre.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60594, 1, 0, 0, 0, 0, 0, 0, 0, 'La luz estelar brilla dentro del Tejesueños, radiante y serena.\n\nY... dos ríos se encuentran en un frondoso delta. Una semilla silvestre y $gun:una; mortal, $gambos:ambas; recién $gllegados:llegadas; al bosque. Conectados en espíritu y destino.\n\nLas ramas llevaban muchísimo tiempo sin presenciar algo semejante. La foresta estará observando.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60600, 0, 0, 0, 0, 0, 0, 0, 0, 'Se ciernen sombras sobre la Arboleda de Duramen. Hay cosas ocultas que se escurren en la oscuridad.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60621, 0, 0, 0, 0, 0, 0, 0, 0, 'Este ataque... ¡Es como si estuvieran poseídos!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60624, 274, 0, 0, 0, 0, 0, 0, 0, 'No podemos dejar Arboleda de Duramen así.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60628, 0, 0, 0, 0, 0, 0, 0, 0, '¿Solo Ara\'lon y Lumera sobrevivieron? ¿Los demás han caído?\n\n<Korayn tiembla con una furia desbocada.> \n\nMe encargaré de que se recuperen sus cuerpos. Esto no puede quedar así. ¡Los vengaremos!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60629, 0, 0, 0, 0, 0, 0, 0, 0, 'Encontraste más armas de las que esperaba.\n\nQué extraño. ¿Cómo consiguieron tantas de NUESTRAS espadas?\n\n<Korayn se ve perturbada.>\n\nLas volveremos a usar contra nuestros enemigos, lo juro.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60630, 0, 0, 0, 0, 0, 0, 0, 0, 'Si no estaba claro antes, ahora sí. Esta caza es personal.\n\nLibraremos a Ardenweald de este monstruo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60631, 0, 0, 0, 0, 0, 0, 0, 0, 'Hemos asesinado muchísimos, pero aún queda una gran cantidad. Tenemos que llegar a la raíz de este mal y arrancarla.\n\nSi no lo hacemos, pronto se esparcirá por todo Ardenweald.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60632, 0, 0, 0, 0, 0, 0, 0, 0, 'No debe ser fácil para Korayn cazar a uno de los suyos... pero tiene razón. El bosque no es un lugar seguro mientras Lorkron aceche en su interior.\n\nQué destino terrible... tanto para quienes son poseídos como para quienes deben cazarlos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60637, 66, 274, 0, 0, 0, 0, 0, 0, 'Gracias. Hice muchos esfuerzos por salvarlos... pero una vez que tienen las máscaras puestas, no hay retorno.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60638, 4, 273, 0, 0, 0, 0, 0, 0, '¡Encontraste más semillas silvestres! Están tan débiles... No soporto verlas así.\n\nDebemos terminar con esto y llevarlas a Tirna Vaal lo más rápido posible.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60639, 18, 274, 0, 0, 0, 0, 0, 0, '¿Korenth... desapareció? ¿Y se han llevado el corazón de la arboleda?\n\nEntonces… tenemos mucho trabajo que hacer.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60644, 1, 1, 0, 0, 0, 0, 0, 0, 'Had it not been for your effort, I have no doubt that my body and soul would be trapped in this helpless place for all eternity.$b$bI owe you my life, $n, and I will fight to the death to return the favor.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60647, 274, 0, 0, 0, 0, 0, 0, 0, 'Se han perdido tantas bellotas de ánima... Al menos pudimos recuperar estas.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60648, 274, 0, 0, 0, 0, 0, 0, 0, 'Derawyn... ¡No puedo perderla a ella también! ¡Podemos usar las bellotas de ánima para salvarla!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60661, 0, 0, 0, 0, 0, 0, 0, 0, 'Saludos, $gforastero:forastera;. Llegas... en tiempos interesantes.\n\n<Herne suelta una risotada.>\n\nY con una compañía igualmente interesante.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60671, 66, 0, 0, 0, 0, 0, 0, 0, 'El corazón de la arboleda...\n\nDebemos recuperarlo... por todos los caídos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60709, 0, 0, 0, 0, 0, 0, 0, 0, 'Hemos perdido mucho. ¿Por qué alguien haría algo así?', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60724, 0, 0, 0, 0, 0, 0, 0, 0, 'Las sombras se avecinan sobre la foresta, como un presagio de la tragedia que sobrevendrá.\n\nEl río del destino fluye raudamente. Sus corrientes son inexorables. ¿A dónde llevan? Aún no podemos saberlo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60733, 1, 0, 0, 0, 0, 0, 0, 0, 'By blood and thunder, we will crush our foes.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60735, 669, 0, 0, 0, 0, 0, 0, 0, '<Kalisthene watches you closely.>$b$bWe have heard you, mortal.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60737, 0, 0, 0, 0, 0, 0, 0, 0, '<The runeblade glows brighter as you approach an old stone altar.>', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60738, 0, 0, 0, 0, 0, 0, 0, 0, 'Me sorprende que pesadillas semejantes provengan de una semilla silvestre.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60763, 1, 0, 0, 0, 0, 0, 0, 0, 'Espero que tu llegada a Ardenweald sea sinónimo de que se acercan tiempos mejores.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60764, 0, 0, 0, 0, 0, 0, 0, 0, 'Me alegra volver a verte. A los Cazadores salvajes nos vendría bien tu ayuda.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60778, 1, 0, 0, 0, 0, 0, 0, 0, '¡Eso fue divertido! ¡Ese pájaro estaba listo para hacer un nido y criar polluelos!\n\nAh, necesitaba reír un poco...', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60831, 6, 0, 0, 0, 0, 0, 0, 0, 'Ah... apprentice? Are you there? I feel... strange.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60839, 0, 0, 0, 0, 0, 0, 0, 0, 'Gracias por rescatar a mis compañeros cazadores. Ya tendremos tiempo para darles nuestros respetos a los que han regresado al ciclo cuando todo esto termine.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60856, 0, 0, 0, 0, 0, 0, 0, 0, 'Buen trabajo, $n. No alcanza para ganar la guerra... pero al menos es una gran ayuda en la lucha por la Pradera de Soryn.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60857, 1, 0, 0, 0, 0, 0, 0, 0, '<El droman Krelnor suspira desanimado mientras lee la corta lista de semillas salvajes.>\n\nAl menos llevarán algunas a Tirna Vaal.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60859, 1, 0, 0, 0, 0, 0, 0, 0, 'Hace mucho tiempo que no llega un alma nueva a Ardenweald. Todas las que están aquí son muy preciadas para nosotros.\n\nMe cercioraré de que Lady Baya Lunar se entere de lo mucho que ayudaste en el rescate en su nombre. ¡Quizás hasta decida que te debe un favor!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60881, 0, 0, 0, 0, 0, 0, 0, 0, 'Es bueno ver que los Cazadores salvajes han recuperado parte de su fuerza... y también que la cuna de espíritus te haya resultado útil.\n\nAhora, llevemos la semilla silvestre a Hondonada Hibernal.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60886, 1, 0, 0, 0, 0, 0, 0, 0, 'We cannot lose sight of the battles ahead of us. The door will have to wait.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60901, 0, 0, 0, 0, 0, 0, 0, 0, 'Tejesueños me contó mucho sobre tu extraña semilla silvestre. \n\nHaré lo que pueda para ayudar.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60905, 0, 0, 0, 0, 0, 0, 0, 0, 'No será fácil, pero conozco un lugar donde podemos encontrar ánima para ayudar a esta semilla silvestre.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (60972, 0, 0, 0, 0, 0, 0, 0, 0, '<The amulet glows red.>', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61096, 1, 0, 0, 0, 0, 0, 0, 0, 'This slot will suffice.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61099, 1, 1, 0, 0, 0, 0, 0, 0, '', 39497);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61107, 0, 0, 0, 0, 0, 0, 0, 0, 'The way is open. The eternal battlefields of Maldraxxus await you.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61135, 0, 0, 0, 0, 0, 0, 0, 0, '<Willowblossom\'s essence will suffuse the crest, completing it so Bastion can once more invest new Ascended.>', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61166, 1, 0, 0, 0, 0, 0, 0, 0, 'This is HEAVY metal! Just like Kofi\'s favorite music!', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61179, 6, 0, 0, 0, 0, 0, 0, 0, 'Beautiful, is it not? I was not sure if I would ever see the forge empowered so ever again.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61190, 1, 0, 0, 0, 0, 0, 0, 0, 'Now to get you and your... associates out of here.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61226, 0, 0, 0, 0, 0, 0, 0, 0, 'Hemos sufrido tanto. Debo proteger lo poco que me queda.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61227, 0, 0, 0, 0, 0, 0, 0, 0, '¿Así que necesitas un mecánico? ¡Qué suerte que tienes!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61230, 0, 0, 0, 0, 0, 0, 0, 0, 'Agradezco tu puntualidad.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61252, 0, 0, 0, 0, 0, 0, 0, 0, 'La confianza de mi baronesa en ti es bien merecida.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61313, 1, 4, 0, 0, 0, 300, 0, 0, 'I can fight once more! Allow me to test my mettle against your enemies!', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61317, 1, 4, 0, 0, 0, 300, 0, 0, 'This new weapon is lacking one thing, the blood of my enemies! Allow me to rectify that.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61318, 1, 4, 0, 0, 0, 300, 0, 0, 'Você honrou seu dever. Agora, preciso de um novo mestre a quem servir. Gostaria que fosse você.', 40593);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61323, 0, 0, 0, 0, 0, 0, 0, 0, 'Haré realidad la voluntad de Akarek una vez más.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61332, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done. They have been spared a truly cruel existence.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61333, 0, 0, 0, 0, 0, 0, 0, 0, 'Excelente, Surcafauces. Con cada alma que recuperes, nuestros ejércitos se fortalecerán.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61335, 1, 0, 0, 0, 0, 0, 0, 0, '¡Gracias por el rescate! Te debo una.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61355, 1, 0, 0, 0, 0, 0, 0, 0, 'Con esto debería alcanzar para escondernos de la mirada del Carcelero.\n\nTe necesito $Gvivo:viva;. Al menos por el momento.\n\nAhora, volvamos a lo nuestro.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61388, 0, 0, 0, 0, 0, 0, 0, 0, 'Espero que como aliados estemos a la altura de las próximas batallas.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61397, 6, 0, 0, 0, 0, 0, 0, 0, 'With your unique abilities, we have an opportunity that has not been presented until now.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61485, 0, 0, 0, 0, 0, 0, 0, 0, 'Pongamos en marcha nuestro plan.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61518, 0, 0, 0, 0, 0, 0, 0, 0, 'Parece que nos han descubierto. Momentos como este exigen flexibilidad.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61548, 0, 0, 0, 0, 0, 0, 0, 0, 'Excelente navegación. Tal vez debas pilotear la necrópolis.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61551, 113, 0, 0, 0, 0, 0, 0, 0, 'Tu fuerza nunca deja de impresionarme.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61569, 396, 0, 0, 0, 0, 0, 0, 0, 'Ven, $p. ¿No quieres ser testigo de tu éxito?', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61594, 0, 0, 0, 0, 0, 0, 0, 0, 'Es el momento de partir.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61697, 1, 0, 0, 0, 0, 0, 0, 0, 'With the Crest complete at last, we must next turn our eyes back to the Forsworn.\n\n<You notice Grubby looking at you expectantly.>', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61715, 1, 0, 0, 0, 0, 0, 0, 0, '<Even through his scarred, molten visage, the emotion on Bolvar\'s face is plain.>\n\nMy daughter... here. I never thought...', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61716, 1, 0, 0, 0, 0, 0, 0, 0, 'A troubling vision indeed. But we must not give up hope.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61767, 1, 0, 0, 0, 0, 0, 0, 0, 'Now we can join our new allies.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61768, 669, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Zerekriss, Maw Walker.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61769, 669, 0, 0, 0, 0, 0, 0, 0, 'This place is filled with horrors... horrors we will purge.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61770, 65535, 0, 0, 0, 0, 0, 0, 0, 'All this bloodshed will not bring them back... but it may spare others the same fate.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61771, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done a great service here.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61772, 669, 0, 0, 0, 0, 0, 0, 0, 'Allow me to read this for a moment.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61773, 669, 0, 0, 0, 0, 0, 0, 0, 'May our victory bring them peace.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61774, 669, 0, 0, 0, 0, 0, 0, 0, 'Well done. I will see that these are destroyed personally.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61775, 669, 0, 0, 0, 0, 0, 0, 0, 'Every abomination denied is one less foe to contend with when we face Gharmal.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61776, 669, 0, 0, 0, 0, 0, 0, 0, 'I am glad Kynthia found you so quickly.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61777, 669, 0, 0, 0, 0, 0, 0, 0, 'This is a long shot, but it just might work...', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61778, 1, 0, 0, 0, 0, 0, 0, 0, 'I could hear their cries from here. Nice work!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61779, 669, 0, 0, 0, 0, 0, 0, 0, 'Gharmal\'s influence runs deep in this place, but it will fade more quickly thanks to your efforts.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61780, 669, 0, 0, 0, 0, 0, 0, 0, 'Such power! Gharmal will have no choice but to face us now.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61781, 0, 0, 0, 0, 0, 0, 0, 0, 'It is time.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61782, 669, 0, 0, 0, 0, 0, 0, 0, 'Our fallen may finally rest in peace.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61785, 669, 0, 0, 0, 0, 0, 0, 0, 'Good. Now we can begin the mission.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61859, 0, 0, 0, 0, 0, 0, 0, 0, 'It is good to see you again, $p.\n\nA friend of yours has kindly offered to assist in our endeavors here: Nemea, the larion master. She will be a powerful ally.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61861, 0, 0, 0, 0, 0, 0, 0, 0, 'It seems we have another recruit, $p.\n\nSika has asked to aid us here whenever she is not tending to her duties at the Eternal Forge.\n\nDo not underestimate her worth as an ally!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61862, 0, 0, 0, 0, 0, 0, 0, 0, '$p, I would ask for a moment of your time.\n\nOne of the recently ascended, Clora, feels that her fierce combat skills would be best put to use tackling the matters laid out before us. I am inclined to agree.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61863, 0, 0, 0, 0, 0, 0, 0, 0, 'Hello there, my mortal friend! I have good news for you today.\n\nApolon has told me with great certainty that the path he follows is intertwined with yours.\n\nThough he has duties to the Path of Ascension, his courage will be of great aid to us here as well.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61864, 0, 0, 0, 0, 0, 0, 0, 0, 'Mikanikos has made a sensible suggestion, $p.\n\nWhen Bron is not directly assisting you in the field, he could be of service elsewhere throughout the Shadowlands.\n\nI am sure Bron will not mind.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61865, 0, 0, 0, 0, 0, 0, 0, 0, 'Your dedication to Bastion is inspiring, $p.\n\nWith the anima you have returned to us, Disciple Kosmas feels he can be away from the Temple of Purity for a time.\n\nHe has offered to join us here at the Hold, and contribute to the greater needs of the Shadowlands.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61869, 0, 0, 0, 0, 0, 0, 0, 0, '¡Esto le pertenecía al Primus!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61874, 1, 0, 0, 0, 0, 0, 0, 0, 'Qué bueno que hayas llegado.\n\nNecesitamos $Ghéroes:heroínas; como tú en estos tiempos funestos.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61876, 1, 0, 0, 0, 0, 0, 0, 0, 'I am relieved that I am not the one to carry that thing back to Bastion.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61877, 1, 0, 0, 0, 0, 0, 0, 0, 'I am glad to be among our own once more. Maldraxxus is not to my tastes.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61878, 0, 0, 0, 0, 0, 0, 0, 0, '<The Fleshshaper\'s Heart fits readily into the glowing sphere. As it enters, you hear faint clicking sounds within the Crest, as if it were designed to accept such power readily.\n\nThus begins a new era for the kyrian.>', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61982, 66, 0, 0, 0, 0, 0, 0, 0, 'You have my thanks, Maw Walker. We will not let it go to waste.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61983, 0, 0, 0, 0, 0, 0, 0, 0, '', 39497);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (61984, 0, 0, 0, 0, 0, 0, 0, 0, 'El ánima que has conseguido nutrirá nuestras valiosas arboledas. Te agradecemos.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62085, 1, 0, 0, 0, 0, 0, 0, 0, 'I wondered where my canteen was. Where did you find it, $n? Did you take a sip before you came back here?$b$bI guess there is no problem.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62169, 0, 0, 0, 0, 0, 0, 0, 0, 'Bien hecho, Surcafauces. Los Ojos depositan su confianza en ti.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62189, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, guau, esto sí que es un verdadero hallazgo. Toma estos rubíes infundidos por el paquete, los mejores que verás en el distrito. Y si te gusta alguno de mis artículos, avísame.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62237, 1, 1, 0, 0, 0, 300, 0, 0, '<Thenios studies the mirror closely.>\n\nI see.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62238, 1, 0, 0, 0, 0, 0, 0, 0, 'I hope that this memory helps you to understand Uther\'s path.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62309, 0, 0, 0, 0, 0, 0, 0, 0, '¡$n, es bueno volver a verte!\n\nLuego del éxito contra la Casa de Ensamblajes, el secutor Mevix no ve la hora de volver a la acción del campo de batalla.\n\nNunca dejes que un soldado motivado pierda tiempo haciendo nada. ¡Dale un propósito!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62448, 66, 0, 0, 0, 0, 0, 0, 0, 'Tus hazañas han llamado la atención de las demás casas y de aquellos que cuestionan sus órdenes.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62555, 1, 0, 0, 0, 0, 0, 0, 0, 'The Archon shall convene the Paragons to discuss our next moves. Go in service.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62557, 1, 0, 0, 0, 0, 0, 0, 0, 'This day would not have been won without you, Maw Walker.\n\nOn behalf of the Archon, the Paragons, and all kyrian--thank you.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62567, 1, 0, 0, 0, 0, 0, 0, 0, 'Que bom que vocÃª veio, $n! Algo me diz que o seu futuro estÃ¡ Ã  sua espera no passado!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62654, 1, 0, 0, 0, 0, 0, 0, 0, 'You return in one piece. And with not just Darion, but his father as well. You continue to succeed, Maw Walker.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62694, 0, 0, 0, 0, 0, 0, 0, 0, 'Our rival houses will think twice before entering our fortress again. Blood and glory to you, Surcamauces.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62698, 1, 0, 0, 0, 0, 0, 0, 0, 'I knew you would answer the call, Maw Walker. There is much to be done.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62700, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, this should be enough to engrave the runes. Upon lesser equipment, that is. \n\nPotent items will require more... more... Soul Ash. Yes, Soul Ash.\n\nAnd you should hold onto it. I will ask for it when the time comes.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62704, 1, 0, 0, 0, 0, 0, 0, 0, 'You have made your decision. The Purpose will guide you from here on.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62714, 1, 0, 0, 0, 0, 0, 0, 0, 'What is this?\n\n<She opens the parcel. A soft smile spreads over her face.>\n\nThis is wonderful. Thank you.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62715, 1, 0, 0, 0, 0, 0, 0, 0, 'This is perfect. I appreciate your efforts on my behalf.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62718, 1, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Hero\'s Rest, crossroads of Bastion. You will find much to aid you in your journey here.', 37474);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62719, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve collected all the necessary pieces. Now all that\'s left is to combine them.\n\nSpeak with me again when you are ready, and I will create the prize you seek.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62730, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes, this very sweet and ripe. Must use quickly, before it spoils!', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62733, 1, 0, 0, 0, 0, 0, 0, 0, 'I hope you liked your surprise! \n\nYou find more silver strongboxes, give Ripe Purian tribute!\n\nOh-ho! One more thing! Ask me about Steward of the Day!', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62785, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, ¿Te envió Anzio? Ja, y yo que supuse que eras solo $gotro fanático molesto:otra fanática molesta;.\n\n¡Sí, creo que eres justo $gel:la; $c que necesito!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62789, 669, 0, 0, 0, 0, 0, 0, 0, 'Greetings, Maw Walker. It is an honor.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62790, 669, 0, 0, 0, 0, 0, 0, 0, 'Follow the path always, and all will be well.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62791, 66, 0, 0, 0, 0, 0, 0, 0, 'The Maw Walker returns triumphant! Have you any anima on hand?', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62792, 1, 0, 0, 0, 0, 0, 0, 0, 'Every drop is precious. I will keep it safe here.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62793, 1, 0, 0, 0, 0, 0, 0, 0, 'I could feel their presence as soon as you brought them to the hold. What a momentous occasion!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62794, 1, 0, 0, 0, 0, 0, 0, 0, 'A wise decision. It will take some time to activate, but all good things are worth waiting for, are they not?', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62795, 0, 0, 0, 0, 0, 0, 0, 0, 'Your soulbind will grow alongside you in time, but your training is complete for now.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62796, 1, 0, 0, 0, 0, 0, 0, 0, 'Welcome back, Maw Walker. Our work has not paused in your absence.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62798, 1, 1, 0, 0, 0, 0, 0, 0, '<Au\'qil examina lo que le trajiste.>\n\n¿Un recuerdo? Fascinante... Con este conocimiento, puedo enseñarte a fabricar armaduras capaces de contener un gran poder. \n\nPor un precio, claro.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62837, 1, 0, 0, 0, 0, 0, 0, 0, '¡$n! La oscuridad de las Fauces flota sobre ti. ¿Qué noticias traes?', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62843, 6, 0, 0, 0, 0, 0, 0, 0, 'Qué pronto has regresado, Surcafauces.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62844, 1, 0, 0, 0, 0, 0, 0, 0, 'Has regresado, Surcafauces. \n\nBien. Tenemos mucho que hacer.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62845, 1, 0, 0, 0, 0, 0, 0, 0, 'Sentí la presencia de esas almas en cuanto las trajiste aquí. ¡Su potencial aumentará nuestro poder!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62846, 1, 0, 0, 0, 0, 0, 0, 0, 'Cuando se trata de este tipo de asuntos, la paciencia es tan necesaria como la fuerza. Con el tiempo, nuestro poder crecerá vastamente y sin igual.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62848, 0, 0, 0, 0, 0, 0, 0, 0, 'Por ahora el entrenamiento ha terminado, nuestro lazo se fortalecerá con el tiempo.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62861, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done. They have been spared a truly cruel existence.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62863, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done. They have been spared a truly cruel existence.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62882, 1, 0, 0, 0, 0, 0, 0, 0, 'Cuida ese guardaalmas. No tengo ninguna intención de volver a emprender la ardua tarea de buscar otro.\n\nBien, pongámonos en acción.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (62916, 1, 0, 0, 0, 0, 0, 0, 0, 'Hoo-hoo! Have fun with new friend!', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63032, 1, 0, 0, 0, 0, 0, 0, 0, 'Tengo noticias terribles, $n.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63050, 0, 0, 0, 0, 0, 0, 0, 0, 'Interesante. A menudo me he preguntado qué diferenciaba a los protectores del Caldero del resto de los jurafauces.\n\nEspero que no haya sido muy difícil conseguir estos lingotes.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63051, 1, 0, 0, 0, 0, 0, 0, 0, 'Cartel Ve was asking you questions about me?\n\nI\'m flattered. Make sure you never tell them anything.\n\nEver.\n\nCome. There\'s work to do.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63052, 1, 0, 0, 0, 0, 0, 0, 0, 'It has been many eons since the anima gateways were active.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63053, 1, 0, 0, 0, 0, 0, 0, 0, 'In time we may open new destinations for our gateways. Haephus can direct you further.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63054, 2, 0, 0, 0, 0, 0, 0, 0, 'Bien hecho, $n. $GJuntos:Juntas;, nos aseguraremos de que nuestros soldados dominen el campo de batalla.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63055, 1, 0, 0, 0, 0, 0, 0, 0, 'Han pasado muchos eones desde que estos portales estuvieron activos. ¡Qué emoción!', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63059, 1, 0, 0, 0, 0, 0, 0, 0, 'Si trabajamos más, podremos establecer portales a otras necrópolis de las Tierras de las Sombras.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63135, 1, 0, 0, 0, 0, 0, 0, 0, 'This will help mitigate the Mawsworn\'s aerial advantage. Caution must still be exercised, of course.\n\nDo try to avoid their notice whenever possible. I would hate for one of them to follow you here.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63136, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, sí. Esto sin duda lo ha escrito alguien tomado por la locura.\n\nNo obstante, eso no significa que no tenga sentido. A veces, solo los dementes ven la verdad.', 38556);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63140, 1, 0, 0, 0, 0, 0, 0, 0, '<Ve\'nari glances over the manuscript.>\n\nThis is truly an ancient document. A pity it is in such poor condition. The secrets it holds must be priceless.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63152, 1, 0, 0, 0, 0, 0, 0, 0, '<Ve\'nari inspeciona rapidamente o pergaminho, virando várias vezes o documento.>\n\nFazia algum tempo que eu não via um método de codificação tão complexo. Mas a extravagância dos venthyr é pública e notória.\n\nSeja qual for o conteúdo dessa mensagem, está claro que o autor queria mantê-la em segredo.', 40725);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63153, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, a rare bit of insight into the minds of the Maw\'s eternal tormentors.\n\nQuite fascinating... if a bit frightening.', 39804);
