INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63158, 1, 0, 0, 0, 0, 0, 0, 0, '', 39497);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63346, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, $n. I have heard many stories about your skill on the battlefield.$b$bIt interests me that great fighters like you are well equipped. Zo\'dash is authorized to upgrade your weapons and armor.', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63576, 0, 0, 0, 0, 0, 0, 0, 0, 'Anduin... now the Jailer\'s pawn...\n\nWe must take immediate action, $n. It is time for the covenants to convene.', 40966);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63578, 0, 0, 0, 0, 0, 0, 0, 0, 'We just barely survived that! I definitely didn\'t expect THAT to happen.\n\nBut it\'s not over. We can\'t let our guard down!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63612, 0, 0, 0, 0, 0, 0, 0, 0, 'They haven\'t noticed us yet. That won\'t stay true for long.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63613, 0, 0, 0, 0, 0, 0, 0, 0, 'That should be enough to let the bulk of the army through.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63614, 1, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. I feared my axe was lost forever.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63615, 0, 0, 0, 0, 0, 0, 0, 0, 'Our foes will learn that even the skies are no longer safe for them.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63616, 0, 0, 0, 0, 0, 0, 0, 0, 'We have our opening. It is time to push ahead.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63617, 0, 0, 0, 0, 0, 0, 0, 0, 'Now to sow chaos here.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63618, 0, 0, 0, 0, 0, 0, 0, 0, 'They were foolish to think they could cross blades with us.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63619, 0, 0, 0, 0, 0, 0, 0, 0, '<You see the surviving Forsworn make their way to Zerekriss.\n\nNo doubt Nikolon will ensure they return to Bastion safely.>', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63620, 5, 0, 0, 0, 0, 0, 0, 0, '<Draka spits on the corpse of Vyraz.>\n\nAnd with that traitor\'s end, Maldraxxus will be unified once again!', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63622, 0, 0, 0, 0, 0, 0, 0, 0, 'There is no doubt that we will need to put pressure on Desmotaeron once again.\n\nThe witch of the Val\'kyr will not let us walk free if she can help it.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63638, 0, 0, 0, 0, 0, 0, 0, 0, 'Ardenweald depends on the cycle. Without these spirits, what purpose would there be?\n\nI could not let the Jailer\'s forces destroy what we have fought so hard to protect.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63639, 0, 0, 0, 0, 0, 0, 0, 0, 'The Jailer has begun his attack on Oribos.\n\nWhat I have feared the most has come to pass.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63644, 1, 0, 0, 0, 0, 0, 0, 0, 'The nathrezim have returned? I dared not imagine they would ever grow so bold again.\n\nA shame they have not learned in their exile, and return to us as enemies instead of family.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63645, 1, 0, 0, 0, 0, 0, 0, 0, 'It may take ages upon ages, but we shall remind Denathrius of his purpose, and remind him how to be beautiful in his craft once again.\n\nThis place is strong with the Light. Few venthyr can tolerate such power for long without succumbing to it. A fitting place to teach uninterrupted.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63646, 1, 0, 0, 0, 0, 0, 0, 0, 'Hmm. A letter from the Primus himself? I had wondered what had become of that old tyrant. \n\n<Glances over the proffered letter.>\n\nI... see.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63647, 1, 0, 0, 0, 0, 0, 0, 0, 'Chelra was a nathrezim the entire time I knew her? \n\nEven in death I must still contend with the schemes of the nathrezim. I have grown too trusting in my penance.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63648, 1, 0, 0, 0, 0, 0, 0, 0, 'I have spoken with the Stonewright. Chelra fell to the master when I was a prisoner of the Maw. \n\nShe was my friend and protector through countless ages, and I did not even know she had been slain, her identity stolen.\n\nOne more sin for Denathrius to atone for.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63649, 1, 1, 0, 0, 0, 0, 0, 0, 'The venthyr have grown complacent, unwilling to acknowledge the reality that nathrezim might return. The same blindness left them exposed to Denathrius\' treachery. \n\nThey will be far more vigilant now.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63650, 1, 0, 0, 0, 0, 0, 0, 0, 'The nathrezim may not be venthyr, but nor were they crafted from souls. In many ways, they are more my siblings than my fellow venthyr.\n\nA shame what they have chosen to become.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63651, 1, 1, 0, 0, 0, 0, 0, 0, 'This does not make sense. Their goals seem too wide spread. My assumption was that they had been planning to steal my medallion to deny us a new Sigil of the Venthyr, but it would appear their designs go beyond that.\n\nWhat are they really up to? Here, you can have a translated copy.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63652, 1, 0, 0, 0, 0, 0, 0, 0, 'Subterfuge and counter-insurgence was never my strong suit. We always had souls sent here that were far better than I at such tasks.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63653, 1, 1, 0, 0, 0, 0, 0, 0, 'I will not suffer my medallion in the hands of these unwelcome guests.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63654, 1, 0, 0, 0, 0, 0, 0, 0, 'This is worse than anything I could have anticipated.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63656, 1, 0, 0, 0, 0, 0, 0, 0, 'Your strength in battle remains uncontested. Today, though, you prove your strength of diplomacy. \n\nI have already issued orders for the deployment of the Stonewright\'s forces. Well done in bringing her into the fold.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63659, 669, 0, 0, 0, 0, 0, 0, 0, 'More dreadlords? I should have suspected the fangs of Denathrius would find their way here again.\n\nI am pleased to know that despite their setbacks in my absence, the House of Eyes has not lost their sight.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63660, 0, 0, 0, 0, 0, 0, 0, 0, 'I do not do this lightly, Maw Walker, but we have no choice.\n\nWe must open a gateway to the Maw.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63661, 1, 0, 0, 0, 0, 0, 0, 0, 'Normally, I would consider your request bothersome and insulting.\n\nBut recent events have led me to believe that your proposal may prove beneficial to both of us.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63662, 1, 1, 0, 0, 0, 0, 0, 0, 'The Jailer desperately wants something here, yet his precious Eye cannot breach its defenses.\n\nOh well. Shall we?', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63663, 0, 0, 0, 0, 0, 0, 0, 0, 'I must find out more about you, Maw Walker.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63665, 1, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n. I am eager to learn of this realm pulled into the Maw.\n\nIt is time to begin our assault on the Jailer\'s forces.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63672, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for coming so swiftly, $n. Ardenweald is in need of your help.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63674, 0, 0, 0, 0, 0, 0, 0, 0, 'With your assistance I should be able to clear my list quickly.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63675, 0, 0, 0, 0, 0, 0, 0, 0, 'One of the ringleaders is dead but I will not be satisfied until they are all dead.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63676, 0, 0, 0, 0, 0, 0, 0, 0, 'Traitors cannot hide.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63677, 0, 0, 0, 0, 0, 0, 0, 0, 'This can be the only end for traitors to Maldraxxus.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63678, 1, 0, 0, 0, 0, 0, 0, 0, 'Traitors will not be tolerated.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63679, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'m glad you could get here so quickly! We\'ve run into a little bit of a hiccup.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63680, 1, 0, 0, 0, 0, 0, 0, 0, 'We found Huln and conveniently he was with just the person we need to help with the ritual!\n\nI love it when things work out for everyone, don\'t you?', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63681, 1, 0, 0, 0, 0, 0, 0, 0, 'I am glad to see you again. We do not seem to have much time.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63682, 1, 0, 0, 0, 0, 0, 0, 0, 'It has been so long...', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63683, 1, 0, 0, 0, 0, 0, 0, 0, '<Ysera sighs, relieved.>\n\nSuch peace has returned to Ardenweald and to those who dwell within. My dear friend has been saved. I do not know what comes next, but I have hope.\n\nThis could not have been done without your help, $n. Thank you.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63708, 1, 6, 273, 0, 0, 0, 0, 0, 'How beautiful. That this Phantasma is able to exist outside of the tower at all is unbelievable. Something is most curious about it\'s nature...further study is needed.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63711, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes, this will do. Think I\'ll save one to show the other smiths back home.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63712, 1, 0, 0, 0, 0, 0, 0, 0, 'Excellent. I should be able to extract the Primus\'s message from these records.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63713, 1, 0, 0, 0, 0, 0, 0, 0, 'What a ride! Keep that hookshot.\n\nJust be careful using it. That thing will chew through any normal grapple points.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63714, 1, 0, 0, 0, 0, 0, 0, 0, 'Tal-Galan informed me of the situation.\n\nWe must get you into that vault.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63717, 669, 0, 0, 0, 0, 0, 0, 0, 'Good work fighting those Mawsworn.\n\nWe\'ll take it from here, $n.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63722, 1, 0, 0, 0, 0, 0, 0, 0, 'A pity all three keepers did not survive. Good work tracking down the two that did.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63725, 1, 0, 0, 0, 0, 0, 0, 0, 'The Primus\'s sigil! Hmm... I sense a familiar magic upon it.\n\nYes. All those things I saw through your eyes from Torghast...', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63726, 669, 0, 0, 0, 0, 0, 0, 0, 'We are free. There is still hope to fight back against Zovaal.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63727, 0, 0, 0, 0, 0, 0, 0, 0, 'We can dwell on our past mistakes when this is over. For now, we must move forward.\n\nYou aided me during my imprisonment as the Runecarver. Take this as a token of my gratitude.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63728, 1, 0, 0, 0, 0, 0, 0, 0, 'Word of your deeds has spread across the Shadowlands.\n\nI know together we will be able to find this Night Warrior swiftly.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63731, 0, 0, 0, 0, 0, 0, 0, 0, 'Relics? Research? We have much more urgent matters at hand!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63732, 1, 0, 0, 0, 0, 0, 0, 0, 'What did we do to deserve such cruelty?', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63733, 1, 0, 0, 0, 0, 0, 0, 0, 'You bought us the moment we needed to make our escape.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63734, 1, 0, 0, 0, 0, 0, 0, 0, 'I cannot fully describe the relief I feel now that we have the Lodestaff.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63736, 1, 0, 0, 0, 0, 0, 0, 0, 'The Lodestaff is humming with vibrant energy! The anima worked better than I could have hoped.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63737, 1, 0, 0, 0, 0, 0, 0, 0, 'So much has been taken from us. And yet, due to Roh-Senara\'s sacrifice, there is hope.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63738, 1, 0, 0, 0, 0, 0, 0, 0, 'It is hard to imagine how I will continue without the Archivist.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63739, 1, 0, 0, 0, 0, 0, 0, 0, 'I was beginning to worry that you had given up on our cause.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63740, 1, 0, 0, 0, 0, 0, 0, 0, 'I trust you and Scholar Roh-Suir learned from the Archivists.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63754, 0, 0, 0, 0, 0, 0, 0, 0, 'I will tell the tale of your noble deeds, $n. Your actions this day will be remembered.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63755, 6, 1, 0, 0, 0, 3000, 0, 0, 'You are searching for the Fatescribe too? I must find him. I cannot fail.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63756, 378, 274, 0, 0, 0, 7000, 0, 0, 'I struggle to believe that not a single attendant has any information on where the Fatescribe might be.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63757, 5, 378, 0, 0, 0, 0, 0, 0, 'Now this is special. This implement is used by the Fatescribe in his work.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63758, 378, 274, 5, 0, 0, 2000, 4000, 0, 'Mad ramblings and incense. Weak clues, but they\'re all we have.\n\nWe need to search the Sanctuary of Guidance.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63759, 412, 378, 0, 0, 0, 4000, 0, 0, '$p! At last! \n\nYou know that I cannot do this without you.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63760, 378, 11, 0, 0, 0, 5000, 0, 0, 'Using an illusion to hide.\n\nCan you imagine anything so clever?', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63761, 0, 0, 0, 0, 0, 0, 0, 0, '<You have completed the protector\'s task.>', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63762, 0, 0, 0, 0, 0, 0, 0, 0, '<The Fatescribe has been taken.>', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63763, 5, 378, 0, 0, 0, 1000, 0, 0, 'News of the nathrezim infiltrating Korthia is deeply troubling.\n\nHow long have they been here? What is their goal?', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63764, 1, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Skyhold, $n.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63771, 1, 1, 0, 0, 0, 0, 0, 0, 'You\'re back, mortal.$b$bDo you want to change your destiny?', 38134);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63783, 0, 0, 0, 0, 0, 0, 0, 0, 'You can trust me with this anima. I will see it is turned against the Jailer and his forces.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63789, 0, 0, 0, 0, 0, 0, 0, 0, 'You have performed admirably in this task, Maw Walker. We thank you for your aid.', 40000);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63810, 1, 0, 0, 0, 0, 0, 0, 0, 'My heart rejoices to see a noble face in this realm of nightmare.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63811, 0, 0, 0, 0, 0, 0, 0, 0, 'Helya has gone too far!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63827, 669, 0, 0, 0, 0, 0, 0, 0, 'You have done much to help our forces. Thank you,$n.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63828, 5, 0, 0, 0, 0, 0, 0, 0, 'The Jailer\'s forces are unending and our numbers few, but with your help we shall succeed!', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63831, 0, 0, 0, 0, 0, 0, 0, 0, 'This is well made, $n.\n\nOf course, every great horn has a name. It is our tradition!', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63833, 1, 0, 0, 0, 0, 0, 0, 0, 'We may not see an end to the Jailer\'s forces now, but all things must eventually end.\n\nEven evil.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63836, 0, 0, 0, 0, 0, 0, 0, 0, 'You also joined Fangcrack\'s fan club? So did Lady Moonberry.\n\n<Prince Renathal thinks for a moment.>\n\nPerhaps I should reconsider my earlier rejection of Rendle\'s invitation to join...', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63839, 1, 0, 0, 0, 0, 0, 0, 0, 'Even in the depths of the Maw, the Accuser remains focused on saving any soul she can...\n\nHer devotion to Revendreth\'s true purpose continues to inspire me.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63842, 0, 0, 0, 0, 0, 0, 0, 0, 'Let it not be said that it was ever boring while Lady Moonberry was present.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63844, 0, 0, 0, 0, 0, 0, 0, 0, 'Well fought. Kolfinna, Dryfinna, and Aeskir have risen to the challenge and await you in Odyn\'s hall.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63845, 0, 0, 0, 0, 0, 0, 0, 0, 'It is done. Danica and her sisters are infused with my power and wield my wrath.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63848, 1, 0, 0, 0, 0, 0, 0, 0, 'A thank-you is polite.\n\nA favor is better.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63849, 1, 0, 0, 0, 0, 0, 0, 0, 'Perfect. Their secrets will soon be mine.\n\nPardon me. I meant to say \"ours.\"', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63853, 0, 0, 0, 0, 0, 0, 0, 0, 'A sweet reprieve from that creature\'s dreadful glare.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63855, 1, 0, 0, 0, 0, 0, 0, 0, 'A worthwhile trip that has opened so many possibilities.\n\n<Ve\'nari digs through the heavy bag and pulls out a few unusual objects.>', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63856, 0, 0, 0, 0, 0, 0, 0, 0, '<Bolvar stares in wonder at the sight before him.>', 40966);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63857, 0, 0, 0, 0, 0, 0, 0, 0, 'The Winter Queen must be the Jailer\'s next target.\n\nWe must do all we can to protect her.\n\nAnd I have a few ideas how we can do that...', 40966);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63859, 669, 0, 0, 0, 0, 0, 0, 0, 'The sounds of those bells strengthens our hearts and gives us the might to win this battle.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63860, 1, 0, 0, 0, 0, 0, 0, 0, 'Where... where did you find this?\n\n<The Archivist brings the talisman closer for inspection.>\n\n I haven\'t seen this in eons. I thought it had been lost.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63861, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes, yes. This is it. Magnificent, don\'t you agree?\n\n<The damp codex catches The Archivist by surprise. He gently runs his hand along the binding, muttering an incantation in a soft, enigmatic tone.>\n\nThat should help preserve it for now, but the incantation won\'t last. We must get to work.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63863, 669, 0, 0, 0, 0, 0, 0, 0, 'You display the greatest of courage.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63895, 0, 0, 0, 0, 0, 0, 0, 0, 'This projector is certainly useful. A safe landing, I perceive.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63901, 1, 0, 0, 0, 0, 0, 0, 0, 'Songs will immortalize the deeds of this day, $n.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63902, 1, 0, 0, 0, 0, 0, 0, 0, 'The Eye defeated? Good news indeed.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63904, 0, 0, 0, 0, 0, 0, 0, 0, 'Our best efforts were still in vain.\n\nOur enemy\'s plan edges closer to fruition.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63912, 1, 1, 1, 65535, 0, 500, 1000, 0, 'Aha! You have recovered the Celestial Shadowlands Chart.\n\nWith the chart and the data you collected, it can be restored to its fully functional form. It will take some time to analyze and interpret the results, which I can get started on now that the relic has been returned to the Archive.\n\nAllow me a moment to find a good spot for it...', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63949, 1, 0, 0, 0, 0, 0, 0, 0, 'The threads of fate weave through you, Maw Walker.\n\nJust as the prophecies foretold.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63952, 1, 0, 0, 0, 0, 0, 0, 0, '<Margrave Sin\'dane looks impressed... or annoyed... or disgusted... or concerned?>\n\nHm. I suppose with enough hands, anything IS possible...', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63954, 0, 0, 0, 0, 0, 0, 0, 0, 'Wonderful! You brought everything that I asked for.\n\nAgain, do not worry about the contents of these boxes. They pose no threat to you.\n\nHere is the reward I promised.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63969, 1, 0, 0, 0, 0, 0, 0, 0, 'We will discover the source of this darkness, and end it.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63973, 1, 0, 0, 0, 0, 0, 0, 0, 'No matter how strong we become, we are stronger when we hunt together.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63974, 1, 0, 0, 0, 0, 0, 0, 0, 'I wish I could\'ve been there to see it myself!\n\nA fitting end for the Heralds...', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63990, 1, 0, 0, 0, 0, 0, 0, 0, 'I am not opposed to helping you, but... There are matters I must attend to first.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (63994, 0, 0, 0, 0, 0, 0, 0, 0, 'Maw Walker. We must summon the others.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64007, 1, 0, 0, 0, 0, 0, 0, 0, 'The Maw...\n\nNow our true work begins.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64014, 1, 0, 0, 0, 0, 0, 0, 0, 'That\'s quite an instrument you have there.\n\nIt seems the golden feathers were worth following, Maw Walker. You owe me yet again.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64042, 1, 0, 0, 0, 0, 0, 0, 0, 'With everything in place we can begin. I only hope we are not too late for Tyrande.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64090, 1, 0, 0, 0, 0, 0, 0, 0, 'It sounds like Huln might not have much time left!', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64091, 1, 0, 0, 0, 0, 0, 0, 0, 'If he got nicked, I know where they took \'im.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64092, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'m busy with this piece of dreadlord filth. Find something useful to do before the Stonewright gets angry. You don\'t want to see her angry.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64101, 0, 0, 0, 0, 0, 0, 0, 0, 'There\'s so much anima here. This will be very useful in helping the attendants rebuild. Thank you.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64106, 1, 0, 0, 0, 0, 0, 0, 0, 'Do not relent, $n. This is only the first of many battles to come.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64210, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah... a curious device. Anchoring it in this place will prove useful.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64216, 0, 0, 0, 0, 0, 0, 0, 0, '<The box hungers, reaching into your memories of Torghast for past deeds to feed upon.>', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64289, 1, 0, 0, 0, 0, 0, 0, 0, 'These will do nicely. There is much I can do to improve them.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64290, 1, 0, 0, 0, 0, 0, 0, 0, 'This should do nicely. The reliquary will improve with more research.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64323, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, $n. I have heard many stories of your prowess on the battlefield. \n\nI have interest in keeping great combatants like yourself well equipped. Zo\'dash is authorized to further upgrade your armor and weapons.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64437, 1, 0, 0, 0, 0, 0, 0, 0, 'Ah, good. With the Queen\'s Sigil restored, we\'re that much closer to being able to stop Zovaal. Thank you for this information.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64438, 1, 0, 0, 0, 0, 0, 0, 0, 'This is the least we can do to help you in your efforts, Maw Walker.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64461, 0, 0, 0, 0, 0, 0, 0, 0, 'Your faith in Bastion is inspiring, $p.\n\nHermestes has seen your unflagging dedication to our cause.\n\nIt has inspired them to join us in a different capacity at the Hold, so that they may contribute further to the greater needs of the Shadowlands.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64506, 1, 0, 0, 0, 0, 0, 0, 0, 'These are just some of the relics that are missing.\n\nYou have the power to recover them, wherever they are within Korthia.', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64511, 1, 0, 0, 0, 0, 0, 0, 0, 'Please, do what you can to find these relics. We must not let this knowledge fall to ruin.', 40120);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64552, 1, 65535, 0, 0, 0, 0, 0, 0, '<Koros carefully studies the documents you brought him.>\n\nThe documentation is incomplete. It is unclear if the archivist even survived...\n\nBut the tales of the anima are coherent, and there are enough details here to launch an expedition.\n\nIt will not be easy. The archivist warns of many dangers protecting the anima source...', 39804);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64555, 0, 0, 0, 0, 0, 0, 0, 0, 'Most of this information is useful. My thanks, Maw Walker.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64556, 1, 0, 0, 0, 0, 0, 0, 0, 'This work brings us one step closer to ending Zovaal\'s reign over this forsaken land.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64560, 0, 0, 0, 0, 0, 0, 0, 0, 'You can trust me with this anima. I will see it is turned against the Jailer and his forces.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64561, 0, 0, 0, 0, 0, 0, 0, 0, 'Places like the Aerie and Estuary must have been desolate, even before all the destruction. What interesting creatures congregate there.\n\nWill they all survive these changes, or will some adapt, and become even more fearsome?\n\nAnd here is your reward, as promised.', 39653);
INSERT IGNORE `quest_offer_reward`(`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (64562, 0, 0, 0, 0, 0, 0, 0, 0, 'That got them back on their feet!\n\nAny little bit helps to keep us in the fight.', 39653);
