INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (24623, 1, 0, 0, 0, 'Poor little tings.', 28153);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (24624, 0, 0, 0, 0, 'Dis is very unpleasant work.', 28153);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (24761, 0, 0, 0, 0, 'Конечно, это не самые страшные зверюги, но царапаются изрядно. Могут все ноги тебе изодрать, если их вовремя не унести. Ноги в смысле, не рысей.', 26365);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (26222, 1, 0, 0, 0, 'Did you get those parts?', 26365);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (27673, 0, 0, 0, 0, 'Onde estão os núcleos, jovem? Pelas barbas de Muradin, onde estão os malditos núcleos?!$B$B<Kand suspira profundamente.>$B$BAhm, me desculpe – você conseguiu os núcleos?', 0);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (28394, 1, 0, 0, 0, 'Não podemos permitir que esses golens sejam usados contra nós.', 0);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (28803, 0, 0, 0, 0, 'Esses velhos ossos parecem enânicos, possivelmente dos construtores originais de Grim Batol. Talvez você possa comparar as marcas da fivela de pedra do cinto com uma Pedra Rúnica Enânica para descobrir mais sobre o assunto.', 0);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (28852, 0, 6, 0, 0, 'Encontrou algum draco ferido? Eles conseguiram ajudar você?', 40593);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29406, 1, 0, 0, 0, 'The training targets are just down the hill. Come back to me once you\'re finished.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29408, 1, 0, 0, 0, 'There will come time when you must trust your instincts and simply act.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29409, 1, 0, 0, 0, 'Our greatest glory is not in never falling, but in getting up every time we do. Jaomin Ro knows this. He finds strength in every defeat.$B$BYou may yet learn that lesson.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29417, 1, 0, 0, 0, 'To not act is to abandon reason.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29418, 1, 0, 0, 0, 'I hope you\'re not shy about getting your hands dirty.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29422, 1, 0, 0, 0, '<Huo rests quietly within the pool.>', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29424, 1, 0, 0, 0, 'You needn\'t worry about hurting the sprites. They\'re really just living plants when it comes down to it - they\'ll resprout later.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29523, 1, 0, 0, 0, 'When opportunity rises, it is our responsibility to reach out and grab it. Second guessing is as good as giving up.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29524, 1, 0, 0, 0, 'There is a time for meditation and contemplation, and there is a time for action. Diving the balance between the two is one of the greatest wisdoms we seek.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29661, 1, 0, 0, 0, 'Have you done as I asked of you?', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29662, 0, 0, 0, 0, 'Telling you of my strength means nothing. I must show you.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29663, 1, 0, 0, 0, 'Only through discipline can we find harmony. Only through harmony can we reach our true potential as warriors.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29664, 1, 0, 0, 0, 'You are not yet ready to face me. First, you must light the braziers.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29665, 1, 0, 0, 0, 'This is a grim site indeed.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29666, 1, 0, 0, 0, 'Balance is a delicate thing. It leans strongly on the wisdom of the elders. The lessons they preach are not always apparent to younger minds, so you must trust that their knowledge of the past will help us preserve the present.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29677, 1, 0, 0, 0, 'The decision to not return to the pools was a hard one. I don\'t think Shu ever truly understood. He does not know what it is to get older and weaker.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29679, 1, 0, 0, 0, 'This is your task, $c. See it throug', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29765, 0, 0, 0, 0, 'You got me goods, mon?', 0);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29768, 1, 0, 0, 0, 'Well, they\'re as warned as they\'re going to get. Do you have the mallet?', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29770, 1, 0, 0, 0, 'Ever since Wugou. the spirit of earth, fell asleep, the dirt is drying out and it\'s getting harder and harder to get new seeds to sprout. We need to hold on to any food we can, or the island will have hard times ahead.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29771, 0, 0, 0, 0, 'Have you done as I\'ve asked?', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29772, 1, 0, 0, 0, 'Come on, $n! The suspense is killing me!', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29774, 1, 0, 0, 0, 'If I know Shu, he\'ll be happy to do this whether it works or not. It\'s just his kind of fun.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29777, 1, 0, 0, 0, 'I do not wish to disparage the hozen for their aspirations - each race must walk its own path. In this moment, however, they are bringing harm to our bodies and our culture. That is a line that cannot be crossed.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29778, 1, 0, 0, 0, 'Elder Shaopai has long been responsible for documenting the great wisdoms of our people and hanging them for all to see. What amazes me is his fortitude in the light of the great sacrilege these hozen are committing. Many years of work destroyed, and yet he remains calm.$B$BI must admit, that is a quality that I do not possess.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29779, 1, 0, 0, 0, 'The time for kindness is done. We have monkeys to kill.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29783, 0, 0, 0, 0, 'The strength of the body cannot exist without the strength of the mind.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29788, 1, 0, 0, 0, 'It seems that everywhere I turn, the land is troubled. Your efforts have already helped to ease it\'s pain, but something greater awaits you before all of this suffering truly behind to subside.', 20886);
INSERT IGNORE `quest_request_items`(`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES (29793, 1, 0, 0, 0, 'In all of my years on Shen-zin Su, I\'ve never witnessed something so dark as these creatures. I shudder to think of the abyss that they crawled forth from.', 20886);
